INSERT INTO `korpus` (`id`, `namafile`, `pathfile`, `isi`) VALUES
(15, 'UU Nomor 21 Tahun 2013 - Keantariksaan.pdf', '/opt/lampp/temp/phpwlZmEm', 'lembarannegara\nr\nepublik\ni\nndonesia\nno.\n133,2013\nwilayah.\nkeantariksaan\n.tataruang.udara.\n(penjelasandalamtambahanlembarannegara\nrepublikindonesianomor5435)\nundang\n-\nundangrepublikindonesia\nnomor21tahun2013\ntentang\nkeantariksaan\ndenganrahmattuhanyangmahaesa\npresidenrepublikindonesia,\nmenimbang\n:\na.\nbahwaantariksamerupakanruangbesertaisinyayang\nterdapatdiluarruangudarayangmengelilingidan\nmelingkupiruangudarasertamerupakanciptaantuhan\nyangm\nahaesayangdipergunakanuntuksebesar\n-\nbesarnyakemakmuranrakyatsebagaimanadiamanatkan\ndalamundang\n-\nundangdasarnegararepublikindonesia\ntahun1945;\nb.\nbahwaposisigeografiswilayahindonesiayangterbentang\ndigariskhatulistiwadanterletakdia\nntaraduabenua\ndanduasamudramenjadikanindonesiasebagainegara\nyangmemilikiketergantungandalampemanfaatan\nteknologikeantariksaandansekaliguskeunggulan\nkomparatifyangberbasisilmudanteknologibagi\nkemajuanperadabansertakesejahteraanmanu\nsia\nindonesiapadakhususnyadanumatmanusiapada\numumnya;\nc.\nbahwaperaturanperundang\n-\nundangankeantariksaan\nsaatinibelummengatursecaraterpadudan\nkomprehensifsertabelummenjadilandasanhukumbagi\npenyelenggaraankeantariksaan;\n2013,\nno.\n133\n2\nd.\nbahwaberd\nasarkanpertimbangansebagaimana\ndimaksuddalamhurufa,hurufb,danhurufc,perlu\nmembentukundang\n-\nundangtentangkeantariksaan;\nmengingat\n:\npasal5ayat(1),pasal20,danpasal31ayat(5)\nundang\n-\nundangdasarnegararepublikindonesia\ntahun1945;\ndeng\nanpersetujuanbersama\ndewanperwakilanrakyatrepublikindonesia\ndan\npresidenrepublikindonesia\nmemutuskan:\nmenetapkan\n:\nundang\n-\nundangtentangkeantariksaan.\nbab\ni\nketentuanumum\npasal\n1\ndalamundang\n-\nundanginiyangdimaksuddengan:\n1.\nantariksaadalahruan\ngbesertaisinyayangterdapatdiluarruang\nudarayangmengelilingidanmelingkupiruangudara.\n2.\nkeantariksaanadalahsegalasesuatutentangantariksadanyang\nberkaitandenganeksplorasidanpendayagunaanantariksa.\n3.\nruangudaraadalahruangyangmengelili\nngidanmelingkupiseluruh\npermukaanbumiyangmengandungudarayangbersifatgas.\n4.\npenyelenggaraankeantariksaanadalahsetiapkegiataneksplorasidan\npemanfaatanantariksayangdilakukan,baikdidandaribumi,ruang\nudara,maupunantariksa.\n5.\npenyelenggara\nkeantariksaanadalahpihakatausubjekyang\nmelaksanakanpenyelenggaraankeantariksaan.\n6.\nasingadalahperseoranganwarganegaraasing,badanusahaasing,\ndan/ataupemerintahasing.\n7.\nbendaantariksaadalahsetiapbenda,baikbuatanmanusiamaupun\nbendaalami\nahyangterkaitdengankeantariksaan.\n8.\nwahanaantariksaadalahbendabuatanmanusiayangterkaitdengan\nkeantariksaandanbagian\n-\nbagiannya.\n9.\nroketadalahbagianwahanaantariksayangdigunakanuntuk\nmengantarkanmuatankeantariksadan/ataumengembalikan\nwah\nanaantariksa,termasukmuatannyakebumi.\n2013,\nno.\n133\n3\n10.\nbandarantariksaadalahkawasandidaratanyangdipergunakan\nsebagailandasandan/ataupeluncuranwahanaantariksayang\ndilengkapidenganfasilitaskeamanandankeselamatansertafasilitas\npenunjanglainnya.\n11.\nkesel\namatanadalahsuatukeadaanterpenuhinyapersyaratan\nkeselamatandalampemanfaatanwilayahindonesia,wahana\nantariksa,kawasanbandarantariksa,transportasiantariksa,\nnavigasikeantariksaan,masyarakat,sertafasilitaspenunjangdan\nfasilitasumumlainn\nya.\n12.\nkeamananadalahsegalaupayadankomitmensecarainternasional\nbagisetiappenyelenggarakeantariksaanuntukmemeliharadan/atau\nmenjaminpemanfaatanantariksadanbenda\n-\nbendalangitlainnya\nuntukmaksud\n-\nmaksuddamaidantidakmenimbulkankerusakan\nbag\nilingkunganbumidanantariksamelaluiketerpaduan\npemanfaatansumberdayamanusia,fasilitas,danprosedur.\n13.\nkerugianadalahsuatukeadaanyangmenimbulkankematian,luka\n-\nluka,ataubentuklaindariterganggunyakesehatanseseorang,hilang\nataurusaknyah\nartamiliknegara,milikpribadi,ataubadanhukum,\natauhartabendaorganisasiinternasionalantarpemerintah.\n14.\npemerintahpusat,yangselanjutnyadisebutpemerintah,adalah\npresidenrepublikindonesiayangmemegangkekuasaan\npemerintahannegara\nrepublikin\ndonesiasebagaimanadimaksud\ndalamundang\n-\nundangdasarnegararepublikindonesiatahun1945.\n15.\npemerintahdaerahadalahgubernur,bupati,atauwalikota,dan\nperangkatdaerahsebagaiunsurpenyelenggarapemerintahandaerah.\n16.\ninstansipemerintahadalahkementer\niandan/ataulembaga\npemerintahnonkementerian.\n17.\nmenteriadalahmenteriyangmenyelenggarakanurusanpemerintahan\ndibidangrisetdanteknologi.\n18.\nlembagaadalahinstansipemerintahyangmelaksanakanurusan\npemerintahandibidangpenelitiandanpengembangank\nedirgantaraan\ndanpemanfaatannyasertapenyelenggaraankeantariksaan.\npasal\n2\nundang\n-\nundanginibertujuan:\na.\nmewujudkankemandiriandanmeningkatkandayasaingbangsadan\nnegaradalampenyelenggaraankeantariksaan;\nb.\nmengoptimalkanpenyelenggaraankeantariksaa\nnuntukkesejahteraan\nrakyatdanproduktivitasbangsa;\nc.\nmenjaminkeberlanjutanpenyelenggaraankeantariksaanuntuk\nkepentingangenerasimasa\nkinidangenerasimasadepan;\n2013,\nno.\n133\n4\nd.\nmemberikanlandasandankepastianhukumdalampenyelenggaraan\nkeantariksaan;\ne.\nmewujudka\nnkeselamatandankeamananpenyelenggaraan\nkeantariksaan;\nf.\nmelindunginegaradanwarganegaranyadaridampaknegatifyang\nditimbulkandalampenyelenggaraankeantariksaan;\ng.\nmengoptimalkanpenerapanperjanjianinternasionalkeantariksaan\ndemikepentingannasio\nnal;dan\nh.\nmewujudkanpenyelenggaraankeantariksaanyangmenjadikomponen\npendukungpertahanandanintegritasnegarakesatuanrepublik\nindonesia.\npasal\n3\n(1)\nantariksamerupakanwilayahbersamayangdapatdimanfaatkanbagi\nkepentingansemuanegara.\n(2)\nantariksabeb\nasuntukdieksplorasidandigunakanolehsemuanegara\ntanpadiskriminasi,berdasarkanasaspersamaan,dansesuaidengan\nhukuminternasional.\npasal\n4\n(1)\nsetiapwahanaantariksayangdiluncurkanuntukdan/atauatas\nnamanegarakesatuanrepublikindonesiabe\nradadalamyurisdiksi\ndankontrolpemerintahrepublikindonesia.\n(2)\nsetiaporangyangberadadalamsaranadanprasaranakeantariksaan\nmiliknegarakesatuanrepublikindonesiatundukpadaperaturan\nperundang\n-\nundanganindonesia.\npasal\n5\nundang\n-\nundanginib\nerlakuterhadap:\na.\nsemuapenyelenggaraankeantariksaanyangdilaksanakandi\ndan/ataudariwilayahkedaulatandanwilayahyurisdiksinegara\nkesatuanrepublikindonesia;\nb.\nsemuapenyelenggaraankeantariksaanyangdilaksanakanuntuk\ndan/atauatasnamanegarakesa\ntuanrepublikindonesia;\nc.\nwarganegaraindonesiaataubadanhukumindonesiayangterlibat\ndan/atauberpartisipasidalampenyelenggaraankeantariksaan;dan\nd.\nasingyangtelahmendapatizinuntukmenyelenggarakankegiatan\nkeantariksaan.\n2013,\nno.\n133\n5\npasal\n6\nlingkuppengatur\nandalamundang\n-\nundanginimeliputi:\na.\nkegiatankeantariksaan;\nb.\npenyelenggaraankeantariksaan;\nc.\npembinaan;\nd.\nbandarantariksa;\ne.\nkeamanandankeselamatan;\nf.\npenanggulanganbendajatuhantariksasertapencariandan\npertolonganantariksawan;\ng.\npendaftaran;\nh.\nkerjasamain\nternasional;\ni.\ntanggungjawabdangantirugi;\nj.\nasuransi,penjaminan,danfasilitas;\nk.\npelestarianlingkungan;\nl.\npendanaan;\nm.\nperansertamasyarakat;dan\nn.\nsanksi.\nbab\nii\nkegiatankeantariksaan\nbagian\nkesatu\numum\npasal\n7\n(1)\nkegiatankeantariksaanmeliputi:\na.\nsainsanta\nriksa;\nb.\npenginderaanjauh;\nc.\npenguasaanteknologikeantariksaan;\nd.\npeluncuran;dan\ne.\nkegiatankomersialkeantariksaan.\n(2)\nkegiatankeantariksaansebagaimanadimaksudpadaayat(1)\ndilaksanakandenganmemperhatikan:\na.\nkepentingannasional;\nb.\nkeamanandankeselamatan;\n2013,\nno.\n133\n6\nc.\nperkembanganilmupengetahuandanteknologi;\nd.\nsumberdayamanusiakeantariksaanyangprofesional;\ne.\nmanfaat,efektivitas,danefisiensi;\nf.\nkeandalansaranadanprasaranakeantariksaan;\ng.\npelindungandanpengelolaanlingkunganhidupdanlingkungan\nantariksa;dan\nh.\nketentuanperaturanperundang\n-\nundangannasionaldan\nperjanjianinternasionalyangindonesiamenjadinegarapihak.\npasal\n8\nsetiapkegiatankeantariksaandilarang:\na.\nmenempatkan,mengorbitkan,ataumengoperasikansenjatanuklir\ndansenjataperusakmassallainn\nyadiantariksa;\nb.\nmelakukanujisenjatanuklirdansenjataperusakmassallainnyadi\nantariksa;\nc.\nmenggunakanbulandanbendaantariksaalamlainnyauntuktujuan\nmiliteratautujuanlainyangmencelakakanumatmanusia;\nd.\nmelakukankegiatanyangdapatmengancam\nkeamanandan\nkeselamatanpenyelenggaraankeantariksaantermasukkeamanan\nbendaantariksa,perseorangan,dankepentinganumum;atau\ne.\nmelakukankegiatanyangdapatmengakibatkanpencemaran\ndan/ataukerusakanlingkunganhidupbumidanantariksaserta\nmembahaya\nkankegiatankeantariksaantermasukpenghancuran\nbendaantariksa.\npasal\n9\nuntukpemutakhiranstatusdanperkembangankegiatankeantariksaan\ndanpemberianrekomendasibagikebijakanpengembangannya,lembaga\nwajibmelaksanakanpengkajiankebijakankeantariks\naansebagaimana\ndimaksuddalampasal7secaraperiodiksetiaptahun.\npasal\n10\n(1)\ndalamkeadaandamai,kegiatankeantariksaandimaksudkanuntuk\npencapaiantujuannasionaldankepentingannasional.\n(2)\ndalamhalnegaradalamkeadaanbahayadanuntuktujuan\npertahan\nandankeamanannegara,menteriyangmenyelenggarakan\nurusanpemerintahandibidangpertahanandapatmemanfaatkan\nseluruhsaranadanprasaranapenyelenggaraankeantariksaan\nindonesia.\n2013,\nno.\n133\n7\nbagian\nkedua\nsainsantariksa\npasal\n11\n(1)\nsainsantariksasebagaimanadimaks\nuddalam\npasal7ayat(1)hurufa\nwajibdilaksanakanolehlembaga.\n(2)\nkegiatansainsantariksasebagaimanadimaksudpadaayat(1)\nmeliputi,tetapitidakterbataspada,penelitianmengenai:\na.\ncuacaantariksa;\nb.\nlingkunganantariksa;dan\nc.\nastrofisika.\n(3)\npenelitianant\nariksasebagaimanadimaksudpadaayat(2)dapat\ndilakukandenganmenggunakansarana:\na.\nsatelit;\nb.\nstasiunantariksa;dan\nc.\nfasilitasobservasidiruasbumi.\n(4)\nselainmenggunakansaranasebagaimanadimaksudpadaayat(3),\npenelitianantariksadapatpuladilakukanm\nelalui:\na.\npartisipasiaktifdalampenelitiankeantariksaaninternasional;\ndan/atau\nb.\nkerjasamadenganinstansipemerintahdanbadanhukumlaindi\nluarnegeri.\npasal\n12\ndalamhalhasilpenelitianantariksasebagaimanadimaksuddalampasal\n11bersifatsensitif\ndan/atauberpotensimemberikandampakluas,\npenyelenggarakeantariksaanwajibmelaporkanhasilpenelitiannyakepada\nlembaga.\npasal\n13\n(1)\nlembagawajibmemberikaninformasikhusustentang:\na.\ncuacaantariksa;\nb.\nmitigasi,antisipasi,danpenangananbencanaakibatcu\naca\nantariksa;dan\nc.\nperingatandini.\n(2)\nselainwajibmemberikaninformasikhusussebagaimanadimaksud\npadaayat(1),lembagajugawajibmemberikanbantuanteknis.\n2013,\nno.\n133\n8\npasal\n14\ninformasikhusustentang:\na.\ncuacaantariksasebagaimanadimaksuddalam\npasal13ayat(1)h\nuruf\nadisampaikankepadainstansipemerintahyangmenangani\nkomunikasiradio,operasisatelit,dannavigasiberbasissatelit;dan\nb.\nmitigasi,antisipasi,danpenangananbencanaakibatcuacaantariksa\nsebagaimanadimaksuddalam\npasal13ayat(1)hurufbdan\nperingatandinisebagaimanadimaksuddalampasal13ayat(1)huruf\ncdisampaikankepadainstansiyangberwenangdalam\npenanggulanganbencana.\nbagian\nketiga\npenginderaanjauh\nparagraf\n1\numum\npasal\n15\n(1)\npenginderaanjauhsebagaimanadimaksuddalampasal7\nayat(1)\nhurufbmeliputikegiatan:\na.\nperolehandata;\nb.\npengolahandata;\nc.\npenyimpanandanpendistribusiandata;dan\nd.\npemanfaatandatadandiseminasiinformasi.\n(2)\nhasilkegiatanpenginderaanjauhsebagaimanadimaksudpadaayat\n(1)dapatberupa:\na\n.\ndataprimer;\nb.\ndataproses;dan\nc.\nanalisisinformasi.\nparagraf\n2\nperolehandata\npasal\n16\n(1)\nperolehandatapenginderaanjauhsebagaimanadimaksuddalam\npasal15ayat(1)hurufadapatdilakukanmelalui:\na.\npengoperasiansatelit;\nb.\npengoperasianstasiunb\numi;dan/atau\nc.\ncitrasatelit.\n2013,\nno.\n133\n9\n(2)\nlembagadalammemperolehdatapenginderaanjauhmelalui\npengoperasiansatelitdanpengoperasianstasiunbumisebagaimana\ndimaksudpadaayat(1)hurufadanhurufbwajibmembuat\nperencanaan,membangun,sertamengoperasikan\nsatelitdanstasiun\nbumi.\n(3)\ncitrasatelitsebagaimanadimaksudpadaayat(1)hurufcdapat\ndiperolehdaripenyediadata,baiksecarakomersialmaupun\nnonkomersial.\n(4)\nselainkewajibansebagaimanadimaksudpada\nayat(2),lembaga\ndapatmelakukankerjasam\naoperasionaldenganoperatorasing\nsesuaidenganketentuanperaturanperundang\n-\nundangan.\npasal\n17\nstasiunbumisebagaimanadimaksuddalampasal16ayat(1)hurufb\nhanyadapatdibangundandioperasikanolehlembaga.\npasal\n18\n1.\ncitrasatelitpenginderaanjau\nhsebagaimanadimaksuddalampasal\n16ayat(1)hurufcterdiriatas:\na.\nresolusirendah;\nb.\nresolusimenengah;dan\nc.\nresolusitinggi.\n2.\ndalammemperolehdatapenginderaanjauh:\na.\nresolusirendahdanmenengahdikenaitarifnonkomersial;dan\nb.\nresolusitinggidikenaitari\nfkomersial.\n3.\npengadaandatapenginderaanjauhresolusitinggiuntukinstansi\npemerintahdanpemerintahdaerahhanyadapatdilaksanakanoleh\nlembaga.\nparagraf\n3\npengolahandata\npasal\n19\n(1)\npengolahandatapenginderaanjauhsebagaimanadimaksuddalam\npasal\n15ayat(1)hurufbdapatmeliputi:\na.\nkoreksigeometrik;\nb.\nkoreksiradiometrik;\nc.\nklasifikasi;dan\nd.\ndeteksiparametergeo\n-\nbio\n-\nfisik.\n2013,\nno.\n133\n10\n(2)\npengolahandatapenginderaanjauhwajibdilakukandenganmengacu\npadametodedankualitaspengolahandatapeng\ninderaanjauhyang\nditetapkanolehlembaga.\nparagraf\n4\npenyimpanandanpendistribusiandata\npasal\n20\n(1)\nlembagawajibmenyelenggarakanpenyimpanandanpendistribusian\ndatamelaluibankdatapenginderaanjauhnasionalsebagaisimpul\njaringandatapenginderaan\njauhdalamsistemjaringandataspasial\nnasional.\n(2)\nlembagadalammenyelenggarakanpenyimpanandanpendistribusian\nsebagaimanadimaksudpadaayat(1)wajib:\na.\nmengumpulkan,menyimpan,danmendistribusikanmetadatadan\ndatapenginderaanjauhwilayahindonesia;\nb.\nmenyediakandatapenginderaanjauhdengantutupanawan\nminimaldanbebasawansetiaptahununtukseluruhwilayah\nindonesia;\nc.\nmenyediakaninformasimengenaikualitasdatapenginderaan\njauh;\nd.\nmemberikansupervisiterkaitpemanfaatandatapenginderaan\njauh;\ne.\nmem\nberikanmasukankepadapemerintahmengenaikebijakan\npengadaan,pemanfaatan,penguasaanteknologi,\ndandata\npenginderaanjauhsatelit;\nf.\nmenjadisimpuldatapenginderaanjauhsatelitdalamsistem\njaringandataspasialnasional;dan\ng.\nmenyediakanfasilitaspeng\nolahandatapenginderaanjauhbagi\nparapenggunadiluarlembaga.\n(3)\nuntukpelaksanaanketentuansebagaimanadimaksudpadaayat(1),\nlembagabertugas:\na.\nmelakukanpembinaandanmenetapkanstandardisasidatadan\nprodukinformasisertametodepengolahanpengi\nnderaanjauh\nnasional;\nb.\nmelakukankoordinasikebutuhanpengadaandatapenginderaan\njauhdenganinstansiterkait;dan\nc.\nmelaksanakankerjasamadalampelestariandatapenginderaan\njauhyangdimilikiolehpenyelenggarakeantariksaanselain\nlembaga.\n2013,\nno.\n133\n11\npasal\n21\n(1)\nins\ntansipemerintahpenyelenggarakeantariksaanwajibmenyerahkan\nmetadatadanduplikatdatapenginderaanjauhkepadalembaga,\nkecualiditentukanlainberdasarkanperjanjianlisensi.\n(2)\npenyelenggarakeantariksaan,selainlembagadaninstansi\npemerintah,wajibm\nenyerahkanmetadatapenginderaanjauhkepada\nlembaga,kecualiditentukanlainberdasarkanperjanjianlisensi.\nparagraf\n5\npemanfaatandatadandiseminasiinformasi\npasal\n22\n(1)\npemanfaatandatadandiseminasiinformasipenginderaanjauh\nsebagaimanadimaksuddal\nampasal15ayat(1)hurufdwajib\ndilakukanberdasarkanpedomanyangditetapkanolehlembaga.\n(2)\nlembagadapatmelaksanakanpengolahanklasifikasidandeteksi\nparametergeo\n-\nbio\n-\nfisikataspermintaanpenggunasesuaidengan\nketentuanperaturanperundang\n-\nundang\nan.\npasal\n23\nketentuanlebihlanjutmengenaitatacarapenyelenggaraankegiatan\npenginderaanjauhdiaturdalamperaturanpemerintah.\nbagian\nkeempat\npenguasaanteknologikeantariksaan\nparagraf\n1\numum\npasal\n24\n(1)\npenguasaanteknologikeantariksaansebagaimanad\nimaksuddalam\npasal7ayat(1)hurufcwajibdilaksanakanolehlembaga.\n(2)\npenguasaanteknologikeantariksaanmeliputi,tetapitidakterbatas\npada:\na.\npenguasaandanpengembanganteknologiroket;\nb.\npenguasaandanpengembanganteknologisatelit;\nc.\npenguasaandanpeng\nembanganteknologiaeronautika;dan\nd.\npenjalaranteknologi.\n2013,\nno.\n133\n12\npasal\n25\nlembagawajibmengupayakanterjadinyaalihteknologikeantariksaan\nsesuaidenganketentuanperaturanperundang\n-\nundangan.\npasal\n26\n(1)\ndalamhallembagamelaksanakanpembuatan,manufaktur,dan\np\nembangunansaranadanprasaranakegiatanpenguasaandan\npengembanganteknologikeantariksaan,lembagadapat\nmengikutsertakanperusahaannasionaluntukmelaksanakan\nkegiatanpenguasaandanpengembanganteknologikeantariksaan.\n(2)\ndalammelaksanakanpenguasaan\ndanpengembanganteknologi\nkeantariksaansebagaimanadimaksudpadaayat(1),perusahaan\nnasionaldapatmengikutsertakanpihakasingsebagaisubkontraktor.\n(3)\nprosedurdanmekanismepelaksanaanketentuansebagaimana\ndimaksudpadaayat(1)danayat(2)diaturs\nesuaidenganketentuan\nperaturanperundang\n-\nundangan.\npasal\n27\n(1)\npemerintahmenjaminkeamananteknologi\n-\nsensitifkeantariksaan\nyangdiimporkewilayahkedaulatandanwilayahyurisdiksinegara\nkesatuanrepublikindonesia.\n(2)\npenjaminankeamanansebagaimanadimaks\nudpadaayat(1)\nditujukanuntuk:\na.\nperdamaian;\nb.\nkepentingannasional;dan\nc.\npemenuhankewajibaninternasional.\n(3)\ntatacaradanmekanismepenjaminankeamananteknologi\n-\nsensitif\nkeantariksaansebagaimanadimaksudpadaayat(1)danayat(2)\ndiaturlebihlanjut\ndalamperaturanpemerintah.\nparagraf\n2\npenguasaandanpengembanganteknologiroket\npasal\n28\n(1)\nlembagadalammelakukanpenguasaandanpengembanganteknologi\nroketsebagaimanadimaksuddalampasal24ayat(2)hurufawajib:\na.\nmenyusunprogrampengembangan\nroket;\nb.\nmembuatperancangandanprototiperoket;dan\nc.\nmelaksanakanpengujianroket.\n2013,\nno.\n133\n13\n(2)\nuntukmelaksanakanpenguasaandanpengembanganteknologiroket\nsebagaimanadimaksudpadaayat(1),lembagawajib\nmengembangkansaranadanprasaranasertasumberdayay\nang\nterkaitdenganteknologiroket.\n(3)\nuntukmembuatperancangandanprototiperoketsebagaimana\ndimaksudpadaayat(1)hurufbdanmelaksanakanpengujianroket\nsebagaimanadimaksudpadaayat(1)hurufc,lembagawajibmenjaga\nkeselamatandankeamananpelaksa\nnaankegiatandanmasyarakat\numumdaririsikokecelakaan.\n(4)\nlembagamengalokasikananggaranuntukpenangananrisiko\nkecelakaanakibatkegiatansebagaimanadimaksudpadaayat(1)\nhurufbdanhurufc.\n(5)\nlembagadapatbekerjasamadenganpenyelenggarakeantariksa\nan\nlainnya,baikdaridalamnegerimaupunasing,dalampenguasaandan\npengembanganteknologiroketsebagaimanadimaksudpadaayat(1).\npasal\n29\n(1)\nuntukpenguasaandanpengembanganteknologiroketsebagaimana\ndimaksuddalampasal28,lembagawajibmengupayaka\nnterjadinya\nalihteknologi.\n(2)\npemerintahwajibmengupayakanalihteknologimelaluikerjasama\ninternasional.\nparagraf\n3\npenguasaandanpengembanganteknologisatelit\npasal\n30\n(1)\nlembagadalammelakukanpenguasaandanpengembanganteknologi\nsatelitsebagaimana\ndimaksuddalampasal24ayat(2)hurufbwajib:\na.\nmenyusunprogrampengembangansatelitnasional;\nb.\nmembuatperancangandanprototipesatelit;\nc.\nmelaksanakanpengujiansatelit;\nd.\nmembangundanmengoperasikanstasiunbumiuntuktelemetri,\npenjejakan,dankomandoja\nrakjauh;dan\ne.\nmelaksanakanpeluncuransatelitdengankemampuansendiri\ndan/ataumelaluikerjasama.\n(2)\nsatelitsebagaimanadimaksudpadaayat(1)dapatdibedakan\nberdasarkanmisi:\na.\ntelekomunikasi;\n2013,\nno.\n133\n14\nb.\npengamatanbumi;\nc.\npengamatanatmosferdanantariksa;\nd.\nnavigasi;d\nan\ne.\ntujuanlainyangmemilikinilaimanfaatbagikemaslahatandan\nkesejahteraannasional.\n(3)\ndalammelakukanpenguasaandanpengembanganteknologisatelit\nsebagaimanadimaksudpada\nayat(1),lembagadapat\nmengembangkansarana,prasarana,dansumberdayalainny\na.\n(4)\npenguasaandanpengembanganteknologisatelitsebagaimana\ndimaksudpadaayat(1)dapatdilakukanolehpenyelenggara\nkeantariksaanselainlembaga.\n(5)\nlembagamembinapenguasaandanpengembanganteknologisatelit\nyangdilaksanakanolehpenyelenggarakeantari\nksaanselainlembaga.\nparagraf\n4\npenguasaandanpengembanganteknologiaeronautika\npasal\n31\n(1)\nlembagadalammelakukanpenguasaandanpengembanganteknologi\naeronautikasebagaimanadimaksuddalampasal24ayat(2)hurufc\nwajibmenyusundanmelaksanakanprogr\nampenguasaandan\npengembanganteknologiaeronautika.\n(2)\ndalammelakukanpenguasaandanpengembanganteknologi\naeronautikasebagaimanadimaksudpadaayat(1),lembagadapat\nmengembangkansarana,prasarana,dansumberdayayangterkait\ndenganteknologiaeronau\ntika.\n(3)\ndalammelaksanakanpenguasaandanpengembanganteknologi\naeronautika,lembagadapatbekerjasamadenganinstansiterkait.\nparagraf\n5\npenjalaranteknologi\npasal\n32\n(1)\nlembagadalammelaksanakanpenjalaranteknologikeantariksaan\nsebagaimanadimaksuddala\nm\npasal24ayat(2)hurufdbertugas:\na.\nmembinaintegrasidandistribusitanggungjawabkemampuan\nnasionaldalamkegiatankeantariksaan,baikswasta,akademisi,\nlembagapenelitiandanpengembangan,maupunlembaga\nkeuangan;dan\n2013,\nno.\n133\n15\nb.\nmendorongdanmemberirekomenda\nsikepadaindustriyang\nmendukungprogramkegiatankeantariksaan.\n(2)\ndalammelaksanakanpenjalaranteknologikeantariksaan\nsebagaimanadimaksudpadaayat(1),pemerintahdapatbertindak\nsebagaipembeliterikatdariindustrikegiatankeantariksaannasional\nber\ndasarkanrekomendasilembaga.\npasal\n33\nsetiaporangyangmemanfaatkanpenggunaandatadaninformasiserta\njasateknologikeantariksaandapatdikenaibiayatertentusesuaidengan\nketentuanperaturanperundang\n-\nundangan.\nbagian\nkelima\npeluncuran\npasal\n34\n(1)\npelu\nncuranwahanaantariksasebagaimanadimaksuddalampasal7\nayat(1)hurufddilakukanolehlembagadi:\na.\nwilayahkedaulatannegarakesatuanrepublikindonesia;\nb.\nwilayahyurisdiksinegarakesatuanrepublikindonesia;\nc.\nkapalataupesawatudarayangberbenderain\ndonesia;dan/atau\nd.\nkapalataupesawatudaraasingyangberadadiwilayah\nkedaulatanatauwilayahyurisdiksinegarakesatuanrepublik\nindonesia.\n(2)\nselainpeluncuranwahanaantariksasebagaimanadimaksudpada\nayat(1)hurufa,hurufb,danhurufc,peluncuranj\nugadapat\ndilakukandiluarwilayahindonesiadenganketentuanwahana\nantariksayangdiluncurkanadalahmilikindonesia.\npasal\n35\n(1)\ndalammelaksanakankegiatanpeluncuranwahanaantariksa,\npenyelenggarakeantariksaanwajib:\na.\nmemenuhipersyaratankeuangandan\njaminanasuransidari\nwahanaantariksa;\nb.\nmempertimbangkanpotensidan/ataukemungkinanterjadinya\nkecelakaandan/ataugangguankesehatanmasyarakatataupun\nkerugianmaterialterhadapakibatyangditimbulkansangatkecil;\nc.\nmenjaminbendaantariksatidakmemba\nwasenjatanuklir,\nsenjatapemusnahmassal,atausenjataberbahayalainnya;\n2013,\nno.\n133\n16\nd.\nmenjaminbahwapeluncurantidakakanmenimbulkan\nkemungkinangangguanterhadapkeamanannasionalsertatidak\nakanmenimbulkanpelanggaranterhadapkebijakanluarnegeri\ndankewajib\naninternasional;dan\ne.\nmemperhatikandanmemenuhiketentuantentangkeselamatan\npenerbangan.\n(2)\ndalamhalpeluncurandilakukandiluarnegeri,izinpeluncuranwajib\nmemperhatikanperjanjianyangmenjaminbahwapemerintah\nindonesiadapatdibebaskandaritanggun\ngjawabterhadapkerugian\nyangterjadi.\npasal\n36\nketentuanlebihlanjutmengenaitatacarapeluncuranwahanaantariksa\nsebagaimanadimaksuddalam\npasal34danpasal35diaturdengan\nperaturanlembaga.\nbagian\nkeenam\nkegiatankomersialkeantariksaan\npasal\n37\n(1)\nkegiatankomersialkeantariksaansebagaimanadimaksuddalam\npasal7ayat(1)hurufedapatdilakukanolehbadanhukumyang\ndidirikanberdasarkanhukumindonesiadanasing.\n(2)\nketentuanmengenaipersyaratandantatacarakegiatankomersial\nkeantariksaansebag\naimanadimaksudpadaayat(1)diaturdalam\nperaturanpemerintah.\nbab\niii\npenyelenggaraankeantariksaan\nbagian\nkesatu\npenyelenggara\npasal\n38\n(1)\npemerintahwajibmelaksanakanpenyelenggaraankeantariksaan.\n(2)\npenyelenggaraankeantariksaansebagaimanadim\naksudpadaayat(1)\ndilaksanakanolehlembaga.\n(3)\nlembagasebagaimanadimaksudpadaayat(2)beradadibawahserta\nbertanggungjawabkepadapresidenmelaluimenteriyang\nmengoordinasikannya.\n2013,\nno.\n133\n17\n(4)\nketentuanmengenaitugas,fungsi,kewenangan,dansusunan\norganisasilembagadiaturdenganperaturanpresiden.\npasal\n39\n(1)\nselaindilaksanakanolehlembagasebagaimanadimaksuddalam\npasal38ayat(2),penyelenggaraankeantariksaandapatdilaksanakan\nolehinstansipemerintahlainnya,pemerintahdaerah,badanhu\nkum,\ndan/ataumasyarakatsesuaidenganketentuanperaturanperundang\n-\nundangan.\n(2)\npenyelenggaraankeantariksaansebagaimanadimaksudpadaayat(1)\ndikoordinasikanolehlembaga.\nbagian\nkedua\nrencanainduk\npasal\n40\n(1)\nrencanaindukwajibdisusunolehlem\nbagasebagaipedoman\nnasionaluntukpenyelenggaraankeantariksaan.\n(2)\nrencanaindukdisusundenganmempertimbangkanmodaldasardan\nlingkunganstrategis.\n(3)\nrencanaindukmemuat:\na.\nvisidanmisi;\nb.\nkebijakan;\nc.\nstrategi;dan\nd.\npetarencanastrateg\nisjangkapendek,menengah,danpanjang.\n(4)\nrencanaindukdisusunolehlembagauntukjangkawaktu25(dua\npuluhlima)tahun.\n(5)\nrencanainduksebagaimanadimaksudpadaayat(4)ditetapkanoleh\npresidenmelaluiusulanmenteriyangmengoordinasikanlembag\na.\n(6)\nrencanainduksebagaimanadimaksudpadaayat(5)dapatditinjau\nkembali1(satu)kalidalam\n5(lima)tahunatausesuaidengan\nkebutuhan.\nbab\niv\npembinaan\npasal\n41\n(1)\npemerintahwajibmelakukanpembinaanterhadappenyelenggaraan\nkeantariksaan.\n2013,\nno.\n133\n18\n(2)\npembinaa\nnpenyelenggaraankeantariksaansebagaimanadimaksud\npadaayat(1)meliputiaspekpengaturandanpengendalian.\npasal\n42\n(1)\npengaturansebagaimanadimaksuddalampasal41\nayat(2)meliputi\npenetapankebijakanumumdanteknisyangterdiriataspenentuan\nnorma,\nstandar,pedoman,dankriteriapenyelenggaraan\nkeantariksaan.\n(2)\npengendaliansebagaimanadimaksuddalampasal41ayat(2)meliputi\npemberianarahan,pembimbingan,pelatihan,perizinan,sertifikasi,\nsertapemberianbantuanteknisdibidangpembangunandan\npen\ngoperasian.\npasal\n43\npembinaanpenyelenggaraankeantariksaansebagaimanadimaksuddalam\npasal41ayat(2)diarahkanuntuk:\na.\nmewujudkankemampuansumberdayamanusiayangprofesionaldan\nberintegritas;\nb.\nmendorongpenguasaanilmupengetahuandanteknologi\nkean\ntariksaan;\nc.\nmendorongterwujudnyaindustrirekayasadanjasakeantariksaan\nuntukmenghasilkanprodukyangdapatmemenuhikebutuhan\nnasionaldaneksporyangdapatbersaingdenganproduknegaralain;\nd.\nmemanfaatkansumberdayaalamkeantariksaansecaraefisien\ndan\ndigunakansebesar\n-\nbesarnyasecaraberkelanjutandengantetap\nmenjagakelestarianfungsilingkunganhidup;\ne.\nmendorongterwujudnyapengakuaninternasionalataskepentingan\nnasionaldalampenyelenggaraankeantariksaansecaramenyeluruh;\ndan\nf.\nmewujudkanprod\nuktivitasyangtinggidalampenyelenggaraan\nkeantariksaanyangdidukungolehmasyarakat,organisasi,dan\nmekanismekoordinasidalamketerpaduan,baikdalamlingkup\npenyelenggaraankeantariksaanitusendirimaupundenganbidang\n-\nbidangpembangunanlainnya,s\nertadidukungsisteminformasi\nkeantariksaandankerjasamadenganbangsadannegaralain.\nbab\nv\nbandarantariksa\npasal\n44\n(1)\nlembagamembangundanmengoperasikanbandarantariksadalam\nwilayahkedaulatannegarakesatuanrepublikindonesia.\n2013,\nno.\n133\n19\n(2)\nlokasibandaranta\nriksasebagaimanadimaksudpadaayat(1)\nditetapkanolehlembaga.\n(3)\nlokasibandarantariksasebagaimanadimaksudpadaayat(2)\nditetapkansebagaikawasanstrategisnasionalsesuaidengan\nketentuanperaturanperundang\n-\nundangan.\n(4)\nbandarantariksaterdiriatasz\nona:\na.\nbahayasatu;\nb.\nbahayadua;dan\nc.\nbahayatiga.\n(5)\nbandarantariksasebagaimanadimaksudpada\nayat(4)merupakan\nkawasanterlarang.\n(6)\nlembagadalammembangunbandarantariksasebagaimana\ndimaksudpadaayat(1)dapatbekerjasamadenganbadanhukum\nindonesia.\npas\nal\n45\n(1)\npemerintahataupemerintahdaerahmemberikemudahandan\nmemfasilitasikeperluandalampembangunanbandarantariksa\nsesuaidenganketentuanperaturanperundang\n-\nundangan.\n(2)\ndalammenentukanlokasi,pembuatanrancangbangun,perencanaan,\ndanpembangunanb\nandarantariksa,termasukkawasandi\nsekelilingnya,wajibmemperhatikankepentingannasional,keamanan\ndankeselamatanpeluncuranwahanaantariksa,sertakelestarian\nlingkungankawasanbandarantariksa.\npasal\n46\npembangunanbandarantariksasebagaimanadim\naksuddalampasal44\nayat(1)harusdilengkapidenganfasilitaspokokdanfasilitaspenunjang.\npasal\n47\npengaturandanpengawasanpengoperasianbandarantariksa\ndilaksanakanolehpemerintah.\npasal\n48\n(1)\npenyelenggarakeantariksaandalammembangunbandarantar\niksa\nwajibmemilikianalisismengenaidampaklingkungan.\n(2)\nanalisismengenaidampaklingkungansebagaimanadimaksudpada\nayat(1)dilaksanakansesuaidenganketentuanperaturanperundang\n-\nundangan.\n2013,\nno.\n133\n20\npasal\n49\nsetiaporangdilarangmendirikanbangunanataumelak\nukankegiatanlain\ndibandarantariksasebagaimanadimaksuddalampasal44ayat(5)yang\nmengakibatkankegagalanataumembahayakankeamanandan\nkeselamatanoperasionalpeluncuranwahanaantariksa.\npasal\n50\nketentuanlebihlanjutmengenaitatacarapembangu\nnandan\npengoperasianbandarantariksasebagaimanadimaksuddalampasal46\ndanpasal47diaturdalamperaturanpemerintah.\nbab\nvi\nkeamanandankeselamatan\nbagian\nkesatu\nkeamanan\npasal\n51\n(1)\nsetiappenyelenggarakeantariksaanbertanggungjawabterhadap\nkeamana\nnpenyelenggaraankeantariksaan.\n(2)\nuntukmenjaminkeamananpenyelenggaraankeantariksaan,setiap\npenyelenggarakeantariksaanwajibmemenuhistandardanprosedur\nkeamanan.\n(3)\nlembagawajibmengawasikepatuhanpemenuhanstandardan\nprosedurkeamananyangdilaksan\nakanolehsetiappenyelenggara\nkeantariksaan.\nbagian\nkedua\nkeselamatan\npasal\n52\n(1)\nsetiappenyelenggaraankeantariksaanwajibdilaksanakandengan\nmematuhistandarkeselamatan.\n(2)\nlembaga,menteri,dan/ataumenteriyangmenyelenggarakanurusan\npemerintahandibid\nangpertahanannegarawajibmenyediakan\ninformasikeselamatanpenyelenggaraankeantariksaan.\n(3)\nlembaga,untukkepentingankeselamatankeantariksaan,wajib\nmenginformasikanancamankeselamatankepadapenyelenggara\nkeantariksaan.\npasal\n53\n(1)\nlembagawajibmenunju\nkdanmenetapkanpetugaskeselamatan\npeluncuranuntuksetiapfasilitaspeluncuranyangtelahmemilikiizin.\n2013,\nno.\n133\n21\n(2)\nsetiappetugaskeselamatanpeluncuransebagaimanadimaksudpada\nayat(1)dapatditugaskanpadabeberapafasilitaspeluncuran.\npasal\n54\npetugaskesel\namatanpeluncuransebagaimanadimaksuddalampasal53\nayat(1)bertugasmemastikan:\na.\npeluncurantelahdilaksanakansesuaidenganstandaroperasional\nprosedur;\nb.\nprosespeluncuranhinggabendaantariksatelahmencapaiatau\nmelewatiorbittidakmembahayakanora\nngataubenda;dan\nc.\nkepatuhanizinkegiatanantariksaatauizinpeluncuran.\npasal\n55\n(1)\nberdasarkantugassebagaimanadimaksuddalampasal54,petugas\nkeselamatanpeluncuranberwenangmelakukantindakanyang\ndianggapperlusesuaidengantugasnya.\n(2)\npetu\ngaskeselamatanpeluncuranpadafasilitaspeluncuran\nsebagaimanadimaksudpadaayat(1)berwenang:\na.\nmemasukidanmemeriksafasilitasdansegalabendaantariksa\nsertamengujiperalatanlainnyayangberadapadafasilitasdengan\npersetujuandaripemegangiz\ninkegiatankeantariksaanatauyang\nditunjuk;\nb.\nmendapatinformasiataubantuanyangdianggapperludari\npemegangizin,karyawan,sertaagenataukontraktor;dan\nc.\nmemberikanpetunjukmengenaipeluncuranwahanaantariksa,\nataupeluncuranyangdirencanaka\nn,padafasilitasyang\ndipandangperlu,termasukmemberikanpetunjukuntuk\npenghentianpeluncuranataupemusnahanbendaantariksa,baik\nsebelummaupunsetelahdiluncurkan.\n(3)\npetugaskeselamatanpeluncuranpadafasilitaspeluncurandalam\nmelaksanakantuga\nsnyawajibmenunjukkanidentitaskepada\npemegangizinkegiatankeantariksaan.\n(4)\npetugaskeselamatanpeluncurandilarangmemilikihubunganbisnis\ndanhubunganlainyangbersifatmengikatdenganpemegangizin\nkegiatankeantariksaanatauizinpeluncuran.\npa\nsal\n56\nsetiappemegangizinkegiatankeantariksaan,karyawan,sertaagenatau\nkontraktorwajibmematuhipetunjukyangdiberikanolehpetugas\nkeselamatanpeluncuranpadafasilitaspeluncuran.\n2013,\nno.\n133\n22\npasal\n57\nketentuanmengenaistandardanprosedurkeamanandankes\nelamatan\npenyelenggaraankeantariksaandiaturdalamperaturanpemerintah.\nbab\nvii\npenanggulanganbendajatuhantariksaserta\npencariandanpertolonganantariksawan\nbagian\nkesatu\npenanggulanganbendajatuhantariksa\npasal\n58\n(1)\nbendajatuhantariksadapat\nterdiriatas:\na.\nbendabuatanmanusia;dan\nb.\nbendaalamiah.\n(2)\nbendajatuhantariksasebagaimanadimaksudpadaayat(1)dapat\njatuhkebumidenganterdeteksiataupuntidakterdeteksi.\n(3)\nsetiaporangdilarangmenghilangkanataumengubahletakdan\nmenga\nmbilbagianbendajatuhantariksadiwilayahkedaulatandan\nwilayahyurisdiksinegarakesatuanrepublikindonesia.\n(4)\nlembagawajibmengidentifikasibendajatuhantariksadiwilayah\nkedaulatandanwilayahyurisdiksinegarakesatuanrepublik\nindonesiadan\nberkoordinasidenganinstansipemerintahlainnya.\n(5)\ndalamhalbendajatuhantariksamilikasing,lembagadapat\nmemprosessesuaidenganperjanjianinternasionalyangberlaku.\npasal\n59\nuntuktujuankeamanandankeselamatan,kepentinganpenelitian,dan\npeng\nembanganilmupengetahuan,setiapbendajatuhantariksadiwilayah\nkedaulatandanwilayahyurisdiksinegarakesatuanrepublikindonesia\nwajibdiserahkankepadalembaga.\npasal\n60\n(1)\npemerintahwajibmelakukaninvestigasimengenaipenyebabsetiap\nkecelakaa\nndan/ataubencanayangseriusdalamkegiatan\nkeantariksaandiwilayahkedaulatannegarakesatuanrepublik\nindonesia.\n(2)\npenginvestigasiansebagaimanadimaksudpada\nayat(1)dilakukan\nolehtimteknisahliyangdibentukdanbertanggungjawabkepada\nmenter\ni.\n2013,\nno.\n133\n23\n(3)\ntimteknisahlisebagaimanadimaksudpadaayat(2)bersifatadhoc.\n(4)\nkeanggotaantimteknisahlipalingsedikitmelibatkankeahliandi\nbidang:\na.\npenguasaanteknologikeantariksaan;\nb.\npenguasaanteknologipenerbangan;\nc.\nhubunganluarnegeri;\nd.\nketenaganukliran;dan\ne.\nhukumkedirgantaraan.\n(5)\ntimteknisahlisebagaimanadimaksudpadaayat(2)bertugas\nmelakukankegiataninvestigasi,menyusunlaporanakhir,dan\nmemberikanrekomendasidalamrangkamencegahterjadinya\nkecelakaandenganpenyebab\nyangsama.\n(6)\nrekomendasisebagaimanadimaksudpadaayat(5)harus\nditindaklanjutiolehpihakterkait.\npasal\n61\n(1)\ntimteknisahliwajibmelaporkansegalaperkembangandanhasil\ninvestigasikepadalembaga.\n(2)\nlembagadapatmenyampaikanlaporanhasil\ninvestigasikepadapihak\nterkait.\npasal\n62\n(1)\nhasilinvestigasitidakdapatdigunakansebagaialatbuktidalam\nprosesperadilan.\n(2)\nhasilinvestigasisebagaimanadimaksudpada\nayat(1),yangbukan\ndigolongkansebagaiinformasirahasia,dapatdiumumkan\nkepada\nmasyarakat.\npasal\n63\n(1)\nsetiaporangdilarangmerusakataumenghilangkanbukti,mengubah\nletakwahanaantariksa,danmengambilbagianataumengambil\nbaranglainyangtersisaakibatdarikecelakaanataukejadianserius\nwahanaantariksa.\n(2)\nuntuk\nkepentingankeamanandankeselamatan,wahanaantariksa\nyangmengalamikecelakaanataukejadianseriussebagaimana\ndimaksudpadaayat(1)dapatdipindahkanataspersetujuaninstansi\nyangberwenang.\n2013,\nno.\n133\n24\npasal\n64\n(1)\ndalamhalwahanaantariksaasingmengalamike\ncelakaandiwilayah\nkedaulatandanwilayahyurisdiksinegarakesatuanrepublik\nindonesia,wakilresmidarinegaratempatwahanaantariksa\ndiluncurkan,negaratempatbadanusahapeluncuranwahana\nantariksa,negaratempatperancang,dannegaratempatpembuat\nan\ndapatdiikutsertakandalaminvestigasisepanjangtidakbertentangan\ndengankepentingannasional.\n(2)\ndalamhalwahanaantariksayangterdaftaratasnamaindonesia\nmengalamikecelakaandiluarwilayahnegarakesatuanrepublik\nindonesiadannegaratempat\nterjadinyakecelakaantidakmelakukan\ninvestigasi,pemerintahrepublikindonesiawajibmelakukan\ninvestigasi.\npasal\n65\n(1)\norangperseorangan,jikadiminta,wajibmemberikanketeranganatau\nbantuanjasakeahlianuntukkelancaraninvestigasiyangdibutuhk\nan\nolehtimteknisahli.\n(2)\notoritasbandarantariksadanpetugaskeselamatanpeluncuran\nwahanaantariksawajibmembantukelancaraninvestigasikecelakaan\nwahanaantariksa.\npasal\n66\n(1)\npejabatyangberwenangdilokasikecelakaanwahanaantariksawajib\nmelakukantindakanpengamananterhadapwahanaantariksayang\nmengalamikecelakaandiluardaerahlingkungankerjabandar\nantariksauntuk:\na.\nmelindungipersonelwahanaantariksadanpenumpangnya;dan\nb.\nmencegahterjadinyatindakanyangdapatmengubahleta\nk\nwahanaantariksa,merusak,dan/ataumengambilbarangdari\nwahanaantariksayangmengalamikecelakaan.\n(2)\ntindakanpengamanansebagaimanadimaksudpadaayat(1)\nberlangsungsampaidenganberakhirnyapelaksanaaninvestigasi\nlokasikecelakaanolehtimtek\nnisahli.\npasal\n67\n(1)\ndalammelaksanakaninvestigasi,timteknisahliberwenang:\na.\nmenghadirkanseseoranguntukdimintaiketeranganterkait\ndenganprosesinvestigasi;dan\nb.\nmemerintahkanseseoranguntukmenyerahkandokumenatau\ncatatantertentu,bagianterte\nntu,ataukomponendaribenda\nantariksaataubendalainyangrelevandalamprosesinvestigasi.\n2013,\nno.\n133\n25\n(2)\ndalammelaksanakaninvestigasisebagaimanadimaksudpadaayat\n(1),pemberitahuansecaratertulisdilakukanterlebihdahulu.\n(3)\npemberitahuansebagaimanadi\nmaksudpadaayat(2)wajib\nditandatanganiolehtimteknisahlidanharusdicantumkanwaktu\ndantempatorangtersebutharushadirataumenyerahkanbendayang\ndianggaprelevandalamprosesinvestigasi.\n(4)\ntimteknisahlidapatmemintaketeranganseseorang\nsebagaimana\ndiaturpadaayat(1)hurufadibawahsumpahataudibawah\npernyataan.\n(5)\ntimteknisahlidapat:\na.\nmenyitabendasebagaimanadimaksudpada\nayat(1)hurufb\nselamadiperlukanuntuktujuaninvestigasi;dan\nb.\nmembuatsalinanataumenyalindokumenata\nucatatanjika\nbendatersebutberupadokumenataucatatan.\n(6)\napabilaseseorangmemberikanketerangansebagaimanadimaksud\npadaayat(1)hurufa,keterangandaninformasilainyangdidapatkan,\nbaiksecaralangsungmaupuntidaklangsung,tidakdapatdijad\nikan\nbuktiyangmemberatkanorangtersebutdalamprosespersidangan,\nkecualidalamhalpersidanganberkaitandenganpemberian\nketeranganpalsu.\n(7)\napabilaseseorangmenyerahkanbenda\n-\nbendasebagaimanadimaksud\npadaayat(1)hurufb,bendadaninformasila\ninyangdidapatkan\nsecaralangsungataupuntidaklangsungtidakdapatdijadikanbukti\nyangmemberatkanorangtersebutdalampersidanganperkarapidana\nataupersidanganperkaratuntutangantirugi.\n(8)\norangyangdihadirkanolehtimteknisahlidapatmemper\noleh\npenggantianbiaya.\npasal\n68\nketentuanlebihlanjutmengenaitatacarapelaksanaaninvestigasi\nkecelakaanwahanaantariksadiaturdalamperaturanlembaga.\npasal\n69\n(1)\nsegerasetelahterjadikecelakaan,izinpeluncurandanhallainyang\nterkaitsebagai\nmanadimaksuddalampasal34,pasal35,danpasal36\nakanditangguhkansampaipembekuantersebutdicabutolehmenteri.\n(2)\nizinpeluncurandanhallainyangterkaitsebagaimanadimaksudpada\nayat(1)tidakakanberlakuselamadibekukan.\n(3)\nmasaizinpelun\ncuransebagaimanadimaksudpadaayat(2)masih\ntetapberlakuselamamasapembekuan.\n2013,\nno.\n133\n26\n(4)\nizinpeluncurandanhallainyangterkaitsebagaimanadimaksudpada\nayat(2)dapatdicabutataudiubahselamadalammasapembekuan.\n(5)\nketentuanmengenaikriteriadan\npersyaratanpenangguhan,\npembekuan,pencabutan,danperubahanizinpeluncurandiatur\ndalamperaturanpemerintah.\nbagian\nkedua\npencariandanpertolonganantariksawan\npasal\n70\n(1)\npemerintahbertanggungjawabmelaksanakanpencariandan\npertolonganterhadappend\naratandaruratdan/ataukecelakaan\nantariksawanyangterjadidiwilayahkedaulatannegarakesatuan\nrepublikindonesia.\n(2)\ntanggungjawabpelaksanaanpencariandanpertolonganoleh\npemerintahsebagaimanadimaksudpadaayat(1)dikoordinasikandan\ndilakukanole\nhinstansiyanglingkuptugasdantanggungjawabnyadi\nbidangpencariandanpertolongan.\nbab\nviii\npendaftaran\npasal\n71\n(1)\nsetiapbendaantariksayangdiluncurkandariwilayahkedaulatan\ndanwilayahyurisdiksinegarakesatuanrepublikindonesiaatau\ndiluncurka\nn\ndiwilayahnegaralainolehinstansipemerintah,badan\nhukum,atauwarganegaraindonesiawajibdidaftarkankepada\nlembaga.\n(2)\ndaftarwahanaantariksapalingsedikitmemuat:\na.\nnamanegarapeluncur;\nb.\nketerangantandawahanaantariksaataunomorpendaftaran\nwaha\nnaantariksa;\nc.\ntanggal,waktu,dantempatpeluncuran;\nd.\nparameterorbitdasaryangmeliputiperiodenodal,inklinasi,serta\napogeedanperigeewahanaantariksa;\ne.\nfungsiumumwahanaantariksa;\nf.\nnamanegarapesertalainjikaterdapatlebihdarisatunegara\npelunc\nur;dan\ng.\ninformasilainyangdianggapterkaitdanbergunauntuktujuan\npendaftaran.\n2013,\nno.\n133\n27\n(3)\npendaftaransebagaimanadimaksudpadaayat(1)wajibdiberinomor\npendaftaran.\n(4)\npelaksanaanpendaftaranwahanaantariksaharusmemperhatikan\npraktikpelaksanaanpendaftaranb\nendaantariksasesuaidengan\nkonvensitentangpendaftaranbenda\n-\nbendayangdiluncurkan\nke\nantariksa.\npasal\n72\n(1)\ndaftarwahanaantariksawajibdiumumkan,mudahdiakses,dan\ndapatterkoneksisecarainternasionalsertadisimpansecarakhusus\nolehlembagapadap\nusatdatadaninformasikeantariksaan.\n(2)\nlembagadapatmengubahdanmenghapusdatabendaantariksadari\nbasisdatasesuaidengankeperluannya.\n(3)\nlembagawajibmendaftarkandatawahanaantariksaindonesia\nkepadasekretarisjenderalperserikatanbangsa\n-\nbangsa.\nb\nab\nix\nkerjasamainternasional\npasal\n73\n(1)\npemerintahdapatmengadakankerjasamainternasionaldibidang\nkeantariksaandenganpemerintahnegaralain,lembaga,atau\norganisasiinternasionalsesuaidenganketentuanperaturan\nperundang\n-\nundangan.\n(2)\nkerjasamainte\nrnasionalsebagaimanadimaksudpadaayat(1)dapat\nberupa:\na.\npenguasaanteknologi;\nb.\npemanfaatanteknologi;\nc.\nalihpengetahuan;\nd.\nalihteknologi;dan/atau\ne.\npeningkatankapasitassumberdayamanusia.\npasal\n74\n(1)\npemerintahharusterlibataktifdalamkeanggotaanorganis\nasi\ninternasionalkeantariksaanuntukmeningkatkankerjasama\ninternasional.\n(2)\nkeikutsertaanindonesiasebagaimanadimaksudpadaayat(1)\ndilakukansesuaidenganketentuanperaturanperundang\n-\nundangan.\n2013,\nno.\n133\n28\npasal\n75\n(1)\nkerjasamainternasionalkeantariksaandiar\nahkanuntukupayaalih\nteknologidan/atauilmupengetahuansertauntukmendorong\nkemandiriandalamkegiatanpenyelenggaraankeantariksaan.\n(2)\nuntukmencapaitujuansebagaimanadimaksudpadaayat(1),\npemerintahdalamsetiapkerjasamainternasionalkeanta\nriksaan\nwajibmengupayakan:\na.\npemberianpeluangpelatihandankesempatankerjabagistaf\nteknisiterkait;\nb.\npenyelenggaraanhubungandenganpusat\n-\npusatpenelitian,baik\npemerintahmaupunswasta;\nc.\npengusahaanbersamaolehswastadanpemerintah;\nd.\npengembangankema\nmpuankapasitasuntukpenelitian;\npenerapandanmanajemenmelaluipengembangansumberdaya\nmanusia;peningkatankapasitaskelembagaanuntukpenelitian\ndanpengembangan;sertaprogram\n-\nprogramimplementasidan\npenelitiankebutuhanteknologidankemitraanjan\ngkapanjang\nantarapemilikteknologidanpenggunapotensiallokal.\n(3)\ntatacarapelaksanaankerjasamainternasionaldilakukansesuai\ndenganketentuanperaturanperundang\n-\nundangan.\nbab\nx\ntanggungjawabdangantirugi\nbagian\nkesatu\ntanggungjawab\npasal\n76\n(1)\npemerintahrepublikindonesiabertanggungjawabsecara\ninternasionalatassetiappenyelenggaraankeantariksaanyang\ndilakukandiwilayahkedaulatandan/atauwilayahyurisdiksinegara\nkesatuanrepublikindonesia.\n(2)\ndalamhalterdapatkerugianakibatd\naripenyelenggaraan\nkeantariksaan,gantirugimenjaditanggungjawabpenyelenggara\nkeantariksaan.\n(3)\ntanggungjawabsebagaimanadimaksudpadaayat(1)dilakukan\nsesuaidenganketentuanperaturanperundang\n-\nundangan.\npasal\n77\n(1)\ntanggungjawabterhadapke\nrugianyangditimbulkanoleh\npenyelenggaraankeantariksaanyangterjadi\ndipermukaanbumiatau\npadapesawatudarayangsedangdalampenerbanganbersifatmutlak.\n2013,\nno.\n133\n29\n(2)\ntanggungjawabterhadapkerugianyangterjadi\ndiantariksa\ndan/atauterhadapwahanaantarik\nsa\ndiantarasesama\npenyelenggarakeantariksaandidasarkanatasadanyaunsur\nkesalahan.\n(3)\ntanggungjawabterhadapkerugiandiantarasesamapenyelenggara\nkeantariksaansebagaimanadimaksudpadaayat(2)ditetapkan\nberdasarkanperjanjianparapihak.\npasal\n78\n(1)\ndalamhalterjadipengalihankepemilikanterhadapaset\nkeantariksaan,tanggungjawabpenyelenggarakeantariksaanberalih\nsejakberlakunyaperjanjianpengalihan.\n(2)\npengalihankepemilikanasetkeantariksaanmilikpemerintah\ndilakukansesuaidenganperatura\nnperundang\n-\nundanganyang\nmengaturbarangmiliknegara/daerah.\n(3)\nperjanjianpengalihansebagaimanadimaksudpadaayat(1)tidak\ndapatmeniadakanketentuanyangterdapatdalambabvi.\nbagian\nkedua\ngantirugi\npasal\n79\n(1)\ntuntutangantirugidapatdilakukansesuai\ndenganmekanisme\nhukuminternasionalyangberlaku,baikmelaluijalurdiplomatik,\nkomisipenuntutan,maupunbadanperadilannasional.\n(2)\nsetiappenyelenggarakeantariksaanwajibmenggantisetiapkerugian\nyangtimbulakibatpenyelenggaraankeantariksaanyang\ndilakukan.\n(3)\nkerugiansebagaiakibatdarikegiatankeantariksaanyangdapat\ndimintakankompensasinyaadalahkerugianyangbersifatfisikdan\nlangsung,termasukbiaya\n-\nbiayayangdikeluarkanuntukmelakukan\nkegiatanpertolongandanpembersihan.\npasal\n80\ntuntut\nangantirugihanyadapatdiajukan:\na.\ndalamjangkawaktu1(satu)tahunsetelahharitimbulnyakerugian;\natau\nb.\ndalamhaltimbulkerugian,tetapipihakyangmenuntuttidak\nmengetahuibahwakerugiantersebuttelahterjadidalamjangka\nwaktu1(satu)tahunsetel\nahpihakyangmenuntut:\n2013,\nno.\n133\n30\n1.\nmengetahuiadanyakerugian;atau\n2.\nakanmengetahuiadanyakerugian.\npasal\n81\npengaturanbebantanggungjawabrentengataskerugianyangdiderita\nolehnegaraataupihakasingsebagaimanadimaksuddalampasal77ayat\n(3)dapatditentuka\nnolehpenyelenggarakeantariksaanterkait.\npasal\n82\n(1)\ndalamhalterjadikerugianyangdideritaolehbadandan/atauwarga\nnegaraindonesiaakibatkegiatankeantariksaan,gugatandapat\ndiajukankepadapihakpelakukegiatankeantariksaanmelalui\nlembagap\neradilan,lembagaarbitrase,dan/ataulembagaalternatif\npenyelesaiansengketa.\n(2)\npengajuangugatandanpenyelesaiangantirugidapatdifasilitasioleh\npemerintah.\n(3)\npembayarangantirugikepadakorbansebagaimanadimaksudpada\nayat(1)harusdilaksana\nkandengansegera,efektif,danlayak.\npasal\n83\nketentuanlebihlanjutmengenaitanggungjawabdangantirugi\nsebagaimanadimaksuddalampasal76sampaidenganpasal82diatur\ndalamperaturanpemerintah.\nbab\nxi\nasuransi,penjaminan,danfasilitas\nbagian\nke\nsatu\nasuransi\npasal\n84\n(1)\nsetiappenyelenggarakeantariksaanwajibmengasuransikantanggung\njawabkerugianterhadappihakketigayangtimbulsebagaiakibatdari\nkegiatankeantariksaanyangdilakukan.\n(2)\nketentuantentangkewajibanasuransisebagaimanadimaksudp\nada\nayat(1)tidakberlakubagiinstansipemerintah.\n(3)\nketentuanmengenaiasuransisebagaimanadimaksudpadaayat(1)\ndanketentuanpenggantiankerugianakibatkecelakaan\npenyelenggaraankeantariksaanolehinstansipemerintahdiatur\ndalamperaturanpemerinta\nh.\n2013,\nno.\n133\n31\nbagian\nkedua\npenjaminan\npasal\n85\n(1)\nasetkeantariksaanyangbukanmilikpemerintahdapatdijadikan\nobjekpenjaminansesuaidenganketentuanperaturanperundang\n-\nundangan.\n(2)\npelaksanaanperjanjianpenjaminanwajibmematuhiketentuanbabx\ndanbabxvdalamunda\nng\n-\nundangini.\n(3)\nasetkeantariksaanmilikpemerintahdilaranguntukdijadikanobjek\npenjaminan.\nbagian\nketiga\nfasilitas\npasal\n86\ndalamrangkamendorongpengembangankeantariksaan,penyelenggara\nkeantariksaandapatdiberifasilitasolehpemerintahsesuaideng\nan\nketentuanperaturanperundang\n-\nundangan.\nbab\nxii\npelestarianlingkungan\npasal\n87\nsetiappenyelenggarakeantariksaanwajibmenjagadanmenjamin\nterpeliharanyapelestarianfungsilingkunganhidup.\npasal\n88\n(1)\nuntukmenghindarikerusakanlingkunganbumidarik\nontaminasi\nyangdisebabkanolehpenyelenggaraankeantariksaan,setiap\npenyelenggarakeantariksaandilarangmelanggarbakumutudan\nkriteriabakukerusakanlingkunganhidup.\n(2)\nketentuanmengenaibakumutudankriteriabakukerusakan\nlingkunganhidupsebagaima\nnadimaksudpadaayat(1)dilaksanakan\nsesuaidenganketentuanperaturanperundang\n-\nundangan.\nbab\nxiii\npendanaan\npasal\n89\nsumberpendanaankegiatankeantariksaanberasaldarianggaran\npendapatandanbelanjanegara,hibah,swasta,dankerjasama\ninternasiona\nl.\n2013,\nno.\n133\n32\nbab\nxiv\nperansertamasyarakat\npasal\n90\n(1)\ndalamrangkameningkatkanpenyelenggaraankeantariksaansecara\noptimal,masyarakatmemilikikesempatanyangsamadanseluas\n-\nluasnyauntukberperansertadalamkegiatankeantariksaan.\n(2)\nperansertamasyarakatsebagai\nmanadimaksudpadaayat(1)adalah:\na.\nmemantaudanmenjagaketertibanpenyelenggaraan\nkeantariksaan;\nb.\nmemberikanmasukankepadapemerintahdalampenyempurnaan\nperaturan,pedoman,danstandarteknisdibidangkeantariksaan;\nc.\nmemberikanmasukankepadapemerintah\ndanpemerintah\ndaerahdalamrangkapembinaan,penyelenggaraan,dan\npengawasankegiatankeantariksaan;\nd.\nmenyampaikanpendapatdanpertimbangankepadapejabatyang\nberwenangterhadapkegiatankeantariksaanyangmengakibatkan\ndampakpentingterhadaplingkunga\nn;\ne.\nmelaporkanapabilamengetahuiterjadinyaketidaksesuaian\nprosedurkeantariksaanatauketidakberfungsianperalatandan\nfasilitaskeantariksaan;\nf.\nmelaporkanapabilamengetahuiterjadinyakecelakaanatau\nkejadianterhadappeluncuranwahanaantariksaatauad\nanya\nbendajatuhdariantariksa;\ng.\nmengutamakandanmempromosikanbudayakeselamatan\nkeantariksaan;\ndan/atau\nh.\nmelaksanakangugatanperwakilanterhadapkegiatan\nkeantariksaanyangmengganggu,merugikan,dan/atau\nmembahayakankepentinganumum.\n(3)\npemerintah,pemer\nintahdaerah,danpenyelenggarakeantariksaan\nmenindaklanjutimasukan,pendapat,danlaporanyangdisampaikan\nolehmasyarakatsebagaimanadimaksudpadaayat(2)hurufb,\nhuruf\nc,hurufd,hurufe,danhuruff.\n(4)\ndalammelaksanakanperansertasebagaimanadim\naksudpadaayat\n(2),masyarakatikutbertanggungjawabmenjagaketertibanserta\nkeselamatandankeamanankegiatankeantariksaan;\npasal\n91\nperansertamasyarakatsebagaimanadimaksuddalampasal90dapat\ndilakukansecaraperseorangan,kelompok,organisasip\nrofesi,badan\n2013,\nno.\n133\n33\nusaha,atauorganisasikemasyarakatanlainsesuaidenganprinsip\nketerbukaandankemitraan.\npasal\n92\nketentuanlebihlanjutmengenaiperansertamasyarakatdiaturdalam\nperaturanpemerintah.\nbab\nxv\nsanksiperdatadanadministratif\npasal\n93\nse\ntiapkegiatankeantariksaanyangdilaksanakanolehpenyelenggara\nkeantariksaanyangkarenakesalahannyamengakibatkankerugian,\npenyelenggarakeantariksaandikenaituntutangantirugiyang\npelaksanaannyadilakukansesuaidenganketentuanperaturan\nperundan\ng\n-\nundangan.\npasal\n94\n(1)\nsetiaporangyangmelanggarketentuansebagaimanadimaksuddalam\npasal21,pasal35,pasal45ayat(2),pasal48,pasal49,pasal51ayat\n(2),pasal56,ataupasal65dikenaisanksiadministratif.\n(2)\nsanksiadministratifsebagai\nmanadimaksudpadaayat(1)dapat\nberupa:\na.\nperingatantertulis;\nb.\npenghentiansementarasebagianatauseluruhkegiatan;\nc.\ndendaadministratif;\nd.\npembongkaranbangunan;\ne.\npencabutanizin;\nf.\npembubarankorporasiataubadanhukum;\ng.\nlaranganmendudu\nkisuatujabatan;dan/atau\nh.\npencabutanhak.\n(3)ketentuanlebihlanjutmengenaitatacarapengenaansanksi\nadministratifdanbesarandendaadministratifsebagaimanadimaksud\npadaayat(2)diaturdalamperaturanpemerintah.\nbab\nxvi\nketentuanpidana\npasal\n95\n(1)\nsetiaporang\nyangdengansengajatidakmelaporkanhasilpenelitian\nyangbersifatsensitifdandapatberdampakluassebagaimana\n2013,\nno.\n133\n34\ndimaksuddalam\npasal12dipidanadenganpidanapenjarapaling\nlama\n6(enam)bulanataudendapalingbanyakrp500.000.000,00\n(l\nimaratusjutarupiah).\n(2)\ndalamhalperbuatansebagaimanadimaksudpadaayat(1)\nmengakibatkanterganggunyakepentingankeamanannasionalatau\nkepentinganpemerintah,pelakudipidanadenganpidanapenjara\npalinglama\n2(dua)tahunataudendapalingbanyak\nrp\n2.000.000.000,00(duamiliarrupiah).\npasal\n96\n(1)\nsetiaporangyangmelaksanakankegiatanpeluncuranwahana\nantariksayangdengansengajatidakmemenuhipersyaratan\nketentuansebagaimanadimaksuddalampasal35yang\nmengakibatkantimbulnyakerugianbagibaran\ngatauorangdipidana\ndenganpidanapenjarapalinglama15(lima\nbelas)tahunataudenda\npalingbanyakrp4.000.000.000.000,00(empattriliunrupiah).\n(2)\ndalamhalperbuatansebagaimanadimaksudpadaayat(1)\nmengakibatkanhilangnyanyawaorang,pelakudipidan\nadengan\npidanapenjarapalinglama20(duapuluh)tahunataudendapaling\nbanyakrp5.000.000.000.000,00(limatriliunrupiah).\npasal\n97\nsetiaporang\nyangmenghilangkanataumengubahletakdanmengambil\nbagianbendajatuhantariksayangjatuhdiwilayahke\ndaulatandan\nwilayahyurisdiksinegarakesatuanrepublikindonesiasebagaimana\ndimaksuddalampasal58ayat(3)danpasal63ayat(1),yangsudahdiberi\ntandabataslaranganmasukdalamareabendajatuhtersebut,dipidana\ndenganpidanapenjarapalinglama\n6(enam)bulanataudendapaling\nbanyakrp500.000.000,00(limaratusjutarupiah).\npasal\n98\n(1)\nsetiaporang\nyangmelanggarbakumutudankriteriabakukerusakan\nlingkunganhidupsebagaimanadimaksuddalampasal88,yang\nmengakibatkantercemaratauterkontamin\nasinyalingkungan\nhidup,\ndipidanadenganpidanapenjarapalinglama\n2(dua)tahunataudenda\npalingbanyakrp2.000.000.000,00(duamiliarrupiah).\n(2)\ndalamhalperbuatansebagaimanadimaksudpadaayat(1)\nmengakibatkantimbulnyakerugianbagibarangatauoran\ng,pelaku\ndipidanadenganpidanapenjarapalinglama5(lima)tahunatau\ndendapalingbanyakrp5.000.000.000,00(limamiliarrupiah).\npasal\n99\nsetiaporangyangdengansengajamelakukansalahsatuataulebih\nkegiatankeantariksaansebagaimanadimaksuddala\nmpasal8dipidana\n2013,\nno.\n133\n35\ndenganpidanapenjarapalinglama20(duapuluh)tahunataudenda\npalingbanyakrp5.000.000.000.000,00(limatriliunrupiah).\npasal\n100\ndalamhalperbuatansebagaimanadimaksuddalampasal95,pasal96,\npasal97,pasal98,danpasal99d\nilakukanolehkorporasiataubadan\nhukum,selainpidanapenjaradanpidanadendaterhadappengurusnya,\npidanadapatdijatuhkanterhadapkorporasiatau\nbadanhukumberupa\npidanadendadenganpemberatan3(tiga)kalidaripidanadendaterhadap\norang.\nbab\nxvi\ni\nketentuanlain\n-\nlain\npasal\n101\n(1)\ndalamhalpenyelenggaraankeantariksaanuntukpenggunaan\nspektrumfrekuensiradiodanorbitsatelituntukkegiatan\nkeantariksaan,pembinaannyadilakukanolehmenteriyang\nmenyelenggarakanurusanpemerintahandibidangkomuni\nkasidan\ninformatikasesuaidenganketentuanperaturanperundang\n-\nundangan.\n(2)\npembinaansebagaimanadimaksudpadaayat(1)meliputipengaturan,\npengawasan,danpengendalian.\npasal\n102\n(1)\nlembagamenyusunrencanapenggunaanfrekuensiradiountuk\npenyelenggaraank\neantariksaannasionaldanpemutakhirannyaserta\nmelaporkankepadamenteriyangmenyelenggarakanurusan\npemerintahandibidangkomunikasidaninformatika.\n(2)\nlembagawajibmendaftarkanpenggunaanfrekuensiradiountuk\noperasisatelitkebadantelekomunikasiin\nternasionalmelalui\nkementerianyangmenyelenggarakanurusanpemerintahandibidang\nkomunikasidaninformatika.\n(3)\nmenteriyangmenyelenggarakanurusanpemerintahandibidang\nkomunikasidaninformatikawajibmemprioritaskanpenggunaan\nfrekuensiradiountukke\ngiatankeantariksaan.\nbab\nxviii\nketentuanperalihan\npasal\n103\npadasaatundang\n-\nundanginiberlaku,pembangunandanpengoperasian\nstasiunbumiyangtelahada\nwajibdilaporkanpengoperasiannyapaling\nlambat\n1(satu)tahunsejakundang\n-\nundanginiberlaku.\n2013,\nno.\n133\n36\nbab\nxix\nketentuanpenutup\npasal\n104\n(1)\nperaturanpemerintahyangdiamanatkan\nundang\n-\nundanginiharus\nditetapkanpalinglama\n2(dua)tahunterhitungsejakundang\n-\nundang\ninidiundangkan.\n(2)\nperaturanpresidenyangdiamanatkan\nundang\n-\nundanginiharus\nditetap\nkanpalinglama\n2(dua)tahunterhitungsejakundang\n-\nundang\ninidiundangkan.\n(3)\nperaturanlembagayangdiamanatkan\nundang\n-\nundanginiharus\nditetapkanpalinglama\n1(satu)tahunterhitungsejakundang\n-\nundang\ninidiundangkan.\npasal\n105\nundang\n-\nundanginimu\nlaiberlakupadatanggaldiundangkan.\nagarsetiaporangmengetahuinya,memerintahkanpengundangan\nundang\n-\nundanginidenganpenempatannyadalamlembarannegara\nrepublikindonesia.\ndisahkandijakarta\npadatanggal6agustus2013\npresidenrepublikindonesia,\ndr.h.susilobambangyudhoyono\ndiundangkandijakarta\npadatanggal6agustus2013\nmenterihukumdanhakasasimanusia\nrepublikindonesia,\namirsyamsudin\n');
INSERT INTO `korpus` (`id`, `namafile`, `pathfile`, `isi`) VALUES
(16, 'UU Nomor 19 Tahun 2013 - Perlindungan dan Pemberdayaan Petani.pdf', '/opt/lampp/temp/phpnWL0uO', 'lembarannegara\nr\nepublik\ni\nndonesia\nno.\n131,2013\nkesejahteraan.petani.perlindungan.\npemberdayaan.\n(penjelasandalamtambahan\nlembarannegararepublikindonesianomor\n5433\n)\nundang\n-\nundang\nrepublikindonesia\nnomor19\ntahun\n2013\ntentang\nperlindungandanpemberdayaanpetani\ndenganrahmattuhanyangmahaesa\npresidenrepublikindonesia,\nmenimbang\n:\na.\nbahwapancasila\ndan\nundang\n-\nundang\ndasarnegara\nrepublikindonesiatahun1945mengamanatkannegara\nmempunyaitanggungjawabuntukmeli\nndungisegenap\nbangsaindonesiadanmemajukankesejahteraanumum,\nmencerdaskankehidupanbangsa,sertamewujudkan\nkeadilansosialbagiseluruhrakyatindonesia;\nb.\nbahwauntukmewujudkanmasyarakatadildanmakmur\nsertauntukmemenuhihakdankebutuhandasar\nwarga\nnegara,negaramenyelenggarakanperlindungandan\npemberdayaanmasyarakat,khususnyapetanisecara\nterencana,terarah,danberkelanjutan;\nc.\nbahwakecenderunganmeningkatnyaperubahaniklim,\nkerentananterhadapbencanaalamdanrisikousaha,\nglobalisasi\ndangejolakekonomiglobal,sertasistempasar\nyangtidakberpihakkepadapetani,sehinggapetani\nmembutuhkanperlindungandanpemberdayaan;\nd.\nbahwaperaturanperundang\n-\nundanganyangsaatini\nberlakubelummengaturperlindungandan\npemberdayaanpetanisecara\nkomprehensif,sistemik,dan\nholistik;\n2013,\nno.\n131\n2\ne.\nbahwaberdasarkanpertimbangansebagaimana\ndimaksud\ndalam\nhurufa,hurufb,hurufc,danhurufd\nperlumembentukundang\n-\nundangtentangperlindungan\ndanpemberdayaanpetani;\nmengingat\n:\n1.\npasal20\n,\npasal21\n,pasal28h\n,danpasal33ayat(3)\nundang\n-\nundangdasarnegararepublikindonesiatahun\n1945\n;\n2.\nu\nndang\n-\nundang\nno\nmor\n41tahun2009tentang\nperlindunganlahanpertanianpangan\nb\nerkelanjuta\nn\n(lembarannegararepublikindonesiatahun2009nomor\n149,tambahanlembarannegara\nrepublikindonesia\nnomor5068)\n;\ndenganpersetujuanbersama\ndewanperwakilanrakyatrepublikindonesia\nd\nan\npresidenrepublikindonesia\nmemutuskan:\nmenetapka\nn\n:\nundang\n-\nundangtentangperlindungandan\npemberdayaanpetani.\nbabi\nketentuanumum\npasal1\ndalamu\nndang\n-\nundanginiyangdimaksuddengan:\n1.\nperlindungan\npetaniadalahsegalaupayauntukmembantupetani\ndalammenghadapipermasalahankesulitanmemperolehprasarana\ndansaranaproduksi,kepastianusaha,risikoharga,kegagalanpanen,\npraktikekonomibiayatin\nggi,danperubahaniklim.\n2.\npemberdayaan\npetani\nadalahsegalaupayauntukmeningkatkan\nkemampuanpetaniuntukmelaksanakanusahataniyanglebihbaik\nmelaluipendidikandanpelatihan,penyuluhandanpendampingan,\npengembangansistemdansaranapemasaranhasi\nlpertanian,\nkonsolidasidanjaminanluasanlahanpertanian,kemudahanakses\nilmupengetahuan,teknologidaninformasi,sertapenguatan\nkelembagaanpetani.\n3.\npetaniadalahwarganegaraindonesiaperseorangandan/ataubeserta\nkeluarganyayangmelakukanusaha\ntanidibidangtanamanpangan,\nhortikultura,perkebunan\n,\ndan/ataupeternakan.\n2013,\nno.\n131\n3\n4.\npertanian\nadalahkegiatanmengelolasumberdayaalamhayatidengan\nbantuanteknologi,modal,tenagakerja,danmanajemenuntuk\nmenghasilkankomoditaspertanianyangmencakuptana\nmanpangan,\nhortikultura,perkebunan\n,\ndan/ataupeternakandalamsuatu\nagroekosistem.\n5.\nkomoditaspertanianadalah\nhasildariusahataniyangdapat\ndiperdagangkan,disimpan,dan/ataudipertukarkan\n.\n6.\nusahataniadalahkegiatandalambidangpertanian,mulaidari\nsaranaproduksi,produksi/budidaya,penangananpascapanen,\npengolahan,\npemasaranhasil,dan/ataujasapenunjang.\n7.\npelakuusahaadalahsetiaporangyangmelakukanusahasarana\nproduksipertanian,pengolahandanpemasaranhasilpertanian,serta\njasapenunja\nngpertanian\nyangberkedudukandiwilayahhukum\nrepublikindonesia.\n8.\nsetiaporangadalahorangperseoranganataukorporasi,baikyang\nberbadanhukummaupunyangtidakberbadanh\nu\nkum\n.\n9.\nkelembagaanpetaniadalahlembagayangditumbuhkembangkandari,\noleh,dan\nuntukpetanigunamemperkuatdanmemperjuangkan\nkepentinganpetani.\n10.\nkelompoktaniadalahkumpulanpetani/peternak/pekebunyang\ndibentukatasdasarkesamaankepentingan;kesamaankondisi\nlingkungansosial,ekonomi,sumberdaya;kesamaankomoditas;dan\nkeakr\nabanuntukmeningkatkansertamengembangkanusaha\nanggota.\n11.\ngabungankelompoktaniadalahkumpulanbeberapakelompoktani\nyangbergabungdanbekerjasamauntukmeningkatkanskala\nekonomidanefisiensiusaha.\n12.\nasosiasi\nkomoditaspertanian\nadalahkumpulandari\npetani,kelompok\ntani,dan/atauga\nbungankelom\npok\nt\nan\ni\nuntukmemperjuangkan\nkepentinganpetani\n.\n13.\ndewankomoditaspertaniannasionaladalahsuatulembagayang\nberanggotakanasosiasikomoditaspertanianuntukmemperjuangkan\nkepentinganpetani.\n14.\nkelembagaanek\nonomipetaniadalahlembagayangmelaksanakan\nkegiatanusahataniyangdibentukoleh,dari,danuntukpetani,guna\nmeningkatkanproduktivitasdanefisiensiusahatani,baikyang\nberbadanhukummaupunyangtidakberbadanhukum.\n15.\nlembagapembiayaanadalahbad\nanusahayangmelakukankegiatan\npembiayaandalambentukpenyediaandanaataubarangmodaluntuk\nmemfasilitasisertamembantupetanidalammelakukanusahatani.\n2013,\nno.\n131\n4\n16.\nasuransipertanianadalahperjanjianantarapetanidanpihak\nperusahaan\nasuransiuntuk\nmengikat\nkandiridalampertanggungan\nrisikousahatani\n.\n17.\npemerintahpusat,selanjutnyadisebutpemerintah,adalahpresiden\nrepublikindonesiayangmemegangkekuasaanpemerintahannegara\nrepublikindonesiasebagaimanadimaksuddalamundang\n-\nundang\ndasarnegararepubl\nikindonesiatahun1945.\n18.\npemerintahdaerahadalahgubernur,bupati/walikota,danperangkat\ndaerahsebagaiunsurpenyelenggarapemerintahandaerah.\n19.\nmenteriadalahmenteriyangmenyelenggarakanurusanpemerintahan\ndibidangpertanian.\nbabii\nasas,tujuan,da\nnlingkup\npengaturan\npasal2\nperlindungandanpemberdayaanpetaniberasaskanpada:\na.\nkedaulatan;\nb.\nkemandirian;\nc.\nkebermanfaatan;\nd.\nkebersamaan\n;\ne.\nketerpaduan;\nf.\nketerbukaan\n;\ng.\nefisiensi\n-\nberkeadilan;\ndan\nh.\nk\ne\nb\ne\nr\nlanjutan.\npasal3\nperlindungan\ndanpemberdayaanpetanibertuj\nuanuntuk\n:\na.\nmewujudkankedaulatan\ndan\nkemandirianpetanidalamrangka\nmeningkatkantarafkesejahteraan,kualitas,dan\nkehidupan\nyang\nlebihbaik\n;\nb.\nmenyediakanprasaranadansaranapertanianyangdibutuhkandalam\nmengembangkanusahatani;\nc.\nmemberikankepastian\nusahatani\n;\nd.\nmelindungipetanidari\nfluktuasiharga,praktikekonomibiayatinggi,\ndangagalpanen;\ne.\nmeningkatkankemampuandankapasitaspetanisertakelembagaan\npetanidalammenjalankanusahataniyangproduktif,maju,modern\ndan\nberkelanjutan\n;dan\n2013,\nno.\n131\n5\nf.\nmenumbu\nhkembangkankelembagaanpembiayaanpertanianyang\nmelayanikepentinganusahatani.\npasal4\nlingkuppengaturan\nperlindungan\ndanpemberdayaanpetanimeliputi:\na.\nperencanaan;\nb.\nperlindunganpetani;\nc.\npemberdayaanpetani;\nd.\npembiayaan\ndanpendanaan;\ne.\npengawasan;dan\nf.\npe\nransertamasyarakat.\nbabiii\nperencanaan\npasal5\n(1)\nperencanaan\nperlindungan\ndanpemberdayaanpetanidilakukan\nsecarasistematis,terpadu,terarah,menyeluruh,transparan,dan\nakuntabel.\n(2)\nperencanaansebagaimanadimaksudpadaayat(1)harus\ndilakukan\ndengan\nb\nerdasarkanpada\n:\na.\ndayadukungsumber\nd\nayaalamdanlingkungan;\nb.\nrencanatataruangwilayah;\nc.\nperkembanganilmupengetahuandanteknologi\n;\nd.\ntingkatpertumbuhanekonomi;\ne.\njumlahpetani\n;\nf.\nkebutuhanprasaranadansarana;dan\ng.\nkelayakanteknisdanekonomissertakeses\nuaiandengan\nkelembagaandanbudayasetempat.\n(3)\nperencanaansebagaimanadimaksudpadaayat(1)merupakanbagian\nyangintegraldari:\na.\nrencanapembangunannasional;\nb.\nrencanapembangunandaerah;\nc.\nrencanapembangunanpertanian;\nd.\nrencanaanggaranpendapatandanbelan\nja\nnegara\n;dan\ne.\nrencanaanggaran\npendapatandan\nbelanjadaerah\n.\n2013,\nno.\n131\n6\npasal6\nperencanaansebagaimanadimaksuddalampasal5palingsedikitmemuat\nstrategidankebijakan.\npasal7\n(1)\nstrategi\nperlindungan\ndan\npemberdayaan\npetani\nsebagaimana\ndimaksuddalampasal6dit\netapkanolehpemerintahdan\npemerintah\ndaerah\nsesuai\ndengan\nkewenangannya\nberdasarkan\npada\nkebijakan\nperlindungan\ndanpemberdayaanpetani.\n(2)\nstrategi\nperlindunganpetanidilakukanmelalui\n:\na.\nprasaranadansaranaproduksipertanian;\nb.\nkepastianusaha;\nc.\nhargakomod\nitaspertanian;\nd.\npenghapusanpraktikekonomibiayatinggi;\ne.\ngantirugigagalpanenakibatkejadianluarbiasa;\nf.\nsistemperingatandinidanpenanganandampakperubahaniklim;\ndan\ng.\na\nsuransipertanian\n.\n(3)\nstrategi\npemberdayaan\npetanidilakukanmelalui:\na.\npendidikanda\nnpelatihan;\nb.\npenyuluhandanpendampingan;\nc.\npengembangansistemdansaranapemasaranhasilpertanian\n;\nd.\nkonsolidasidanjaminanluasanlahanpertanian\n;\ne.\npenyediaanfasilitaspembiayaandanpermodalan;\nf.\nkemudahan\naksesilmupengetahuan,teknologi\n,\ndaninformasi;\ndan\ng.\npenguatankelembagaanpetani.\npasal8\n(1)\nkebijakanperlindungandanpemberdayaanpetanisebagaimana\ndimaksuddalampasal6ditetapkanolehpemerintahdanpemerintah\ndaerahsesuai\ndengan\nkewenangannya\ndengan\nmemperhatikanasas\ndantujuanperlindungandanp\nemberdayaanpetani.\n(2)\ndalammenetapkankebijakanperlindungandanpemberdayaanpetani\nsebagaimanadimaksudpadaayat(1),pemerintahdanpemerintah\ndaerahmempertimbangkan\n:\na.\nkeselarasandengan\nprogram\np\nemberdayaanmasyarakat\n;\ndan\n2013,\nno.\n131\n7\nb.\nperansertamasyarakat\ndan/at\naupemangkukepentinganlainnya\nsebagaimitrapemerintahdanpemerintahdaerah.\npasal9\n(1)\nperencanaan\nperlindungan\ndanpemberdayaanpetanidisusunoleh\np\nemerintahdan\npemerintahdaerah\nsesuai\ndengan\nkewenangannya\ndengan\nmelibatkan\npetani.\n(2)\nperencanaanperlind\nungandanpemberdayaanpetanisebagaimana\ndimaksudpadaayat(1)disusunditingkatnasional,provinsi,dan\nkabupaten/kota.\n(3)\nperencanaanperlindungandanpemberdayaanpetaniditetapkanoleh\npemerintah,pemerintahprovinsi,danpemerintahkabupaten/kota\nmenj\nadirencanaperlindungandanpemberdayaanpetanibaikjangka\npendek,jangkamenengah,maupunjangkapanjang.\npasal10\nrencanaperlindungandanpemberdayaanpetanisebagaimanadimaksud\ndalampasal9ayat(3)terdiriatas:\na.\nrencanaperlindungandanpemberdaya\nanpetaninasional;\nb.\nrencana\nperlindungandanpemberdayaanpetaniprovinsi;dan\nc.\nrencana\nperlindungan\ndanpemberdayaanpetanikabupaten/kota\n.\npasal11\n(1)\nrencanaperlindungandanpemberdayaanpetaninasionalmenjadi\npedomanuntukmenyusunperencanaanperlindung\nandan\npemberdayaanpetani\ntingkat\nprovinsi.\n(2)\nrencanaperlindungandanpemberdayaanpetaniprovinsimenjadi\npedomanuntukmenyusunperencanaanperlindungandan\npemberdayaanpetani\ntingkat\nkabupaten/kota.\n(3)\nrencana\nperlindungan\ndanpemberdayaanpetani\nnasional\n,provinsi,\ndan\nkabupaten/kotamenjadipedomanuntuk\nmerencanakandan\nmelaksanakankegiatan\nperlindungandanpemberdayaanpetani.\nbabiv\nperlindunganpetani\nbagiankesatu\numum\npasal12\n(1)\nperlindungan\npetanidilakukanmelaluistrategisebagaimana\ndimaksuddal\nampasal\n7\nayat(2).\n2013,\nno.\n131\n8\n(2)\nperlindunganpetanisebagaimanadimaksuddalampasal7ayat(2)\nhuruf\na,hurufb,hurufc,huruf\ne,danhuruf\ng\ndiberikankepada:\na.\npetanipenggaraptanamanpanganyangtidakmemilikilahan\nusahatanidanmenggarappalingluas2(dua)he\nktare;\nb.\npetaniyangmemilikilahandanmelakukanusahabudidaya\ntanamanpanganpadalahanpalingluas2(dua)hektare;\ndan/atau\nc.\npetanihortikultura,pekebun,ataupeternakskalausahakecil\nsesuaidenganketentuanperaturanperundang\n-\nundangan.\n(3)\nperlindungan\npetani\nsebagaimanadimaksuddalampasal\n7\nayat(2)\nhurufd\ndanhuruf\nfdiberikan\nkepada\npetani.\npasal13\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nbertanggungjawabatasperlindunganpetani.\npasal14\n(1)\npemerintahdanpemerintahdaerahmel\nakukankoordinasidalam\nperencanaan,pelaksanaan,danpengawasan\nperlindungan\npetani.\n(2)\nkoordinasisebagaimanadimaksudpadaayat(1)\ndilakukan\nuntuk\nmelaksanakanstrategi\nperlindungan\npetanisebagaimanadimaksud\ndalampasal\n7\nayat(2).\npasal15\n(1)\npemerintahb\nerkewajibanmengutamakanproduksipertaniandalam\nnegeriuntukmemenuhikebutuhanpangannasional.\n(2)\nkewajibanmengutamakanproduksi\npertaniandalamnegeri\nsebagaimanadimaksudpadaayat(1)dilakukanmelaluipengaturan\nimporkomoditaspertaniansesuaidenga\nnmusimpanendan/atau\nkebutuhankonsumsidalamnegeri.\n(3)\ndalamhalimpor\nkomoditaspertanian\n,menteriterkaitharus\nmelakukankoordinasidenganmenteri.\nbagiankedua\nprasaranapertaniandansaranaproduksipertanian\nparagraf1\nprasaranapertanian\npasal16\n(1)\np\nemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nbertanggungjawabmenyediakandan/ataumengelolaprasarana\npertaniansebagaimanadimaksuddalampasal7ayat(2)hurufa.\n2013,\nno.\n131\n9\n(2)\np\nrasarana\npertanian\nsebagaimanadimaksudpadaayat(1)antaralain\nmeliput\ni:\na.\njalanusahatani,jalanproduksi,danjalandesa;\nb.\nbendungan,dam,jaringanirigasi,danembung;dan\nc.\njaringanlistrik,pergudangan,pelabuhan,danpasar.\npasal17\nselainpemerintahdanpemerintahdaerah,pelakuusahadapat\nmenyediakan\ndan/ataumengelola\nprasaranapertaniansebagaimana\ndimaksuddalampasal16\nyangdibutuhkanpetani.\npasal18\npetaniberkewajibanmemeliharaprasaranapertanianyangtelah\nada\nsebagaimanadimaksuddalampasal16\ndanpasal17\n.\nparagraf2\nsaranaproduksipertanian\npasal19\n(1)\npemer\nintah\ndanpemerintahdaerah\nsesuaidengankewenangannya\nbertanggungjawabmenyediakansaranaproduksipertanian\nsebagaimanadimaksuddalampasal\n7\nayat(2)hurufa\nsecara\ntepat\nwaktu\ndan\ntepatmutuserta\nharga\nterjangkaubagipetani.\n(2)\nsaranaproduksipertan\niansebagaimanadimaksudpadaayat(1)\npalingsedikit\nmeliputi:\na.\nbenih,\nbibit,bakalanternak,\npupuk,pestisida\n,pakan,danobat\nhewansesuaidenganstandarmutu\n;\ndan\nb.\nalat\ndanmesinpertaniansesuaistandarmutudankondisi\nspesifiklokasi\n.\n(3)\npenyediaan\nsaran\naproduksipertaniandiutamakanberasaldari\nproduksidalamnegeri.\n(4)\npemerintah\ndanpemerintahdaerah\nsesuai\ndengan\nkewenangannya\nmembinapetani\n,\nkelompoktani\n,\ndangabungankelompoktanidalam\nmenghasilkan\nsarana\nproduksipertanianyang\nberkualitas.\npasal\n20\nselainpemerintahdanpemerintahdaerah,pelakuusahadapat\nmenyediakan\nsarana\nproduksi\npertaniansebagaimanadimaksuddalam\npasal1\n9\nyangdibutuhkanpetani.\n2013,\nno.\n131\n10\npasal21\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\ndapatmemberikansubsidi\nbenihataubibittanaman,bibitatau\nbakalanternak,pupuk,dan/ataualatdanmesinpertaniansesuai\ndengankebutuhan.\n(2)\npemberiansubsidisebagaimanadimaksudpadaayat(1)harustepat\nguna,tepatsasaran,tepatwaktu,tepatlokasi,\ntepatjenis,tepatmutu,\ndantepatjumlah.\nbagianketiga\nkepastianusaha\npasal22\nuntukmenjaminkepastian\nu\nsahasebagaimanadimaksuddalampasal7\nayat(2)hurufb,pemerintahdanpemerintahdaerah\nsesuaidengan\nkewenangannya\nberkewajiban:\na.\nmenetapkankawasanusahataniberdasark\nankondisidanpotensi\nsumberdayaalam,sumberdayamanusia,dansumberdayabuatan;\nb.\nmemberikan\njaminanpemasaranhasilpertaniankepadapetaniyang\nmelaksanakanusahatanisebagaiprogrampemerintah;\nc.\nmemberikankeringananpajakbumidanbangunanbagilah\nan\npertanianproduktifyangdiusahakansecaraberkelanjutan;dan\nd.\nmewujudkan\nfasilitaspendukungpasarhasilpertanian.\npasal23\n(1)\njaminanpemasaransebagaimanadimaksuddalampasal22hurufb\nmerupakanhakpetaniuntukmendapatkanpenghasilanyang\nmenguntung\nkan\n.\n(2)\njaminan\npemasaran\nsebagaimanadimaksudpadaayat(1)dapat\ndilakukanmelalui:\na.\npembeliansecaralangsung;\nb.\npenampunganhasilusahatani;dan/atau\nc.\npemberianfasilitasaksespasar.\npasal24\nketentuanlebihlanjutmengenai\nkepastianusaha\nsebagaimanadimak\nsud\ndalampasal22dan\npasal2\n3\ndiaturolehpemerintah.\n2013,\nno.\n131\n11\nbagiankeempat\nhargakomoditaspertanian\nparagraf1\numum\npasal25\n(1)\npemerintahberkewajibanmenciptakankondisiyangmenghasilkan\nhargakomoditaspertanianyangmenguntungkanbagipetani\nsebagaimanadim\naksuddalampasal\n7\nayat(2)hurufc.\n(2)\nkewajibanpemerintahmenciptakankondisisebagaimanadimaksud\npadaayat(1)dapatdilakukandenganmenetapkan:\na.\ntarifbeamasuk\nkomoditaspertanian;\nb.\ntempat\npemasukankomoditaspertaniandariluarnegeri\ndalam\nkawasanpa\nbean\n;\nc.\npersyaratan\nadministratif\ndanstandarmutu\n;\nd.\nstrukturpasarprodukpertanianyang\nberimbang;\ndan\ne.\nkebijakan\nstabilisasi\nhargapangan.\nparagraf2\ntarifbeamasukkomoditaspertanian\npasal26\n(1)\npemerintahmenetapkanjeniskomoditaspertanianyangdikenakan\ntarifbeamasuksebagaimanadimaksuddalampasal25ayat(2)huruf\na.\n(2)\nbesarantarifbeamasuksebagaimanadimaksudpadaayat\n(1)\nditetapkanolehpemerintahberdasarkanketentuanperaturan\nperundang\n-\nundangan.\n(3)\npenetapanbesarantarifbeamasuksebagaimanad\nimaksudpadaayat\n(2)\npalingsedikit\ndidasarkanpada:\na.\nhargapasarinternasional;\nb.\nhargapasardomestik;\nc.\njeniskomoditaspertaniantertentunasionaldanlokal;dan\nd.\nproduksidankebutuhannasional.\npasal27\n(1)\npemerintahmenetapkanjeniskomoditaspertaniantert\nentu\nnasional\ndanlokal\nsebagaimanadimaksuddalampasal26ayat(3)\nhuruf\nc.\n2013,\nno.\n131\n12\n(2)\npenetapanjeniskomoditaspertanian\ntertentusebagaimanadimaksud\npadaayat(1),dilakukanberdasarkan:\na.\npengaruhkomoditaspertanianterhadap\nstabilitasekonomi\nnasional\n;dan\n/ata\nu\nb.\nk\nepentinganhajathiduporangbanyak.\n(3)\nketentuanmengenaipenetapanjeniskomoditaspertaniantertentu\nsebagaimanadimaksudpadaayat(1)\ndanayat(2)\ndiatur\noleh\npemerintah.\nparagraf3\ntempatpemasukankomoditaspertanian\npasal28\npenetapan\ntempat\npemasu\nkan\nkomoditaspertanian\ndalamkawasan\npabean\nsebagaimanadimaksuddalampasal25ayat(2)hurufbharus\nmempertimbangkan\n:\na.\ndaerahsentraproduksikomoditaspertaniandalamnegeri;dan\nb.\nkelengkapaninstalasikarantinasesuaidenganketentuanperaturan\nperunda\nng\n-\nundangan.\npasal29\nsetiaporang\nyangmeng\nimpor\nkomoditaspertanianwajib\nmelalui\ntempat\npemasukanyang\ntelah\nditetapkanoleh\npemerintah\n.\npasal30\n(1)\nsetiaporangdilarang\nmengimpor\nkomoditaspertanianpadasaat\nketersediaankomoditaspertaniandalamnegeri\nsudahmen\ncukup\ni\nkebutuhankonsumsidan/ataucadanganpanganpemerintah.\n(2)\nkecukupan\nkebutuhan\nkonsumsi\ndancadanganpangan\np\nemerintah\nsebagaimanadimaksudpadaayat(1)ditetapkanolehmenteri.\nparagraf4\npersyaratanadministra\ntif\ndanstandarmutu\npasal\n31\n(1)\nsetiapkomoditaspertanianyangdiimporharusmemenuhi\npersyaratanadministratifdanstandarmutusebagaimanadimaksud\ndalampasal25ayat(2)hurufc.\n(2)\npersyaratanadministratifsebagaimanadimaksudpadaayat(1)paling\nsedikitmeliputi:\na.\nsuratizinimpor;\n2013,\nno.\n131\n13\nb.\ntanggalpanendantanggalk\ne\ndaluwarsa;dan\nc.\ns\nuratketerangan\nasalnegarapenghasildannegarapengekspor\n.\n(3)\nsetiaporangyangmengimporkomoditaspertaniansebagaimana\ndimaksudpadaayat(2)harusmemenuhipersyaratanadministratif.\n(4)\nselainpersyaratanadminis\ntratifdanstandarmutusebagaimana\ndimaksudpadaayat(1)danayat(2),komoditaspanganharus\nmemenuhikeamananpangan.\n(5)\nketentuanmengenai\npersyaratanadministratif,standarmutu,dan\nkeamananpangansebagaimanadimaksudpadaayat(2),ayat(3),dan\nayat\n(4)diaturdenganatauberdasarkanperaturanpemerintah.\nbagiankelima\npenghapusan\npraktikekonomibiayatinggi\npasal\n3\n2\np\nenghapusan\npraktikekonomibiayatinggi\nsebagaimanadimaksuddalam\npasal7ayat(2)huruf\nd\ndilakukandengan\nmenghapuskanberbagai\npu\nngutanyangtidaksesuaidengan\nketentuan\nperaturanperundang\n-\nundangan.\nbagiankeenam\ngantirugigagalpanen\nakibat\nkejadianluarbiasa\npasal33\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\ndapat\nmemberikanbantuangantirugigagalpanenak\nibat\nkejadian\nluarbiasa\nsebagaimanadimaksuddalampasal\n7\nayat(2)hurufe\nsesuaidengankemampuankeuangannegara.\n(2)\nuntukmenghitungbantuangantirugigagalpanenakibat\nkejadian\nluarbiasa\nsebagaimanadimaksudpadaayat(1)\np\nemerintah\ndan\npemerintahda\nerahsesuaidengankewenangannya\nberkewajiban:\na.\nmenentukan\njenis\ntanamandan\nmenghitungluastanamyang\nrusak;\nb.\nmenentukanjenisdanmenghitung\nternakyang\nmati\n;\ndan\nc.\nmenetapkan\nbesaran\ngantirugitanaman\ndan/atau\nternak\n.\nbagianke\ntujuh\nsistemperingatandini\ndan\ndampakperubahaniklim\npasal\n34\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nmembangunsistemperingatandinidanpenanganandampakperubahan\n2013,\nno.\n131\n14\niklimsebagaimanadimaksuddalampasal\n7\nayat(2)huruff\nu\nntuk\nmengantisipasigagalpanenak\nibatbencanaalam\n.\npasal\n35\n(1)\npemerintahwajibmelakukanprakiraaniklimuntukmengantisipasi\nterjadinyagagalpanen.\n(2)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nwajibmengantisipasiterjadinyagagalpanendenganmelakukan\n:\na.\nperamalanseranga\nnorganismepengganggutumbuhan,serangan\nhama,dan/atauwabahpenyakithewanmenular;dan\nb.\nupayapenangananterhadaphasilprakiraaniklimdanperamalan\nseranganorganismepengganggutumbuhan,seranganhama,\ndan/atauwabahpenyakithewanmenular.\npasal36\nketentuanlebihlanjutmengenaisistemperingatandinidanpenanganan\ndampakperubahaniklimdiaturdenganperaturanmenteri.\nbagiankedelapan\nasuransipertanian\npasal\n37\n(1)\np\nemerintahdanpemerintahdaerahsesuaidengankewenangannya\nberkewajibanmelindungi\nusahataniyangdilakukanolehpetani\nsebagaimanadimaksud\ndalampasal\n12\nayat(2)\ndalambentuk\na\nsuransi\npertanian\n.\n(2)\nasuransi\npertaniansebagaimanadimaksudpadaayat(1)dilakukan\nuntukmelindungipetanidarikerugiangagalpanen\nakibat:\na.\nbencanaalam;\nb.\nsera\nnganorganismepengganggutumbuhan;\nc.\nwabahpenyakithewanmenular;\nd.\ndampakperubahaniklim;dan/atau\ne.\njenisrisiko\n-\nrisikolain\ndiatur\ndenganperaturanmenteri.\npasal38\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nmenugaskan\nbadanusahamilik\nnegaradan/ataubadanusahamilik\ndaerahdibidangasuransi\nuntukmelaksanakan\nasuransipertanian\n.\n(2)\npelaksanaanasuransipertaniansebagaimanadimaksudpadaayat(1)\ndilaksanakan\nsesuaidenganketentuanperaturanperundang\n-\nundangan.\n2013,\nno.\n131\n15\npasal39\n(1)\npemerintahdan\npemerintahdaerah\nsesuaidengankewenangannya\nmemfasilitasis\netiappetanimenjadipesertaasuransipertanian.\n(2)\nfasilitasisebagaimanadimaksudpadaayat(1)meliputi:\na.\nkemudahanpendaftaranuntukmenjadipeserta;\nb.\nkemudahanaksesterhadapperusahaanasuransi;\nc.\nsosialisasiprogramasuransiterhadappetanidanperusahaan\nasuransi;dan/atau\nd.\nbantuanpembayaranpremi.\n(3)\npelaksanaanfasilitasiasuransipertaniansebagaimanadimaksud\npadaayat(2)diaturdenganperaturanmenteri.\nbabv\npemberdayaanpetani\nbagiankesatu\numum\npasal40\npemberdayaanpetanidilakukanuntukmemajukandanmengembangkan\npolapikir\ndanpolakerja\npetani,meningkatkanusahatani\n,serta\nmenumbuhkandanmenguatkankelembagaanpetaniagarmampu\nmandiridanberdayasaingtinggi.\npasal41\n(1)\npemerintahda\nnpemerintahdaerahmelakukankoordinasidalam\nperencanaan,pelaksanaan,danpengawasanpemberdayaanpetani\n(2)\nkoordinasisebagaimanadimaksudpadaayat(1)\ndilakukan\nuntuk\nmelaksanakanstrategipemberdayaanpetanisebagaimanadimaksud\ndalampasal\n7\nayat(3).\nbagiankedua\npendidikandan\np\nelatihan\npasal42\n(1)\npemerintahdanpemerintahdaerahsesuaidengankewenangannya\nberkewajiban\nmenyelenggarakan\npendidikandanpelatihankepada\npetani.\n(2)\npendidikandanpelatihansebagaimanadimaksudpadaayat(1)antara\nlainberup\na:\n2013,\nno.\n131\n16\na.\npengembanganprogrampelatihandanpemagangan;\nb.\npemberianbeasiswabagipetaniuntukmendapatkanpendidikan\ndibidangpertanian;atau\nc.\npengembanganpelatihankewirausahaandibidang\nagr\ni\nbisnis.\n(3)\npetanisebagaimanadimaksuddalampasal12ayat(2)yangsuda\nh\nmendapatkanpendidikandanpelatihansertamemenuhikriteria\nberhakmemperolehbantuanmodaldaripemerintahdan/atau\npemerintahdaerah.\n(4)\npersyaratanpetaniyangberhakmemperolehbantuanmodaldari\npemerintahdan/ataupemerintahdaerahdiaturdenganpera\nturan\nmenteri.\npasal43\n(1)\npemerintahdanpemerintahdaerahsesuaidengankewenangannya\nberkewajibanmeningkatkankeahliandanketerampilanpetanimelalui\npendidikandanpelatihansecaraberkelanjutan.\n(2)\nselainpemerintahdanpemerintahdaerah,badandan/ataul\nembaga\nyangterakreditasidapatmelaksanakanpendidikandanpelatihan\nsebagaimanadimaksudpadaayat(1)sesuaidenganketentuan\nperaturanperundang\n-\nundangan.\n(3)\nuntukmeningkatkankeahliandanketerampilanpetanisebagaimana\ndimaksudpadaayat(1)dapatdila\nkukanmelaluisertifikasi\nkompetensi.\n(4)\npemerintahdanpemerintahdaerahsesuaidengankewenangannya\nberkewajibanmemfasilitasipetanisebagaimanadimaksuddalam\npasal12ayat(2)untukmemperolehsertifikatkompetensi.\n(5)\nketentuanlebihlanjutmengenaipenyel\nenggaraanpendidikandan\npelatihan,sertasertifikasikompetensidiaturdenganperaturan\nmenteri.\npasal44\npetaniyangtelahditingkatkan\nkompetensinya\nmelaluipendidikandan\npelatihansebagaimanadimaksuddalampasal\n43\nwajibmenerapkan\ntata\ncarabudi\nday\na,\npascapanen,pengolahan\n,\ndan\npemasaranyangbaik\nuntuk\nmeningkatkankualitasdandayasaingsecaraberkelanjutansesuai\ndenganperaturanmenteri.\npasal45\npelakuusahadalampemberdayaanpetanidapatmenyelenggarakan\n:\na.\npendidikanformaldannonformal;\ndan\nb.\npelatihandanpemagangan\n.\n2013,\nno.\n131\n17\nbagianketiga\npenyuluhan\ndanpendampingan\npasal46\n(1)\npemerintahdanpemerintahdaerahsesuaidengankewenangannya\nmember\ni\nfasilitaspenyuluhandanpendampingankepadapetani.\n(2)\npemberianfasilitaspenyuluhansebagaimanadimaksu\ndpadaayat(1),\nberupapembentukanlembagapenyuluhandanpenyediaanpenyuluh.\n(3)\nlembagapenyuluhansebagaimanadimaksudpadaayat(2)dibentuk\nolehpemerintahdanpemerintahdaerah.\n(4)\npenyediaanpenyuluhsebagaimanadimaksudpadaayat(2)paling\nsedikit1(s\natu)orangpenyuluhdalam1(satu)desa.\n(5)\npendampingansebagaimanadimaksudpadaayat(1)dapatdilakukan\nolehpenyuluh.\n(6)\npenyuluhandanpendampingandilakukanantaralainagarpetani\ndapatmelakukan:\na.\ntatacara\nbudidaya,\npascapanen,\npengolahan,danpemasara\nn\nyangbaik;\nb.\nanalisiskelayakanusaha;dan\nc.\nkemitraandengan\npelakuusaha\n.\n(7)\np\nenyuluhandanpendampingandilaksanakansesuaidengan\nketentuanperaturanperundang\n-\nundangan.\npasal\n47\nsetiaporang\ndilarangmelakukanpenyuluhanyang\ntidaksesuaidengan\nmateripen\nyuluhandalambentukteknologitertentu\nyangtelahditetapkan\noleh\npemerintahataupemerintahdaerah\n,\nkecualiyangbersumberdari\npengetahuantradisional.\nbagiankeempat\nsistemdansaranapemasaranhasilpertanian\npasal\n48\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nmelakukanpemberdayaanpetanimelalui\npengembangansistemdan\nsarana\npemasaranhasilpertanian\n.\n(2)\npengembangan\nsistemdansaranapemasaranhasilpertanian\nsebagaimanadimaksudpadaayat(1)\ndiselenggarakandengan:\n2013,\nno.\n131\n18\na.\nmewujudkanpasar\nhasilpertanianyangmemenuhistandar\nkeamananpangan,sanitasi,sertamemperhatikanketertiban\numum\n;\nb.\nmewujudkanterminalagribisnisdansubterminalagribisnis\nuntukpemasaranhasilpertanian\n;\nc.\nmewujudkanfasilitaspendukungpasarhasilpertanian;\nd.\nmemfasil\nitasipengembanganpasarhasilpertanianyangdimiliki\ndan/ataudikelolaoleh\nkelompoktani,gabungankelompoktani,\nkoperasi,\ndan/atau\nkelembagaanekonomipetanilainnyad\ni\ndaerahproduksikomoditaspertanian;\ne.\nme\nmbatasi\npasarmodernyangbukandimilikida\nn\n/atau\ntidak\nbekerjasamadengankelompoktani,gabungankelompoktani,\nkoperasi,\ndan/atau\nkelembagaanekonomipetanilainnya\ndi\ndaerahproduksikomoditaspertanian;\nf.\nmengembangkan\npola\nkemitraan\nusaha\ntani\nyang\nsaling\nmemerlukan,mempercayai,memperkuat,d\nan\nmenguntungkan;\ng.\nmengembangkansistempemasarandanpromosihasilpertanian;\nh.\nmengembangkan\npasarlelang;\ni.\nmenyediakaninformasipasar;dan\nj.\nmengembangkanlindungnilai.\n(3)\nketentuan\nlebih\nlanjut\nmengenai\npembatasan\npasar\nmodern\nsebagaimana\ndimaksud\npadaayat(\n2)hurufe\ndiaturoleh\npemerintah\n.\npasal\n49\npetanidapatmelakukankemitraanusahadengan\npelaku\nusaha\ndalam\nmemasarkanhasilpertanian\nsesuaidenganketentuanperaturan\nperundang\n-\nundangan.\npasal50\nsetiaporangyangmengelolapasarmodern\nberke\nwajib\nan\nmen\ngutamakan\npenjualankomoditaspertaniandalamnegeri\n.\npasal5\n1\n(1)\ntransaksijualbelikomoditaspertaniandipasarinduk,terminal\nagribisnis,dansubterminalagribisnisdapatdilakukanmelalui\nmekanismepelelangan.\n(2)\ndalammekanismepelelangansebagaimanadima\nksudpadaayat(1),\npenyelenggarapelelangan\nharus\nmenetapkanhargaawalyang\nmenguntungkanpetani.\n2013,\nno.\n131\n19\n(3)\nketentuanmengenaipenyelenggara,mekanisme,danpenetapanharga\nawalpelelangankomoditaspertaniandiaturdenganatauberdasarkan\nperaturanpemerintah.\npa\nsal52\n(1)\nkomoditaspertanianyangdipasarkanharusmemenuhistandar\nmutu.\n(2)\npemerintahmenetapkanstandarmutuuntuksetiapjeniskomoditas\npertanian.\npasal\n53\n(1)\nsetiappetani\nyangmemproduksikomoditas\npertanian\nwajib\nmemenuhistandar\nmutu\nsebagaimanadimaksud\ndalampasal5\n2\nayat\n(\n1\n).\n(2)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nmembina\npetani\nsebagaimanadimaksuddalampasal12ayat(2)\nuntukmemenuhistandarmutu\nkomoditaspertanian.\npasal\n54\npemerintahdanpemerintahdaerah\nsesuaidengankewen\nangannya\nmenyelenggarakanpromosidansosialisasipentingnyamengonsumsi\nk\nomoditaspertaniandalamnegeri.\nbagiankelima\nkonsolidasidanjaminanluasan\nlahan\npertanian\nparagraf1\numum\npasal55\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nbe\nrkewajibanmemberikanjaminanketersediaanlahanpertanian\n.\n(2)\njaminan\nsebagaimana\ndimaksudpadaayat(1)dilakukanmelalui:\na.\nkonsolidasilahanpertanian;dan\nb.\njaminanluasanlahanpertanian.\nparagraf2\nkonsolidasilahanpertanian\npasal\n56\n(1)\nkonsolidasilahan\nper\ntanian\nsebagaimanadimaksuddalampasal5\n5\nayat(2)hurufamerupakanpenataankembalipenggunaandan\npemanfaatan\nlahan\nsesuaidengan\npotensidan\nrencanatataruang\nwilayahuntukkepentinganlahan\npertanian\n.\n2013,\nno.\n131\n20\n(2)\nkonsolidasilahanpertaniandiutamakanuntukmen\njaminluasan\nlahanpertanian\nbagi\npetanisebagaimanadimaksuddalampasal12\nayat(2)agarmencapaitingkatkehidupanyanglayak.\n(3)\nkonsolidasisebagaimanadimaksudpadaayat(1)dilakukan\nmelalui\n:\na.\npengendalianalihfungsilahan\npertanian\n;\ndan\nb.\npemanfaatanla\nhan\npertanian\nyangterlantar.\npasal57\n(1)\nselainkonsolidasilahan\npertanian\nsebagaimanadimaksuddalam\npasal5\n6\n,pemerintahdanpemerintahdaerah\nsesuaidengan\nkewenangannya\ndapatmelakukanperluasanlahanpertanianmelalui\npenetapanlahanterlantaryangpot\nensialsebagailahanpertanian.\n(2)\np\nerluasanlahanpertaniansebagaimanadimaksudpadaayat(1)\ndilakukansesuaidenganketentuanperaturanperundang\n-\nundangan.\nparagraf3\njaminanluasanlahanpertanian\npasal\n58\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengank\newenangannya\nberkewajibanmemberikanjaminanluasanlahan\npertanian\nsebagaimanadimaksuddalampasal5\n5\nayat(2)hurufbbagipetani\nsebagaimanadimaksuddalampasal12ayat(2).\n(2)\njaminansebagaimanadimaksudpadaayat(1)dilakukandengan\nmemberikankemuda\nhanuntukmemperolehtanahnegara\nbebas\nyang\ndiperuntukanatauditetapkan\nsebagai\nkawasanpertanian\n.\n(3)\nkemudahan\nsebagaimanadimaksudpadaayat(\n2\n)berupa:\na.\npemberian\npalingluas2hektaretanah\nnegarabebasyangtelah\ndi\ntetapkansebagai\nkawasanpertanian\nkep\nadapetani,yangtelah\nmelakukanusahatani\npalingsedikit5(lima)tahunberturut\n-\nturut.\nb.\npemberian\nlahan\npertanian\nsebagaimana\ndimaksuddalam\npasal\n57\nayat\n(1).\n(4)\nselainkemudahansebagaimanadimaksudpadaayat(3),pemerintah\ndanpemerintahdaerah\nsesuaid\nengankewenangannya\nmemfasilitasi\npinjamanmodalbagipetanisebagaimanadimaksuddalampasal12\nayat(2)untukmemiliki\ndan/ataumemperluaskepemilikan\nlahan\npertanian\n.\n2013,\nno.\n131\n21\npasal\n59\nkemudahanbagipetaniuntukmemperolehlahanpertaniansebagaimana\ndimaksudd\nalampasal\n58\nayat(3)hurufadiberikan\ndalambentuk\nhak\nsewa,izinpengusahaan,izinpengelolaan,atauizinpemanfaatan.\npasal\n60\npemberianlahanpertaniansebagaimanadimaksuddalampasal\n58\nayat(3)\nhurufbdiutamakankepadapetanisetempatyang:\na.\ntidak\nmemilikilahandantelahmengusahakanlahanpertaniandi\nlahanyangdiperuntukkansebagaikawasanpertanianselama5(lima)\ntahunberturut\n-\nturut;atau\nb.\nmemilikilahan\npertanian\nkurangdari2(dua)hektare.\npasal61\npetaniyangmenerimakemudahanuntukmemp\nerolehtanahnegarayang\ndiperuntukanatauditetapkan\nsebagai\nkawasanpertaniansebagaimana\ndimaksuddalampasal\n58\nayat(3)wajibmengusahakanlahanpertanian\nyangdiberikan\ndenganmemanfaatkansumberdayaalamsecaralestari\ndanberkelanjutan.\npasal\n62\np\netani\nsebagaimana\ndimaksuddalampasal\n61\ndapat\nmemperoleh\nkeringananpajakbumidanbangunandan\ninsentiflainnyasesuaidengan\nketentuanperaturanperundang\n-\nundangan\n.\npasal\n63\n(1)\npetanidilarangmengalihfungs\nikan\nlahanpertanianyangdiperoleh\nsebagaimanad\nimaksuddalampasal58ayat(3)menjadilahannon\n-\npertanian.\n(2)\npetanidilarangmengalihkanlahanpertanian\nsebagaimanadimaksud\ndalampasal58ayat(3)kepadapihaklainsecarakeseluruhanatau\nsebagian,kecualimendapatizindaripemerintahataupemerintah\ndaerah.\n(3)\npetaniyangmengalihkanlahanpertaniankepadapihaklainsecara\nkeseluruhanatau\nsebagiantanpamendapatizin\nsebagaimana\ndimaksud\npadaayat(2)dikenaisanksiadministratifberupa\npencabutanhakatauizin.\npasal64\n(1)\npemerintahdanpemerintahdaera\nh\nsesuaidengankewenangannya\nmembinapetaniyanglahannyasudahdi\nmiliki\nolehpetanilain\nsebagaimanadimaksuddalampasal\n58\nayat(\n4\n)\nuntukalihprofesi.\n2013,\nno.\n131\n22\n(2)\npembinaan\nbagipetani\nsebagaimanadimaksudpadaayat(1)\ndilakukandenganmemberikanpelatihankewir\nausahaandanbantuan\nmodal.\npasal\n65\nketentuanlebihlanjutmengenaijaminanluasanlahanpertaniandiatur\ndenganperaturanpemerintah.\nbagiankeenam\nfasilitaspembiayaan\ndanpermodalan\npasal\n66\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nberkewajibanmemfasilitasipembiayaandanpermodalanusahatani.\n(2)\npemberianfasilitaspembiayaandanpermodalan\nsebagaimana\ndimaksudpadaayat(1)\ndilakukandengan:\na.\npinjamanmodaluntukmemiliki\ndan/ataumemperluas\nkepemilikan\nlahanpertanian;\nb.\npemberianban\ntuanpenguatanmodalbagipetanisebagaimana\ndimaksuddalampasal12ayat(2);\nc.\npemberiansubsidibungakredit\nprogramdan/atauimbaljasa\npenjaminan\n;\ndan/atau\nd.\np\nemanfaatan\ndana\ntanggungjawabsosial\nsertadanaprogram\nkemitraan\ndan\nbinalingkungan\ndaribada\nnusaha\n.\nbagianketujuh\na\nkses\ni\nlmu\np\nengetahuan,\nt\neknologi\n,\ndan\ni\nnformasi\npasal67\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nberkewajibanmemberikankemudahanaksesilmupengetahuan,\nteknologi,daninformasi\nuntukmencapaistandarmutuko\nmoditas\npertanian\n.\n(2)\nkemudahanaksessebagaimanadimaksudpadaayat(1)meliputi:\na.\npenyebarluasanilmupengetahuandanteknologi;\nb.\nkerjasamaalihteknologi;dan\nc.\npenyediaanfasilitasbagipetaniuntukmengaksesilmu\npengetahuan,teknologi,daninformasi.\n2013,\nno.\n131\n23\npasal\n68\n(1)\npenyediaaninformasisebagaimanadimaksuddalampasal6\n7\nayat(2)\nhurufcpalingsedikitberupa:\na.\nsaranaproduksipertanian;\nb.\nharga\nkomoditaspertanian;\nc.\npeluangdantantanganpasar;\nd.\nprakiraaniklim,danledakanorganismepengganggutumbuhan\ndan/atauwaba\nh\np\nenyakithewanmenular;\ne.\npendidikan,pelatihan,danpenyuluhan;\nf.\npemberiansubsididanbantuanmodal;dan\ng.\nketersediaanlahanpertanian.\n(2)\ninformasisebagaimanadimaksudpadaayat(1)harusakurat\n,tepat\nwaktu,dan\ndapatdiaksesdenganmudahdancepatolehpe\ntani,\npelakuusaha\n,dan/ataumasyarakat.\nbagianke\ndelapan\npenguatankelembagaan\nparagraf1\numum\npasal\n69\n(1)\npemerintahdanpemerintahdaerah\nsesuaidengankewenangannya\nberkewajibanmendorongdanmemfasilitasiterbentuknya\nk\nelembagaan\np\netanidan\nk\nelembagaan\ne\nkonomi\np\netani.\n(2)\npembentukankelembagaansebagaimanadimaksudpadaayat(1)\ndilaksanakandenganperpaduandaribudaya,norma,nilai,dan\nkearifanlokalpetani.\npasal\n70\n(1)\nkelembagaanpetanisebagaimanadimaksud\ndalampasal\n69\nayat(\n1\n)\nterdiriatas:\na.\nkelompokta\nni;\nb.\ngabungankelompoktani\n;\nc.\nasosiasikomoditaspertanian\n;dan\nd.\ndewankomoditaspertaniannasional.\n(2)\nkelembagaan\ne\nkonomipetanisebagaimanadimaksuddalampasal\n69\nayat(\n1\n)berupabadanusahamilikpetani.\n2013,\nno.\n131\n24\npasal\n71\npetani\nberkewajiban\nbergabungdanberperana\nktifdalamkelembagaan\npetanisebagaimanadimaksuddalampasal70ayat(1).\nparagraf2\nkelembagaanpetani\npasal\n72\n(1)\nkelompoktanisebagaimanadimaksuddalampasal\n70\nayat(1)huruf\nadibentukoleh,dari,danuntukpetani\n.\n(2)\npembentukan\nkelompok\ntanimemperhat\nikanlembaga\n-\nlembagaadat\npetaniyangsudahadadanmemperhatikanketerlibatanpetani\nperempuan.\npasal\n73\ngabungankelompoktanisebagaimanadimaksuddalampasal\n70\nayat(1)\nhuruf\nbmerupakangabungandaribeberapakelompoktaniyang\nberkedudukandidesaa\ntaubeberapadesadalamkecamatanyangsama.\npasal\n74\nkelompoktanidangabungankelompoktaniberfungsisebagaiwadah\npembelajaran,kerja\nsama,dantukarmenukarinformasiuntuk\nmenyelesaikanmasalahdalammelakukanusahatanisesuaidengan\nkedudukannya.\npasal\n75\ndalamme\nnyelenggara\nkanfungsinya,kelompoktanidangabungan\nkelompoktanisebagaimanadimaksuddalampasal7\n4\nbertugas:\na.\nmeningkatkankemampuananggotaataukelompokdalam\nmengembangkanusahataniyangberkelanjutandankelembagaan\npetaniyangman\ndiri;\nb.\nmemperjuangkankepentingananggotaataukelompokdalam\nmengembangkankemitraanusaha;\nc.\nmenampungdanmenyalurkanaspirasianggotaataukelompok;dan\nd.\nmembantumenyelesaikanpermasalahananggotaataukelompok\ndalamber\n-\nusahatani.\npasal76\n(1)\nasosiasi\nkomo\nditaspertanian\nsebagaimanadimaksuddalampasal\n70\nayat(1)hurufcmerupakanlembagaindependennirlabayang\ndibentukoleh,dari,danuntuk\np\netani.\n2013,\nno.\n131\n25\n(2)\npetanidalammengembangkanasosiasinyadapatmengikutsertakan\npelakuusaha\n,pakar,dan/atautokohmasyarak\natyangpeduli\nterhadap\nkesejahteraanpetani.\npasal77\nasosiasi\nkomoditaspertanian\ndapatberkedudukandikabupaten/kota\natauprovinsi.\npasal\n78\na\nsosiasi\nkomoditaspertanian\nbertugas:\na.\nmenampungdanmenyalurkanaspirasipetani;\nb.\nmengadvokasidanmengawasipe\nlaksanaankemitraaanusahatani;\nc.\nmemberikanmasukankepadapemerintahdan/ataupemerintah\ndaerahdalamperumusankebijakanperlindungandanpemberdayaan\npetani;\nd.\nmempromosikankomoditaspertanianyangdihasilkananggota,di\ndalamnegeridandiluarnegeri;\ne.\nmendorongpersainganusahataniyangadil;\nf.\nmemfasilitasianggotadalammengaksessaranaproduksidan\nteknologi\n;\ndan\ng.\nmembantumenyelesaikanpermasalahan\ndalamber\n-\nu\nsahatani\n.\npasal\n79\n(1)\ndewankomoditaspertaniannasionalsebagaimanadimaksuddalam\npasal\n70\nay\nat(1)hurufdbersifatnirlabayangmerupakangabungan\ndariberbagaiasosiasikomoditaspertanian.\n(2)\ndewankomoditaspertaniannasionalberfungsisebagaiwadahuntuk\nmenyelesaikanpermasalahandalamber\n-\nusahatani.\n(3)\npetanidalammengembangkandewankomoditas\npertaniannasional\ndapatmengikutsertakanpelakuusaha,pakar,dan/atautokoh\nmasyarakatyangpedulipadakesejahteraanpetani.\n(4)\ndewankomoditaspertaniannasionalmerupakanmitrapemerintah\ndalamperumusanstrategidankebijakanperlindungandan\npemberday\naanpetani.\nparagraf3\nkelembagaanekonomipetani\npasal\n80\n(1)\nbadanusahamilik\np\netanidibentukoleh,dari,danuntukpetani\nmelaluigabungankelompoktanidenganpenyertaanmodalyang\nseluruhnyadimilikiolehgabungankelompoktani.\n2013,\nno.\n131\n26\n(2)\nbadanusahamilik\np\netani\nsebagaimanadimaksudpadaayat(1)\nberbentukkoperasiataubadanusahalainnyasesuaidengan\nketentuanperaturanperundang\n-\nundangan.\n(3)\nbadanusahamilikpetanisebagaimanadimaksudpadaayat(1)\nberfungsiuntukmeningkatkanskalaekonomi,dayasaing,wadah\ni\nnvestasi,danmengembangkanjiwakewirausahaanpetani.\npasal\n81\nbadanusahamilikpetanisebagaimanadimaksuddalampasal80paling\nsedikitbertugas\n:\na.\nmenyusunkelayakanusaha;\nb.\nmengembangkankemitraanusaha;dan\nc.\nmeningkatkannilaitambahkomoditaspertanian\n.\nbabvi\npembiayaan\ndanpendanaan\nbagiankesatu\numum\npasal\n82\n(1)\npembiayaan\ndanpendanaanuntukkegiatan\nperlindungan\ndan\npemberdayaanpetaniyangdilakukanolehpemerintahbersumberdari\nanggaranpendapatandanbelanjanegara.\n(2)\npembiayaan\ndanpendanaanuntuk\nkegiatan\nperlindungandan\npemberdayaan\npetani\nyangdilakukanolehpemerintahdaerah\nbersumberdarianggaranpendapatandanbelanjadaerah.\npasal\n83\npembi\na\nyaandanpe\nndanaan\ndalam\nkegiatan\nperlindungandan\npemberdayaanpetanidilakukan\nuntukmengembangkanu\nsahatani\nmelalui:\na.\nl\nembagaperbankan\n;\ndan/atau\nb.\nlembagapembiayaan.\nbagiankedua\nlembagaperbankan\npasal\n84\n(1)\ndalammelaksanakanperlindungan\nd\nan\npemberdayaanpetani,\npemerintahme\nnugasi\nbadanusahamiliknegarabidangperbankan\n2013,\nno.\n131\n27\ndanpemerintahdaerahmenugasi\nbadanusahamilikdaerahbidang\nperbankan\nuntukmelayanikebutuhanpembiayaanusahatanidan\nbadanusahamilikpetani\nsesuaidenganketentuanperaturan\nperundang\n-\nundangan\n.\n(2)\nuntukmelaksanakanpenugasansebagaimanadimaksudpadaayat\n(1),badanusahamilik\nnegarabidangperbankandanbadanusaha\nmilikdaerahbidangperbankanmembentukunitkhususpertanian.\n(3)\npelayanankebutuhanpembiayaanolehunitkhususpertanian\nsebagaimanadimaksudpadaayat(2)dilaksanakandenganprosedur\nmudahdanpersyaratanyangluna\nk.\npasal85\nselainmelaluipenugasansebagaimanadimaksuddalampasal8\n4\n,\npelayanankebutuhanpembiayaanusahatanidapatdilakukanolehbank\nswastasesuaidenganketentuanperaturanperundang\n-\nundangan.\npasal86\n(1)\nuntukmelaksanakanpenyalurankreditdan/ata\nupembiayaanusaha\ntani,pihakbankberperanaktifmembantupetaniagarmemenuhi\npersyaratanmemperolehkreditdan/ataupembiayaan.\n(2)\nselainmelaksanakanpenyalurankreditdan/ataupembiayaan,pihak\nbankberperanaktifmembantudanmemudahkanpetanimengakse\ns\nfasilitasperbankan.\n(3)\nbankdapatmenyalurkankreditdan/ataupembiayaanbersubsidi\nuntukusahatanimelaluilembagakeuanganbukanbankdan/atau\njejaringlembagakeuanganmikrodibidangagribisnis.\npasal87\nketentuanlebihlanjutmengenaipembentukanuni\ntkhususpertanian\nsertaprosedurpenyalurankreditdanpembiayaanusahatanidiatur\ndenganperaturanpemerintah.\nbagianketiga\nlembagapembiayaanpetani\npasal\n88\ndalammelaksanakanperlindungandan\npemberdayaanpetani,pemerintah\ndanpemerintahdaerahber\nkewajibanme\nnugasi\nlembagapembiayaan\npemerintahataupemerintahdaerah\nuntukmelayani\npetanidan/atau\nbadanusahamilikpetani\nmemperolehpembiayaanusahatani\nsesuai\ndenganketentuanperaturanperundang\n-\nundanga\nan.\n2013,\nno.\n131\n28\npasal89\nlembagapembiayaan\nberke\nwajib\nan\nmelaksanakankegiatanpembiayaan\nusahatani\ndenganpersyaratansederhanadanprosedurcepat.\npasal90\n(1)\nuntukmelaksanakanpenyalurankreditdan/ataupembiayaanbagi\npetani,pihaklembagapembiayaanberperanaktifmembantupetani\nagarmemenuhipersyaratanm\nemperolehkreditdan/atau\npembiayaan\n.\n(2)\nselainmelaksanakanpenyalurankreditdan/ataupembiayaan,pihak\nlembagapembiayaanberperanaktifmembantudanmemudahkan\npetanidalammemperolehfasilitaskreditdan/ataupembiayaan.\n(3)\nlembagapembiayaansebagaimanadi\nmaksuddalampasal88dapat\nmenyalurkankreditdan/ataupembiayaanbersubsidikepadapetani\nmelaluilembagakeuanganbukanbankdan/ataujejaringlembaga\nkeuanganmikrodibidangagribisnisdanpelakuusahauntuk\nmengembangkanpertanian.\npasal\n91\nketentuan\nlebihlanjutmengenaipersyaratansederhanadanprosedur\ncepatsebagaimanadimaksuddalampasal\n89\ndanpenyalurankredit\ndan/ataupembiayaanbagipetanisebagaimanadimaksuddalam\np\nasal\n90\ndiaturoleh\npemerintah\n.\nbabvi\ni\npengawasan\npasal\n92\n(1)\nuntukmenjamin\ntercapainyatujuanperlindungandanpemberdayaan\npetani,dilakukanpengawasanterhadapkinerjaperencanaandan\npelaksanaan.\n(2)\npengawasansebagaimanadimaksudpadaayat(1)meliputi\npemantauan,pelaporan,danevaluasi.\n(3)\npengawasansebagaimanadimaksudpadaay\nat(1)danayat(2)\ndilaksanakanolehpemerintahdanpemerintahdaerahsesuaidengan\nkewenangannya.\n(4)\ndalammelaksanakanpengawasansebagaimanadimaksudpadaayat\n(3)pemerintahdanpemerintahdaerahdapatmelibatkanmasyarakat\ndalampemantauandanpelaporan\ndenganmemberdayakanpotensi\nyangada.\n2013,\nno.\n131\n29\npasal93\n(1)\nlaporanhasilpengawasandisampaikansecaraberjenjangdari:\na.\np\nemerintahkabupaten/kotakepada\npemerintahprovinsi;dan\nb.\npemerintah\nprovinsikepadapemerintah.\n(2)\nlaporanhasilpengawasansebagaimanadimaksudpad\naayat(1)harus\nberbentukdokumentertulisdandisertaidokumenpendukung\nlainnya.\n(3)\nlaporanhasilpengawasansebagaimanadimaksudpadaayat(1)\nmerupakaninformasipublikyangdiumumkandandapatdiakses\nsecaraterbukaolehmasyarakatsesuaidenganketentu\nanperaturan\nperundang\n-\nundangan.\npasal94\n(1)\npemerintahataupemerintahdaerah\nsesuaidengan\nkewenangannya\ndapatmelakukanpemantauandanevaluasidarihasilpelaporan\nsecaraberjenjang.\n(2)\npemerintah\ndan\npemerintahdaerahsesuai\ndengan\nkewenangannya\nberkewajiba\nnmenindaklanjutilaporanhasilpengawasansebagaimana\ndimaksuddalampasal\n93\nsesuaidenganketentuanperaturan\nperundang\n-\nundangan.\nbabvi\nii\nperansertamasyarakat\npasal95\nmasyarakatdapatberperansertadalampenyelenggaraanperlindungan\ndanpemberdayaa\nnpetani.\npasal96\n(1)\nperansertamasyarakatsebagaimanadimaksuddalampasal\n95\ndapat\ndilakukan\nsecaraperseorangandan/atauberkelompok.\n(2)\nperan\nserta\nmasyarakatsebagaimanadimaksudpadaayat(1),dapat\ndilakukanterhadap:\na.\npenyusunanperencanaan;\nb.\nperlindunga\nnpetani;\nc.\npemberdayaanpetani;\nd.\npembiayaan\ndanpendanaan\n;da\nn\ne.\npengawasan\n.\n2013,\nno.\n131\n30\npasal97\nmasyarakatdalamperlindunganpetanidapatberperansertadalam:\na.\nmemeliharadanmenyediakanprasaranapertanian;\nb.\nmengutamakankonsumsihasilpertaniandalamnegeri;\nc.\nmenyediak\nanbantuansosialbagipetaniyangmengalamibencana;\ndan\nd.\nmelaporkanadanyapungutanyangtidaksesuaidenganperaturan\nperundang\n-\nundangan.\npasal\n98\nmasyarakatdalampemberdayaanpetanidapatberperansertadalam\nmenyelenggarakan:\na.\npendidikannonformal;\nb.\npel\natihandanpemagangan;\nc.\npenyuluhan;\nd.\npencegahanalihfungsilahanpertanian;\ne.\npenguatankelembagaanpetanidankelembagaanekonomipetani\n;\nf.\npemberian\nfasilitassumberpembiayaanataupermodalan\n;dan/atau\ng.\npemberianfasilitasaksesterhadapinformasi.\npasal99\nk\netentuanlebihlanjutmengenaiperansertamasyarakatdalam\nperlindungandanpemberdayaanpetanidiaturdalamperaturanmenteri\n.\nbabix\nketentuanpidana\npasal100\nsetiaporang\nyangmeng\nimpor\nkomoditaspertanian\nyangtidak\nmelalui\ntempat\nyangditetapkanole\nhpemerintahsebagaimanadimaksuddalam\npasal29,\ndipidanadenganpidanapenjarapalinglama\n6\n(\nenam\n)tahun\ndan\ndendapalingbanyakrp\n6\n.000.000.000,00(\nenam\nmiliarrupiah)\n.\npasal101\nsetiaporang\nyang\nmengimpor\nkomoditaspertanianpadasaat\nketersediaanko\nmoditaspertaniandalamnegeri\nsudahmen\ncukup\ni\nkebutuhankonsumsidan/ataucadanganpanganpemerintah\nsebagaimanadimaksuddalampasal\n30ayat(1)\ndipidanadenganpidana\npenjarapalinglama2(dua)tahun\ndan\ndendapalingbanyak\nrp2.000.000.000,00(duamili\narrupiah)\n.\n2013,\nno.\n131\n31\npasal1\n0\n2\nsetiaporang\nyang\nmelakukan\npenyuluhanyang\ntidaksesuaidengan\nmateri\npenyuluhan\ndalambentukteknologitertentu\nyangtelahditetapkan\noleh\npemerintahataupemerintahdaerah\nsebagaimanadimaksuddalam\npasal\n47\ndipidanadenganpidana\npenjarapalinglama2(dua)tahun\ndan\ndendapalingbanyakrp5\n0\n.000.000,00(\nlim\napuluhjutarupiah).\npasal\n103\npetaniyangmengalihfungsikanlahanpertanian\nmenjadilahannon\n-\npertanian\nsebagaimana\ndimaksuddalampasal6\n3\nayat(1)\ndipidanadengan\npidanapenj\narapalinglama4(empat)tahundandendapalingbanyak\nrp1.000.000.000,00(satumiliarrupiah).\npasal\n104\ndalamhalperbuatansebagaimanadimaksuddalam\npasal100sampai\ndengan\npasal10\n2dilakukan\nolehkorporasi,\nselainpidanaterhadap\npengurus\nnya\n,pidana\ndapatdijatuhkanterhadap\nkorporasi\nberupapidana\ndendadenganpe\nm\nberatan\n1/\n3(\nseper\ntiga)daripidanadendaterhadap\nperseorangan\n.\nbab\nx\nketentuanpenutup\npasal105\npadasaat\nundang\n-\nundangini\nmulaiberlaku\n,\nsemua\nketentuan\nperaturan\nperundang\n-\nundangan\nya\nng\nmengatur\nperlindungandanpemberdayaan\npetanimasihtetapberlakusepanjangbelumdiganti\natau\ntidak\nbertentangandenganundang\n-\nundangini.\npasal\n106\npembentukanunit\nkhusus\nsebagaimanadimaksuddalampasal8\n7\nharus\ntelah\ndi\nbentukpaling\nlama\n2(dua)ta\nhunsejak\nundang\n-\nundangini\ndiundangkan.\npasal\n107\nperaturanpelaksanaanundang\n-\nundanginiharustelahditetapkanpaling\nlam\na\n2(dua)\ntahun\nsejakundang\n-\nundanginidiundangkan.\npasal\n108\nundang\n-\nundanginimulaiberlakupadatanggaldiundangkan.\n2013,\nno.\n131\n32\nagarsetia\nporang\nmengetahuinya\n,memerintahkanpengundangan\nundang\n-\nundanginidenganpenempatannyadalamlembarannegara\nrepublikindonesia.\ndisahkandijakarta\npadatanggal6agustus2013\npresidenrepublikindonesia,\ndr.h.susilobambangyudhoyono\ndiundangkand\nijakarta\np\nadatanggal6agustus2013\nmenterihukumdanhakasasimanusia\nrepublikindonesia,\namirsyamsudin\n');
INSERT INTO `korpus` (`id`, `namafile`, `pathfile`, `isi`) VALUES
(17, 'UU Nomor 18 Tahun 2013 - Pencegahan dan Pemberantasan Perusakan Hutan.pdf', '/opt/lampp/temp/phpjhYSQH', 'lembarannegara\nr\nepublik\ni\nndonesia\nno.\n130,2013\nlingkunganhidup.hutan.pencegahan.\npemberantasan.\nperusakan.\n(penjelasan\nd\nalam\ntambahanlembarannegararepublik\nindonesianomor5432)\nundang\n-\nundangrepublikindonesia\nnomor18\ntahun2013\ntentang\npencegahandanpemberantasanperusakanhutan\ndenganrahmattuhanyangmahaesa\npresidenrepublikindonesia,\nmenimbang\n:\na.\nbahwahutan,sebagaikaruniadananugerahtuhanyang\nmahaesayangdiamanatkankepadabangsaindonesia,\nmerupaka\nnkekayaanyangdikuasaiolehnegaradan\nmemberikanmanfaatbagiumatmanusiayangwajib\ndisyukuri,dikelola,dandimanfaatkansecaraoptimal\nsertadijagakelestariannyauntuksebesar\n-\nbesarnya\nkemakmuranrakyatsebagaimanadinyatakandalam\nundang\n-\nundangda\nsarnegararepublikindonesiatahun\n1945;\nb.\nbahwapemanfaatandanpenggunaankawasanhutan\nharusdilaksanakansecaratepatdanberkelanjutan\ndenganmempertimbangkanfungsiekologis,sosial,dan\nekonomissertauntukmenjagakeberlanjutanbagi\nkehidupansekara\nngdankehidupangenerasiyangakan\ndatang;\nc.\nbahwatelahterjadiperusakanhutanyangdisebabkan\nolehpemanfaatanhutandanpenggunaankawasanhutan\nyangtidaksesuaidenganketentuanperaturan\nperundang\n-\nundangan;\n2013,\nno.\n130\n2\nd.\nbahwaperusakanhutan,terutamaberupapemb\nalakan\nliar,penambangantanpaizin,danperkebunantanpaizin\ntelahmenimbulkankerugiannegara,kerusakan\nkehidupansosialbudayadanlingkunganhidup,serta\nmeningkatkanpemanasanglobalyangtelahmenjadiisu\nnasional,regional,daninternasional;\ne.\nbahw\naperusakanhutansudahmenjadikejahatanyang\nberdampakluarbiasa,terorganisasi,danlintasnegara\nyangdilakukandenganmodusoperandiyangcanggih,\ntelahmengancamkelangsungankehidupanmasyarakat\nsehinggadalamrangkapencegahandanpemberantasan\npe\nrusakanhutanyangefektifdanpemberianefekjera\ndiperlukanlandasanhukumyangkuatdanyangmampu\nmenjaminefektivitaspenegakanhukum;\nf.\nbahwaperaturanperundang\n-\nundanganyangadasampai\nsaatinitidakmemadaidanbelummampumenangani\npemberantasanse\ncaraefektifterhadapperusakanhutan\nyangterorganisasi;dan\ng.\nbahwaberdasarkanpertimbangansebagaimana\ndimaksuddalamhurufa,hurufb,hurufc,hurufd,huruf\ne,danhuruffperlumembentukundang\n-\nundangtentang\npencegahandanpemberantasanperusakanhu\ntan.\nmengingat\n:\n1.\npasal20,pasal21,pasal28hayat(1),danpasal33ayat\n(3)undang\n-\nundangdasarnegararepublikindonesia\ntahun1945;dan\n2.\nundang\n-\nundangnomor41tahun1999tentang\nkehutanan(lembarannegararepublikindonesiatahun\n1999nomor167,\ntambahanlembarannegararepublik\nindonesianomor3888)sebagaimanatelahdiubahdengan\nundang\n-\nundangnomor19tahun2004tentang\npenetapanperaturanpemerintahpenggantiundang\n-\nundangnomor1tahun2004tentangperubahanatas\nundang\n-\nundangnomor41tahun\n1999tentang\nkehutananmenjadiundang\n-\nundang(lembarannegara\nrepublikindonesiatahun2004nomor86,tambahan\nlembarannegararepublikindonesianomor4412).\ndenganpersetujuanbersama\ndewanperwakilanrakyatrepublikindonesia\ndan\npresidenrepublikindo\nnesia\n2013,\nno.\n130\n3\nmemutuskan:\nmenetapkan\n:\nundang\n-\nundangtentangpencegahandan\npemberantasanperusakanhutan.\nbabi\nketentuanumum\npasal1\ndalamundang\n-\nundanginiyangdimaksuddengan:\n1.\nhutanadalahsuatukesatuanekosistemberupahamparanlahan\nberisisumberdayaal\namhayatiyangdidominasipepohonandalam\nkomunitasalamlingkungannyayangtidakdapatdipisahkanantara\nyangsatudanyanglainnya.\n2.\nkawasanhutanadalahwilayahtertentuyangditetapkanoleh\npemerintahuntukdipertahankankeberadaannyasebagaihutanteta\np.\n3.\nperusakanhutanadalahproses,cara,atauperbuatanmerusakhutan\nmelaluikegiatanpembalakanliar,penggunaankawasanhutantanpa\nizin\nataupenggunaanizinyangbertentangandenganmaksuddan\ntujuanpemberianizindidalamkawasanhutanyangtelahdite\ntapkan,\nyangtelahditunjuk,ataupunyangsedangdiprosespenetapannyaoleh\npemerintah.\n4.\npembalakanliaradalahsemuakegiatanpemanfaatanhasilhutan\nkayusecaratidaksahyangterorganisasi.\n5.\npenggunaankawasanhutansecaratidaksahadalahkegiatan\nterorg\nanisasiyangdilakukandidalamkawasanhutanuntuk\nperkebunandan/atau\npertambangantanpaizinmenteri.\n6.\nterorganisasiadalahkegiatanyangdilakukanolehsuatukelompok\nyangterstruktur,yangterdiriatas2(dua)orangataulebih,danyang\nbertindaksecar\nabersama\n-\nsamapadawaktutertentudengantujuan\nmelakukanperusakanhutan,tidaktermasukkelompokmasyarakat\nyangtinggaldidalamataudisekitarkawasanhutanyangmelakukan\nperladangantradisionaldan/ataumelakukanpenebangankayuuntuk\nkeperluansen\ndiridantidakuntuktujuankomersial.\n7.\npencegahanperusakanhutanadalahsegalaupayayangdilakukan\nuntukmenghilangkankesempatanterjadinyaperusakanhutan.\n8.\npemberantasanperusakanhutanadalahsegalaupayayangdilakukan\nuntukmenindaksecarahukumter\nhadappelakuperusakanhutan\nbaiklangsung,tidaklangsung,maupunyangterkaitlainnya.\n9.\npemanfaatanhutanadalahkegiatanuntukmemanfaatkankawasan\nhutan,jasalingkungan,hasilhutankayudanbukankayu,serta\nmemunguthasilhutankayudanbukankayuse\ncaraoptimaldanadil\n2013,\nno.\n130\n4\nuntukkesejahteraanmasyarakatdengantetapmenjaga\nkelestariannya.\n10.\npemanfaatanhasilhutankayuadalahkegiatanuntukmemanfaatkan\ndanmengusahakanhasilhutanberupakayumelaluikegiatan\npenebangan,permudaan,pengangkutan,pengola\nhandanpemasaran\ndengantidakmerusaklingkungandantidakmengurangifungsi\npokoknya.\n11.\nizinpemanfaatanhasilhutankayuadalahizinusahayangdiberikan\nolehmenteriuntukmemanfaatkanhasilhutanberupakayupada\nhutanproduksimelaluikegiatanpemanena\nnataupenebangan,\npengayaan,pemeliharaan,danpemasaran.\n12.\nsuratketerangansahnyahasilhutanadalahdokumen\n-\ndokumenyang\nmerupakanbuktilegalitashasilhutanpadasetiapsegmenkegiatan\ndalampenatausahaanhasilhutan.\n13.\nhasilhutankayuadalahhasilhuta\nnberupakayubulat,kayubulat\nkecil,kayuolahan,ataukayupacakanyangberasaldari\nkawasan\nhutan.\n14.\npohonadalahtumbuhanyangbatangnyaberkayudandapat\nmencapaiukurandiameter10(sepuluh)sentimeterataulebihyang\ndiukurpadaketinggian1,50(satu\nkomalimapuluh)meterdiatas\npermukaantanah.\n15.\npolisikehutananadalahpejabattertentudalamlingkupinstansi\nkehutananpusatdan/ataudaerahyangsesuaidengansifat\npekerjaannyamenyelenggarakandan/ataumelaksanakanusaha\npelindunganhutanyangoleh\nkuasaundang\n-\nundangdiberikan\nwewenangkepolisiankhususdibidangkehutanandankonservasi\nsumberdayaalamhayatidanekosistemnyayangberadadalamsatu\nkesatuankomando.\n16.\npejabatadalahorangyangdiperintahkanatauorangyangkarena\njabatannyamemiliki\nkewenangandengansuatutugasdantanggung\njawabtertentu.\n17.\npejabatpenyidikpegawainegerisipil,yangselanjutnyadisingkat\nppnsadalahpejabatpegawainegerisipiltertentudalamlingkup\ninstansikehutananpusatdandaerahyangolehundang\n-\nundang\ndiberi\nwewenangkhususdalampenyidikandibidangkehutanandan\nkonservasisumberdayaalamhayatidanekosistemnya.\n18.\nsaksiadalahorangyangdapatmemberikanketeranganguna\nkepentinganpenyelidikan,penyidikan,penuntutan,danperadilan\ntentangsuatuperkarapi\ndanayangdidengar,dilihat,dandialami\nsendiri.\n2013,\nno.\n130\n5\n19.\npelaporadalahorangyangmemberitahukanadanyadugaan,sedang,\natautelahterjadinyaperusakanhutankepadapejabatyang\nberwenang.\n20.\ninformanadalahorangyangmenginformasikansecararahasiaadanya\ndugaan,\nsedang,atautelahterjadinyaperusakanhutankepada\npejabatyangberwenang.\n21.\nsetiaporangadalahorangperseorangandan/ataukorporasiyang\nmelakukanperbuatanperusakanhutansecaraterorganisasidi\nwilayahhukumindonesiadan/atauberakibathukumdiwil\nayah\nhukumindonesia.\n22.\nkorporasiadalahkumpulanorangdan/ataukekayaanyang\nteroganisasi,baikberupabadanhukummaupunbukanbadan\nhukum.\n23.\npemerintahpusat,yangselanjutnyadisebutpemerintahadalah\npresidenrepublikindonesiayangmemegangkekuasaan\npe\nmerintahannegararepublikindonesiasebagaimanadimaksud\ndalamundang\n-\nundangdasarnegararepublikindonesiatahun1945.\n24.\npemerintahdaerahadalahgubernur,bupatiatauwalikotadan\nperangkat\ndaerahsebagaiunsurpenyelenggarapemerintahandaerah.\n25.\nmenteri\nadalahmenteriyangmenyelenggarakanurusanpemerintahan\ndibidangkehutanan.\nbabii\nasas,tujuan,danruanglingkup\npasal2\npencegahandanpemberantasanperusakanhutanberasaskan:\na.\nkeadilandankepastianhukum;\nb.\nkeberlanjutan;\nc.\ntanggungjawabnegara;\nd.\npart\nisipasimasyarakat;\ne.\ntanggunggugat;\nf.\nprioritas;dan\ng.\nketerpaduandankoordinasi.\npasal3\npencegahandanpemberantasanperusakanhutanbertujuan:\na.\nmenjamin\nkepastianhukumdanmemberikanefekjerabagipelaku\nperusakanhutan;\n2013,\nno.\n130\n6\nb.\nmenjaminkeberadaanhutansecarab\nerkelanjutandengantetap\nmenjagakelestariandantidakmerusaklingkungansertaekosistem\nsekitarnya;\nc.\nmengoptimalkanpengelolaandanpemanfaatanhasilhutandengan\nmemperhatikankeseimbanganfungsihutangunaterwujudnya\nmasyarakatsejahtera;dan\nd.\nmeningka\ntnyakemampuandankoordinasiaparatpenegakhukum\ndanpihak\n-\npihakterkaitdalammenanganipencegahandan\npemberantasanperusakanhutan.\npasal4\nruanglingkuppencegahandanpemberantasanperusakanhutanmeliputi:\na.\npencegahanperusakanhutan;\nb.\npemberantasan\nperusakanhutan;\nc.\nkelembagaan;\nd.\nperansertamasyarakat;\ne.\nkerjasamainternasional;\nf.\npelindungansaksi,pelapor,daninforman;\ng.\npembiayaan;dan\nh.\nsanksi.\nbabiii\npencegahanperusakanhutan\npasal5\npemerintahdan/ataupemerintahdaerahberkewajibanmelakukan\npence\ngahanperusakanhutan.\npasal6\n(1)\ndalamrangkapencegahanperusakanhutan,pemerintahmembuat\nkebijakanberupa:\na.\nkoordinasilintassektordalampencegahandanpemberantasan\nperusakanhutan;\nb.\npemenuhankebutuhansumberdayaaparaturpengamanan\nhutan;\nc.\ninsentifba\ngiparapihakyangberjasadalammenjagakelestarian\nhutan;\n2013,\nno.\n130\n7\nd.\npetapenunjukankawasanhutandan/ataukoordinatgeografis\nsebagaidasaryuridisbataskawasanhutan;dan\ne.\npemenuhankebutuhansaranadanprasaranapencegahandan\npemberantasanperusakanhutan.\n(2)\npem\nerintahdanpemerintahdaerahsesuaidengankewenangannya\nmenetapkansumberkayualternatifdenganmendorong\npengembanganhutantanamanyangproduktifdanteknologi\npengolahan.\n(3)\nselainmembuatkebijakansebagaimanadimaksudpadaayat(1),\nupayapencegahanp\nerusakanhutandilakukanmelaluipenghilangan\nkesempatandenganmeningkatkanperansertamasyarakat.\n(4)\nketentuanlebihlanjutmengenaipenetapansumberkayualternatif\nsebagaimanadimaksudpadaayat(2)diaturdalamperaturanmenteri.\npasal7\npencegahanperu\nsakanhutandilakukanolehmasyarakat,badanhukum,\ndan/ataukorporasiyangmemperolehizinpemanfaatanhutan.\nbabiv\npemberantasanperusakanhutan\nbagiankesatu\numum\npasal8\n(1)\npemerintahdanpemerintahdaerahberkewajibanmelakukan\npemberantasanperusakanh\nutan.\n(2)\npemberantasanperusakanhutandilakukandengancaramenindak\nsecara\nhukumpelakuperusakanhutan,baiklangsung,tidak\nlangsung,maupunyangterkaitlainnya.\n(3)\ntindakansecarahukumsebagaimanadimaksudpadaayat(2)\nmeliputipenyelidikan,penyidikan,\npenuntutan,danpemeriksaandi\nsidangpengadilan.\npasal9\npenyelidikan,penyidikan,penuntutan,danpemeriksaandisidang\npengadilandalamperkaratindakpidanaperusakanhutandilakukan\nberdasarkanhukumacarapidanayangberlaku,kecualiditentukanlain\ndalamundang\n-\nundangini.\npasal10\nperkaraperusakanhutanharusdidahulukandariperkaralainuntuk\ndiajukankesidangpengadilangunapenyelesaiansecepatnya.\n2013,\nno.\n130\n8\nbagiankedua\nketentuanperbuatanperusakanhutan\npasal11\n(1)\nperbuatanperusakanhutansebagaimana\ndimaksuddalamundang\n-\nundanginimeliputikegiatanpembalakanliardan/ataupenggunaan\nkawasanhutansecaratidaksahyangdilakukansecaraterorganisasi.\n(2)\nperbuatanperusakanhutansecaraterorganisasimerupakankegiatan\nyangdilakukanolehsuatukelompok\nyangterstruktur,yangterdiri\natas2(dua)orangataulebih,danyang\nbertindaksecarabersama\n-\nsamapadawaktutertentudengantujuanmelakukanperusakan\nhutan.\n(3)\nkelompokterstruktursebagaimanadimaksudpadaayat(2)tidak\ntermasukkelompokmasyarakatyan\ngbertempattinggaldidalam\ndan/ataudisekitarkawasanhutanyangmelakukanperladangan\ntradisionaldan/ataumelakukanpenebangankayudiluarkawasan\nhutankonservasidanhutanlindunguntukkeperluansendiridan\ntidakuntuktujuankomersial.\n(4)\nmasyarakat\nyangbertempattinggaldidalamdan/ataudisekitar\nkawasanhutanyangmelakukanpenebangankayudiluarkawasan\nhutankonservasidanhutanlindunguntukkeperluansendiridan\ntidakuntuktujuankomersialharusmendapatizindaripejabatyang\nberwenangse\nsuaidenganketentuanperaturanperundang\n-\nundangan.\n(5)\nketentuanmengenaipenebangankayudiluarkawasanhutan\nkonservasidanhutanlindunguntukkeperluansendiridantidak\nuntuktujuankomersialdiaturlebihlanjutdenganperaturan\npemerintah.\npasal12\nse\ntiaporangdilarang:\na.\nmelakukanpenebanganpohondalamkawasanhutanyangtidak\nsesuaidenganizinpemanfaatanhutan;\nb.\nmelakukanpenebanganpohondalamkawasanhutantanpamemiliki\nizinyangdikeluarkanolehpejabatyangberwenang;\nc.\nmelakukanpenebanganpoho\nndalamkawasanhutansecaratidak\nsah;\nd.\nmemuat,membongkar,mengeluarkan,mengangkut,menguasai,\ndan/ataumemilikihasilpenebangandikawasanhutantanpaizin;\ne.\nmengangkut,menguasai,ataumemilikihasilhutankayuyangtidak\ndilengkapisecarabersamasura\ntketerangansahnyahasilhutan;\n2013,\nno.\n130\n9\nf.\nmembawaalat\n-\nalatyanglazimdigunakanuntukmenebang,\nmemotong,ataumembelahpohondidalamkawasanhutantanpaizin\npejabatyangberwenang;\ng.\nmembawaalat\n-\nalatberatdan/ataualat\n-\nalatlainnyayanglazimatau\npatutdiduga\nakandigunakanuntukmengangkuthasilhutandi\ndalamkawasanhutantanpaizinpejabatyangberwenang;\nh.\nmemanfaatkanhasilhutankayuyangdidugaberasaldarihasil\npembalakanliar;\ni.\nmengedarkankayuhasilpembalakanliarmelaluidarat,perairan,atau\nudara;\nj.\nmenyelundupkankayuyangberasaldariataumasukkewilayah\nnegarakesatuanrepublikindonesiamelaluisungai,darat,laut,atau\nudara;\nk.\nmenerima,membeli,menjual,menerimatukar,menerimatitipan,\ndan/ataumemilikihasilhutanyangdiketahuiberasaldari\npembalakanliar;\nl.\nmembeli,memasarkan,dan/ataumengolahhasilhutankayuyang\nberasaldarikawasanhutanyangdiambilataudipungutsecaratidak\nsah;\ndan/atau\nm.\nmenerima,menjual,menerimatukar,menerimatitipan,menyimpan,\ndan/ataumemilikihasilhutankay\nuyangberasaldarikawasanhutan\nyangdiambilataudipungutsecaratidaksah.\npasal13\n(1)\npenebanganpohondalamkawasanhutansecaratidaksah\nsebagaimanadimaksuddalampasal12\nhurufcmerupakan\npenebanganpohonyangdilakukandalamkawasanhutandengan\nr\nadiusataujaraksampaidengan:\na.\n500(limaratus)meterdaritepiwadukataudanau;\nb.\n200(duaratus)meterdaritepimataairdankirikanansungaidi\ndaerahrawa;\nc.\n100(seratus)meterdarikirikanantepisungai;\nd.\n50(limapuluh)meterdarikirikanantepian\naksungai;\ne.\n2(dua)kalikedalamanjurangdaritepijurang;dan/atau\nf.\n130(seratustigapuluh)kaliselisihpasangtertinggidanpasang\nterendahdaritepipantai.\n(2)\npenebanganpohonyangdilakukandalamkawasanhutan\nsebagaimana\ndimaksud\npadaayat(1)hurufa\ndikecualikanuntuk\nkegiatanyangmempunyaitujuanstrategisyangtidakdapat\ndihindari\ndenganmendapatizinkhususdarimenteri.\n2013,\nno.\n130\n10\npasal14\nsetiaporangdilarang:\na.\nmemalsukansuratketerangansahnyahasilhutankayu;dan/atau\nb.\nmenggunakansuratketerangansahn\nyahasilhutankayuyangpalsu.\npasal15\nsetiaporangdilarangmelakukanpenyalahgunaandokumenangkutan\nhasilhutankayuyangditerbitkanolehpejabatyangberwenang.\npasal16\nsetiaporangyangmelakukanpengangkutankayuhasilhutanwajib\nmemilikidokume\nnyangmerupakansuratketerangansahnyahasilhutan\nsesuaidenganketentuanperaturanperundang\n-\nundangan.\npasal17\n(1)\nsetiaporangdilarang:\na.\nmembawaalat\n-\nalatberatdan/ataualat\n-\nalatlainyanglazimatau\npatutdidugaakandigunakanuntukmelakukankegiatan\npenambangandan/ataumengangkuthasiltambangdidalam\nkawasanhutantanpaizinmenteri;\nb.\nmelakukankegiatanpenambangandidalamkawasanhutan\ntanpaizinmenteri;\nc.\nmengangkutdan/ataumenerimatitipanhasiltambangyang\nberasaldarikegiatanpenambangandi\ndalamkawasanhutan\ntanpaizin;\nd.\nmenjual,menguasai,memiliki,dan/ataumenyimpanhasil\ntambangyangberasaldarikegiatanpenambangandidalam\nkawasanhutantanpaizin;dan/atau\ne.\nmembeli,memasarkan,dan/ataumengolahhasiltambangdari\nkegiatanpenambangan\ndidalamkawasanhutantanpaizin.\n(2)\nsetiaporangdilarang:\na.\nmembawaalat\n-\nalatberatdan/ataualat\n-\nalatlainnyayanglazim\nataupatutdidugaakandigunakanuntukmelakukankegiatan\nperkebunandan/ataumengangkuthasilkebundidalamkawasan\nhutantanpaizin\nmenteri;\nb.\nmelakukankegiatanperkebunantanpaizinmenterididalam\nkawasanhutan;\nc.\nmengangkutdan/ataumenerimatitipanhasilperkebunanyang\nberasaldarikegiatanperkebunandidalamkawasanhutantanpa\nizin;\n2013,\nno.\n130\n11\nd.\nmenjual,menguasai,memiliki,dan/ataumenyimp\nanhasil\nperkebunanyangberasaldarikegiatanperkebunandidalam\nkawasanhutantanpaizin;dan/atau\ne.\nmembeli,memasarkan,dan/ataumengolahhasilkebundari\nperkebunanyangberasaldarikegiatanperkebunandidalam\nkawasanhutantanpaizin.\npasal18\n(1)\ns\nelaindikenaisanksipidana,pelanggaranterhadapketentuan\nsebagaimanadimaksuddalampasal12hurufa,hurufb,hurufc,\npasal17ayat(1)hurufb,hurufc,hurufe,danpasal17ayat(2)huruf\nb,hurufc,danhurufeyangdilakukanolehbadanhukumatau\nkorporasidikenaisanksiadministratifberupa:\na.\npaksaanpemerintah;\nb.\nuangpaksa;dan/atau\nc.\npencabutanizin.\n(2)\nketentuanmengenaimekanismedantatacarapenerapansanksi\nadministratifsebagaimanadimaksudpadaayat(1)diaturdengan\nperaturanpemerintah.\np\nasal19\nsetiaporangyangberadadidalamataudiluarwilayahindonesiadilarang:\na.\nmenyuruh,mengorganisasi,ataumenggerakkanpembalakanliar\ndan/ataupenggunaankawasanhutansecaratidaksah;\nb.\nikutsertamelakukanataumembantuterjadinyapembalakanliar\ndan/ataupenggunaankawasanhutansecaratidaksah;\nc.\nmelakukanpermufakatanjahatuntukmelakukanpembalakanliar\ndan/ataupenggunaankawasanhutansecaratidaksah;\nd.\nmendanaipembalakanliardan/ataupenggunaankawasanhutan\nsecaratidaksahsecaralangsu\nngatautidaklangsung;\ne.\nmenggunakandanayangdidugaberasaldarihasilpembalakanliar\ndan/ataupenggunaankawasanhutansecaratidaksah;\nf.\nmengubahstatuskayuhasilpembalakanliardan\n/\natauhasil\npenggunaankawasanhutansecaratidaksah,seolah\n-\nolahme\nnjadi\nkayuyangsah,atauhasilpenggunaankawasanhutanyangsah\nuntukdijualkepadapihakketiga,baikdidalammaupundiluar\nnegeri;\ng.\nmemanfaatkankayuhasilpembalakanliardenganmengubahbentuk,\nukuran,termasukpemanfaatanlimbahnya;\n2013,\nno.\n130\n12\nh.\nmenempatkan,me\nntransfer,membayarkan,membelanjakan,\nmenghibahkan,menyumbangkan,menitipkan,membawakeluar\nnegeri,dan/ataumenukarkanuang\natausuratberhargalainnyaserta\nhartakekayaanlainnyayangdiketahuinyaataupatutdiduga\nmerupakanhasilpembalakanliarda\nn/atauhasilpenggunaan\nkawasanhutansecaratidaksah;dan/atau\ni.\nmenyembunyikanataumenyamarkanasalusulhartayangdiketahui\nataupatutdidugaberasaldarihasilpembalakanliardan/atauhasil\npenggunaankawasanhutansecaratidaksahsehinggaseolah\n-\nol\nah\nmenjadihartakekayaanyangsah.\npasal20\nsetiaporangdilarangmencegah,merintangi,dan/ataumenggagalkan\nsecaralangsungmaupuntidaklangsungupayapemberantasan\npembalakanliardanpenggunaankawasanhutansecaratidaksah.\npasal21\nsetiaporangdi\nlarangmemanfaatkankayuhasilpembalakanliardan/atau\npenggunaankawasanhutansecaratidaksahyangberasaldarihutan\nkonservasi.\npasal22\nsetiaporangdilarangmenghalang\n-\nhalangidan/ataumenggagalkan\npenyelidikan,penyidikan,penuntutan,ataupemerik\nsaandisidang\npengadilantindakpidanapembalakanliardanpenggunaankawasan\nhutansecaratidaksah.\npasal23\nsetiaporangdilarangmelakukanintimidasidan/atauancamanterhadap\nkeselamatanpetugasyangmelakukanpencegahandanpemberantasan\npembalakan\nliardanpenggunaankawasanhutansecaratidaksah.\npasal24\nsetiaporangdilarang:\na.\nmemalsukansuratizinpemanfaatanhasilhutankayudan/atau\npenggunaankawasanhutan;\nb.\nmenggunakansuratizinpalsupemanfaatanhasilhutankayu\ndan/ataupenggunaankawasa\nnhutan;dan/atau\nc.\nmemindahtangankanataumenjualizinyangdikeluarkanolehpejabat\nyangberwenangkecualidenganpersetujuanmenteri.\npasal25\nsetiaporangdilarangmerusaksaranadanprasaranapelindunganhutan.\n2013,\nno.\n130\n13\npasal26\nsetiaporangdilarangmerusak,me\nmindahkan,ataumenghilangkanpal\nbatasluarkawasanhutan,batasfungsikawasanhutan,ataubatas\nkawasanhutanyangberimpitdenganbatasnegarayangmengakibatkan\nperubahanbentukdan/atauluasankawasanhutan.\npasal27\nsetiappejabatyangmengetahuite\nrjadinyaperbuatansebagaimana\ndimaksuddalampasal12,13,14,15,16,17,dan19wajibmelakukan\ntindakansesuaidengankewenangannya.\npasal28\nsetiappejabatdilarang:\na.\nmenerbitkanizinpemanfaatanhasilhutankayudan/atau\npenggunaankawasanhutandida\nlamkawasanhutanyangtidak\nsesuaidengankewenangannya;\nb.\nmenerbitkanizinpemanfaatandidalamkawasanhutandan/atauizin\npenggunaankawasanhutanyangtidaksesuaidenganketentuan\nperaturanperundang\n-\nundangan;\nc.\nmelindungipelakupembalakanliardan/atau\npenggunaankawasan\nhutansecaratidaksah;\nd.\nikutsertaataumembantukegiatanpembalakanliardan/atau\npenggunaankawasanhutansecaratidaksah;\ne.\nmelakukanpermufakatanuntukterjadinyapembalakanliardan/atau\npenggunaankawasanhutansecaratidaksah;\nf.\nme\nnerbitkansuratketerangansahnyahasilhutantanpahak;\ng.\ndengansengajamelakukanpembiarandalammelaksanakantugas;\ndan/atau\nh.\nlalaidalammelaksanakantugas.\nbagianketiga\npenyidikan,penuntutan,danpemeriksaandisidangpengadilan\nparagraf1\npenyidikan\ndanpenuntutan\npasal29\nselainpenyidikpejabatpolisinegararepublikindonesia,ppnsdiberi\nwewenangkhusussebagaipenyidiksebagaimanadimaksuddalamkitab\nundang\n-\nundanghukumacarapidana.\n2013,\nno.\n130\n14\npasal30\nppnssebagaimanadimaksuddalampasal29berwenang:\na.\nmelakukanpemeriksaanataskebenaranlaporanatauketerangan\nberkenaandengantindakpidanaperusakanhutan;\nb.\nmelakukanpemeriksaanterhadaporangataubadanhukumyang\ndidugamelakukantindakpidanaperusakanhutan;\nc.\nmemintaketerangandanbarangbuktidar\niorangataubadanhukum\nsehubungandenganperistiwatindakperusakanhutan;\nd.\nmelakukanpemeriksaanataspembukuan,catatan,dandokumenlain\nberkenaandengantindakpidanaperusakanhutan;\ne.\nmelakukanpemeriksaanditempattertentuyangdidugaterdapat\nbaran\ngbukti,pembukuan,pencatatan,dandokumenlainserta\nmelakukanpenyitaanterhadapbahandanbaranghasilkejahatan\nyangdapatdijadikanbuktidalamperkaratindakpidanaperusakan\nhutan;\nf.\nmelakukanpenangkapan,penahanan,penggeledahan,danpenyitaan;\ng.\nme\nmintabantuanahlidalamrangkapelaksanaantugaspenyidikan\ntindakpidanaperusakanhutan;\nh.\nmenghentikanpenyidikanapabilatidakterdapatbuktitentangadanya\ntindakanperusakanhutan;\ni.\nmemanggiloranguntukdidengardandiperiksasebagaitersangka\natausa\nksi;\nj.\nmembuatdanmenandatanganiberitaacaradansurat\n-\nsuratlainyang\nmenyangkutpenyidikanperkaraperusakanhutan;dan\nk.\nmemotretdan/ataumerekammelaluialatpotretdan/ataualat\nperekamterhadaporang,barang,saranapengangkut,atauapasaja\nyangdapa\ntdijadikanbuktitindakpidanayangmenyangkuthutan,\nkawasanhutan,danhasilhutan.\npasal31\nwilayahhukumatauwilayahkerjappnssebagaimanadimaksuddalam\npasal29meliputiseluruhwilayahnegarakesatuanrepublikindonesia\ntermasukwilayahkepabeana\nn.\npasal32\nppnssebagaimanadimaksuddalampasal29memberitahukandimulainya\npenyidikandanmenyampaikanhasilpenyidikannyakepadapenuntut\numumsetelahberkoordinasidenganpenyidikpejabatpolisinegara\nrepublikindonesia.\n2013,\nno.\n130\n15\npasal33\nuntukmemperolehbu\nktipermulaanyangcukup,penyidikdapat\nmenggunakanlaporanyangberasaldarimasyarakatdan/atauinstansi\nterkait.\npasal34\n(1)\nberdasarkanbuktipermulaanyangcukupsebagaimanadimaksud\ndalampasal33,penyidikberwenangmemintakepadalembaga\npenyelen\nggarakomunikasiuntuk:\na.\nmembuka,memeriksa,danmenyitasuratataukirimanmelalui\npossertajasapengirimanlainnyayangmempunyaihubungan\ndenganpembalakanliaryangsedangdiperiksa;dan/atau\nb.\nmemintainformasipembicaraanmelaluiteleponataualat\nkomu\nnikasilainyangdidugadigunakanuntukmempersiapkan,\nmerencanakan,danmelakukanperusakanhutan.\n(2)\ntindakansebagaimanadimaksudpadaayat(1)hurufb,hanyadapat\ndilakukanatasizinketuapengadilannegerisetempatataspermintaan\npenyidikuntukjan\ngkawaktupalinglama1(satu)tahun.\n(3)ketuapengadilannegerisetempatwajibmemberikanizinuntuk\nmemintainformasisebagaimanadimaksudpadaayat(2)dalamwaktu\npalinglama7(tujuh)harikerjasetelahditerimanyapermintaandari\npenyidik.\n(4)\ntinda\nkansebagaimanadimaksudpadaayat(1)danayat(2)\ndilaporkansertadipertanggungjawabkankepadaatasanpenyidik.\npasal\n35\n(1)\nuntukkepentinganpenyidikan,penuntutan,ataupemeriksaandi\nsidangpengadilan,penyidik,penuntutumum,atauhakimberwenang\nmemin\ntaketerangankepadabanktentangkeadaankeuangan\ntersangkaatauterdakwa.\n(2)\npermintaanketerangankepadabanksebagaimanadimaksudpada\nayat(1)diajukankepadapimpinanotoritasjasakeuangan.\n(3)\npimpinanotoritasjasakeuanganwajibmemenuhipermintaan\nseba\ngaimanadimaksudpadaayat(2)dalamwaktupalinglama3(tiga)\nharikerjaterhitungsejaksuratpermintaanditerima.\n(4)\npenyidik,penuntutumum,atauhakimberwenangmemintakepada\nbankuntukmemblokirrekeningsimpananmiliktersangkaatau\nterdakwayangdid\nugasebagaihasilpembalakanliarselamaproses\npenyidikan,penuntunan,dan/ataupemeriksaanberlangsung.\n(5)\ndalamhalhasilpemeriksaanterhadaptersangkaatauterdakwatidak\ndiperolehbuktiyangcukup,ataspermintaanpenyidik,penuntut\numum,atauhakim,pi\nmpinanbankharusmencabutpemblokiran.\n2013,\nno.\n130\n16\npasal36\nuntukkepentinganpenyidikan,penuntutan,ataupemeriksaandisidang\npengadilan,penyidik,penuntutumum,atauhakimberwenang:\na.\nmemintadatakekayaandandataperpajakantersangkaatauterdakwa\nkepadaunitk\nerjaterkait;\nb.\nmemintabantuankepadapusatpelaporandananalisistransaksi\nkeuanganuntukmelakukanpenyelidikanatasdatakeuangan\ntersangka;\nc.\nmemintakepadainstansiyangterkaituntukmelarangseseorang\nberpergiankeluarnegeri;\nd.\nmenetapkanseseorangse\nbagaitersangkadandimasukkandalam\ndaftarpencarianorang;dan/atau\ne.\nmemintakepadapimpinanatauatasantersangkauntuk\nmemberhentikansementaratersangkadarijabatannya.\npasal37\nalatbuktipemeriksaanperbuatanperusakanhutanmeliputi:\na.\nalatbuktise\nbagaimanadimaksuddalamkitabundang\n-\nundang\nhukumacarapidana;dan/atau\nb.\nalatbuktilainberupa:\n1.\ninformasielektronik;\n2.\ndokumenelektronik;dan/atau\n3.\npeta.\npasal38\n(1)\npenyidikmelakukanpenangkapanterhadaporangyangdidugakeras\nmelakukantindakpidanaper\nusakanhutanberdasarkanbukti\npermulaanyangcukupuntukpalinglama2x24(duakaliduapuluh\nempat)jam.\n(2)\ndalamhal\nwaktu\nuntukpemeriksaansebagaimanadimaksudpada\nayat(1)belummencukupi,atasanlangsungpenyidikdapatmemberi\nizinuntukmemperpanja\nngpenangkapantersebutuntukpalinglama\n3x24(tigakaliduapuluhempat)jam.\npasal39\nuntukmempercepatpenyelesaianperkaraperusakanhutan:\na.\npenyidikwajibmenyelesaikandanmenyampaikanberkasperkara\nkepadapenuntutumumpalinglama60(enampuluh)\nharisejak\ndimulainyapenyidikandandapatdiperpanjangpalinglama30(tiga\npuluh)hari;\n2013,\nno.\n130\n17\nb.\ndalamhalhasilpenyidikanbelumlengkap,penuntutumumwajib\nmelakukanpenyidikanpalinglama20(duapuluh)haridandapat\ndiperpanjangpalinglama30(tigapuluh)\nhari;\nc.\npenuntutumumwajibmelimpahkanperkarakepengadilanpaling\nlama25(duapuluhlima)hariterhitungsejakselesaipenyidikan;\nd.\nuntukdaerahyangsulitterjangkaukarenafaktoralamdangeografis\natautransportasidantingginyabiayadalamrangkapen\njagaandan\npengamananbarangbukti,terhadapbarangbuktikayucukup\ndilakukanpenyisihanbarangbuktiyangdisertaidenganberitaacara\npenyisihanbarangbukti;dan\ne.\ninstansitekniskehutananwajibmenunjukahlipengujidanpengukur\nkayuyangdimintapenyi\ndikdenganmempertimbangkankecepatan\nuntukpenyidikan.\npasal40\n(1)\npenyidikyangmelakukanpenyitaanbarangbuktihasiltindakpidana\nperusakan\nhutan,baikberupabarangbuktitemuanmaupunbarang\nbuktisitaan,wajibmelakukanpenyegelandanmembuatberitaa\ncara\npenyitaanpadaharipenyitaandilakukanyangsekurang\n-\nkurangnya\nmemuat:\na.\nnama,kelompokjenis,sifat,danjumlah;\nb.\nketerangantempat,jam,hari,tanggal,bulan,dantahun\ndilakukanpenyitaan;\nc.\nketeranganmengenaipemilikatauyangmenguasaikayuhasil\npe\nmbalakanliar;dan/atau\nd.\ntandatangandanidentitas\nlengkappejabatpenyidikyang\nmelakukanpenyitaan.\n(2)\npenyidikbertanggungjawabataspenyimpananbarangbukti\nsebagaimana\ndimaksudpadaayat(1)yangberadadibawah\npenguasaannya.\n(3)\npenyidikyangmelakukanpe\nnyitaanbarangbuktitemuan\nsebagaimanadimaksudpadaayat(1)wajib:\na.\nmelaporkandanmemintaizinsita;\nb.\nmemintaizinperuntukankepadaketuapengadilannegeri\nsetempatdalamwaktupalinglama3x24(tigakaliduapuluh\nempat)jamsejakdilakukanpenyitaan\n;dan\nc.\nmenyampaikantembusankepadakepalakejaksaannegeri\nsetempat.\n2013,\nno.\n130\n18\n(4)\npenyidikyangmelakukanpenyitaanbarangbuktisitaansebagaimana\ndimaksudpadaayat(1)wajib:\na.\nmelaporkandanmemintaizinsita;\nb.\nmemintaizinlelangbagibarangyangmudahrusakkepada\nketua\npengadilannegerisetempatdalamwaktupalinglama3x24(tiga\nkaliduapuluhempat)jamsejakdilakukanpenyitaan;dan\nc.\nmenyampaikantembusankepadakepalakejaksaannegeri\nsetempat.\n(5)\nbataswaktupelaporansebagaimanadimaksudpadaayat(4),untuk\nd\naerahyangsulitterjangkaukarenafaktoralam,geografis,atau\ntransportasi,dapatdiperpanjangmenjadipalinglama14(empatbelas)\nhari.\n(6)\nketuapengadilannegeriwajibmenerbitkanataumenolak\nizin/persetujuansitayangdiajukanolehpenyidikpalinglamb\nat2x\n24(duakaliduapuluhempat)jamsejakpermintaanditerima.\npasal41\nketuapengadilannegerisetempat,dalamwaktupalinglama7(tujuh)hari\nsetelahmenerimapermintaanpenyidiksebagaimanadimaksuddalam\npasal40ayat(3),wajibmenetapkanperunt\nukanpemanfaatanbarang\nbukti.\npasal42\nsetiappejabatyangtidakmelaksanakankewajibansebagaimana\ndimaksuddalampasal39,pasal40,danpasal41dikenaisanksi\nadministratifsesuaidenganketentuanperaturanperundang\n-\nundangan.\npasal\n43\nperuntukanpema\nnfaatanbarangbuktisebagaimanadimaksuddalam\npasal41ditujukan:\na.\nuntukkepentinganpembuktianperkara;\nb.\nuntukpemanfaatanbagikepentinganpengembanganilmu\npengetahuan;\nc.\nuntukdimusnahkan;dan/atau\nd.\nuntukkepentinganpublikataukepentingansosial.\npasal\n44\n(1)\nbarangbuktikayuhasilpembalakanliardan/atauhasildari\npenggunaankawasanhutansecaratidaksahyangberasaldarihutan\nkonservasidimusnahkan,kecualiuntukkepentinganpembuktian\nperkaradanpenelitian.\n2013,\nno.\n130\n19\n(2)\nbarangbuktikayutemuan\nhasilpembalakan\nliaryangberasaldari\nluarhutankonservasidimanfaatkan\nuntukkepentinganpublikatau\nkepentingansosial.\n(3)\nbarangbuktikayusitaan\nhasilpembalakanliaryangberasaldariluar\nhutankonservasidapatdilelangkarenadapatcepatrusakataubiaya\npenyimpan\nannyaterlalutinggi\nyangpelaksanaannyasesuaidengan\nketentuanperaturanperundang\n-\nundangan.\n(4)\nhasillelangkayusitaansebagaimanadimaksudpadaayat(3)\ndisimpandibankpemerintahsebagaibarangbuktiperkaradi\npengadilan.\n(5)\nperuntukanbarangbuktiperk\narasebagaimanadimaksudpadaayat\n(4)dilaksanakansesuaidenganputusanpengadilanyangmempunyai\nkekuatanhukumtetap.\npasal\n45\n(1)\nbarangbuktitemuan\nhasilkebundan/atauhasiltambangbeserta\nsaranaprasaranapendukungnyadarihasiltindakpidanapenggun\naan\nkawasanhutansecaratidaksahdapatdilelangdanhasilnya\ndimanfaatkan\nuntukkepentinganpublikataukepentingansosial.\n(2)\nbarangbuktisitaanhasilkebundan/atauhasiltambangbeserta\nsaranaprasaranapendukungnyadarihasiltindakpidanapenggunaan\nk\nawasanhutansecaratidaksahdapatdilelangkarenadapatcepat\nrusakataubiayapenyimpanannyaterlalutinggi\n.\n(3)\nhasil\nlelangbarang\nbuktisitaanhasilkebundan/atauhasiltambang\nbesertasaranaprasaranapendukungnya\nsebagaimanadimaksud\npadaayat(2)dis\nimpandibankpemerintahsebagaibarangbukti\nperkaradipengadilan.\n(4)\nperuntukan\nbarangbuktiperkarasebagaimanadimaksudpadaayat\n(3)dilakukansesuaidenganputusanpengadilanyangmempunyai\nkekuatanhukumtetap.\npasal\n46\n(1)\nbarangbuktiberupakebundan/a\ntautambangdaripenggunaan\nkawasanhutansecaratidaksahyangtelahmendapatputusan\npengadilanberkekuatanhukumtetapdikembalikankepada\npemerintahuntukdihutankankembalisesuaidenganfungsinya.\n(2)\nbarangbuktiberupakebunsebagaimanadimaksudpadaa\nyat(1)\ndimanfaatkanpalinglama1(satu)daursampaiselesainyaproses\npemulihankawasanhutan.\n(3)\ndalamhalbarangbuktikebundimanfaatkansebagaimanadimaksud\npadaayat(2),pemerintahdapatmemberikanpenugasankepada\nbadanusahamiliknegarayangberger\nakdibidangperkebunan.\n2013,\nno.\n130\n20\n(4)\nbarangbuktiberupatambangsebagaimanadimaksudpadaayat(1)\ndapatdiberikanizinsesuaidenganketentuanperaturanperundang\n-\nundangan.\npasal47\n(1)\nuntukkepentinganpenyidikanyangterkaitdengankuantitasbarang\nbuktiyangberad\nadalamkapalataualatangkutairlainnyadapat\ndigunakanmetodesurveidayamuat,metodepemeriksaanpembacaan\nskalaangkakapal,ataumetodelainyanglazimdigunakandalam\nbidangpelayaran.\n(2)\nmetodesebagaimanadimaksudpadaayat(1)dilaksanakanoleh\nle\nmbagayangtelahmempunyaikualifikasidibidangnyasesuai\ndenganketentuanperaturanperundang\n-\nundangan.\npasal48\nketentuanmengenaitatacarapenyimpananbarangbuktihasilperusakan\nhutanyangdisitasebagaimanadimaksuddalampasal40ayat(2)dantata\ncaraperuntukanbarangbuktisebagaimanadimaksuddalampasal44,\npasal45,danpasal46diaturdenganperaturanmenteri.\npasal49\n(1)\npenyidikmengajukanpermohonanlelangkepadaketuapengadilan\nnegerisetempatterhadapbarangbuktisitaanberupakayuhasil\npembalakanliarsebagaimanadimaksuddalampasal44ayat(3)dan\nbarangbuktitemuansertabarangbuktisitaanberupahasilkebun\ndan/atauhasiltambangbesertasaranaprasaranapendukungnyadari\nhasiltindakpidanapenggunaankawasanhutansecaratidaks\nah\nsebagaimanadimaksuddalampasal45ayat(1)danayat(2).\n(2)\npelaksanaanlelang\nterhadapbarangbukti\nsebagaimanadimaksud\npadaayat(1)dilakukanolehbadanlelangnegaradalamjangka\nwaktupalinglama14(empatbelas)harikerja.\n(3)\npelaksanaanlelangoleh\nbadanlelangnegarasebagaimanadimaksud\npadaayat(2)dilakukansecaraterbukasetelahselesainyapengujian,\npenghitungan,danpenetapannilaibarangbuktiolehlembaga.\n(5)\nterhadappihakterafiliasi\n,\ntersangkakasusperusakanhutandilarang\nmengikutilelan\ngsebagaimanadimaksudpadaayat(3).\n(6)\npengujian,penghitungan,ataupenetapannilaibarangbukti\nsebagaimanadimaksudpadaayat(3)harusdilakukanolehorangyang\nmemilikikeahliandanbersertifikatdarilembagayangterakreditasi.\npasal50\npengembalian\nkerugianakibatperusakanhutantidakmenghapuspidana\npelakuperusakanhutan.\n2013,\nno.\n130\n21\nparagraf2\npemeriksaandisidangpengadilan\npasal51\n(1)\ndalamhalterdakwatelahdipanggilsecarasahtetapitidakhadirdi\nsidangpengadilantanpaalasanyangsah,makaperkarada\npat\ndiperiksadandiputustanpahadirnyaterdakwa.\n(2)\nputusanyangdijatuhkantanpakehadiranterdakwadiumumkanoleh\npenuntutumumpadapapanpengumumanpengadilan,kantor\npemerintahdaerah,dan/ataudiberitahukankepadaterdakwaatau\nkuasanya.\n(3)\nterdakwaatau\nkuasanyadapatmengajukanupayahukumatas\nputusansebagaimanadimaksudpadaayat(1)dalamjangkawaktu\npalinglama14(empatbelas)harisejakputusandijatuhkan,\ndiumumkan,ataudiberitahukankepadaterdakwayangtidakhadir.\npasal52\n(1)\nperkaraperusakan\nhutanwajibdiperiksadandiputusolehpengadilan\nnegeridalamjangkawaktupalinglama45(empatpuluhlima)hari\nkerjaterhitungsejaktanggalpenerimaanpelimpahanperkaradari\npenuntutumum.\n(2)\ndalamhalputusanpengadilansebagaimanadimaksudpadaayat\n(1)\ndimohonkanbanding,perkaraperusakanhutanwajibdiperiksadan\ndiputusdalamjangkawaktupalinglama30(tigapuluh)harikerja\nterhitungsejaktanggalberkasperkaraditerimaolehpengadilan\ntinggi.\n(3)\ndalamhalputusanpengadilantinggidimohonkankas\nasi,perkara\npembalakanliarwajibdiperiksadandiputusdalamjangkawaktu\npalinglama50(limapuluh)harikerjaterhitungsejaktanggalberkas\nperkaraditerimaolehmahkamahagung.\npasal53\n(1)\npemeriksaanperkaraperusakanhutansebagaimanadimaksuddalam\npasal52ayat(1),padapengadilannegeri,dilakukanolehmajelis\nhakimyangberjumlah3(tiga)orangyangterdiridarisatuorang\nhakimkarierdipengadilannegerisetempatdanduaoranghakimad\nhoc.\n(2)\npengangkatanhakimadhocsebagaimanadimaksudpadaay\nat(1)\ndilakukanolehpresidenatasusulanketuamahkamahagung\nrepublikindonesia.\n(3)\nsetelahberlakunyaundang\n-\nundanginiketuamahkamahagung\nrepublikindonesiaharusmengusulkancalonhakimadhocyang\n2013,\nno.\n130\n22\ndiangkatmelaluikeputusanpresidenuntukmemeriksap\nerkara\nperusakanhutan.\n(4)\ndalammengusulkancalonhakimadhocsebagaimanadimaksudpada\nayat(3),ketuamahkamahagungwajibmengumumkankepada\nmasyarakat.\n(5)\nuntukdapatdiangkatmenjadihakimadhoc,harusterpenuhisyarat\nsebagaiberikut:\na.\nwarganegaraindon\nesia;\nb.\nbertakwakepadatuhanyangmahaesa;\nc.\nberusiapalingrendah40(empatpuluh)tahunpadasaat\npengangkatan;\nd.\nberijazahsarjanahukumatausarjanalainyangmemiliki\nkeahliandanpengalamansekurang\n-\nkurangnya10(sepuluh)\ntahundalambidangkehutanan;\ne.\nti\ndakpernahdijatuhihukumanpidanapenjaraberdasarkan\nputusanpengadilanyangtelahmemperolehkekuatanhukum\ntetapkarenamelakukantindakpidanayangdiancampidana5\n(lima)tahunataulebih;\nf.\ntidakpernahmelakukanperbuatantercela;\ng.\ncakap,jujur,serta\nmemilikiintegritasmoralyangtinggidan\nmemilikireputasiyangbaik;\nh.\ntidakmenjadipengurussalahsatupartaipolitik;dan\ni.\nmelepaskanjabatanstrukturaldanjabatanlainnyaselama\nmenjadihakimadhoc.\nbabv\nlembagapencegahandanpemberantasanperusaka\nnhutan\npasal54\n(1)\ndalamrangkapelaksanaanpencegahandanpemberantasan\nperusakanhutan,presidenmembentuklembagayangmenangani\npencegahandanpemberantasanperusakanhutan.\n(2)\nlembagasebagaimanadimaksudpadaayat(1)berkedudukandi\nbawahdanbertanggung\njawabkepadapresiden.\n(3)\nlembagasebagaimanadimaksudpadaayat(1)terdiriatas:\na.\nunsurkementeriankehutanan;\nb.\nunsurkepolisianrepublikindonesia;\nc.\nunsurkejaksaanrepublikindonesia;dan\n2013,\nno.\n130\n23\nd.\nunsurlainyangterkait.\n(4)\npelaksanaantugaslembagasebagaimanadimak\nsudpadaayat(1)\ndilakukanberdasarkanketentuandalamundang\n-\nundangini.\npasal55\n(1)\nlembaga\ndipimpinseorangkepaladandibantuolehseorangsekretaris\ndanbeberapaorangdeputi.\n(2)\nsekretarissebagaimanadimaksudpadaayat(1)berasaldariunsur\npemerintah\ndanbertugasmenyelenggarakandukunganadministratif\nterhadappelaksanaantugasdantanggungjawablembaga.\n(3)\ndeputi\nsebagaimanapadaayat(1)membidangi:\na.\nbidangpencegahan;\nb.\nbidangpenindakan;\nc.\nbidanghukumdankerjasama;dan\nd.\nbidangpengawasaninternaldanp\nengaduanmasyarakat.\n(4)\ndalam\nmelaksanakantugasdanwewenangnya,lembagadapat\nmembentuksatuantugassebagaiunsurpelaksana.\n(5)\nsatuantugasmelaksanakanpemberantasanperusakanhutanyang\nbersifatstrategissejakpenyelidikansampaidenganpenuntutandi\nselu\nruhwilayahnegarakesatuanrepublikindonesia,termasuk\nwilayahkepabeananatasperintahkepalalembagadan/ataudeputi.\n(6)\nketentuanlebihlanjutmengenaipembentukan,susunanorganisasi,\ndantatakerjalembagadiaturdalamperaturanpresiden.\npasal56\n(1)\nlemb\nagayangmenanganipencegahandanpemberantasanperusakan\nhutansebagaimanadimaksuddalampasal\n54ayat(1)bertugas:\na.\nmelakukanpenyelidikandanpenyidikantindakpidanaperusakan\nhutan;\nb.\nmelaksanakanadministrasipenyelidikandanpenyidikan\nterhadapperka\nraperusakanhutan;\nc.\nmelaksanakankampanyeantiperusakanhutan;\nd.\nmembangundanmengembangkansisteminformasi\npencegahan\ndan\npemberantasanperusakanhutanyangterintegrasi;\ne.\nmemberdayakanmasyarakatdalamupayapencegahandan\npemberantasanperusakanhutan;\nf.\nmelakukankerjasamadankoordinasiantarlembagapenegak\nhukumdalampemberantasanperusakanhutan;\n2013,\nno.\n130\n24\ng.\nmengumumkanhasilpelaksanaantugasdankewenangannya\nsecaraberkalakepadamasyarakatsesuaidenganketentuan\nperaturanperundang\n-\nundangan;dan\nh.\nmemberiiz\ninpenggunaanterhadapbarangbuktikayutemuan\nhasiloperasipemberantasanperusakanhutanyangberasaldari\nluarkawasanhutankonservasiuntukkepentingansosial.\n(2)\nketentuanlebihlanjutmengenaipelaksanaantugassebagaimana\ndimaksudpadaayat(1)diatu\nrdalamperaturanpresiden.\npasal57\ndalammelaksanakan\npencegahandan\npemberantasanperusakanhutan,\nlembagamelaporkanhasilkerjanyakepadadewanperwakilanrakyat\nrepublikindonesiapalingsedikit6(enam)bulansekali.\nbabvi\nperansertamasyarakat\npa\nsal58\n(1)\nmasyarakatberhakatas:\na.\nlingkunganhidupyangbaikdansehat,termasukkualitas\nlingkunganhidupyangdihasilkanolehhutan;\nb.\npemanfaatanhutansesuaidenganketentuanperaturan\nperundang\n-\nundangan;\nc.\nupayapemberdayaanmasyarakat;dan\ne.\npenyuluhantenta\nngpentingnyakelestarianhutandandampak\nnegatifperusakanhutan.\n(2)\nselainhaksebagaimanadimaksudpadaayat(1),dalamrangka\npencegahandanpemberantasanperusakanhutan,masyarakat\nberhak:\na.\nmencaridanmemperolehinformasiadanyadugaantelah\nterjadinya\nperusakanhutan;\nb.\nmendapatpelayanandalammencari,memperoleh,dan\nmemberikaninformasiadanyadugaantelahterjadiperusakan\nhutandanpenyalahgunaanizinkepadapenegakhukum;\nc.\nmencaridanmemperolehinformasiterhadapizinpengelolaan\nhutanyangtelahd\nikeluarkanolehpemerintahdaerahsetempat;\nd.\nmenyampaikansarandanpendapatsecarabertanggungjawab\nkepadapenegakhukum;dan\ne.\nmemperolehpelindunganhukumdalam:\n2013,\nno.\n130\n25\n1.\nmelaksanakanhaknyasebagaimanadimaksuddalamhuruf\na,hurufb,danhurufc;dan\n2.\nprosespeny\nelidikan,penyidikan,danpersidangansebagai\nsaksipelapor,saksi,atausaksiahlisesuaidenganketentuan\nperaturanperundang\n-\nundangan.\npasal\n59\nmasyarakatberkewajiban:\na.\nmenjagadanmemeliharakelestarianhutan;dan\nb.\nmengelolahutansesuaidenganketentua\nnperaturanperundang\n-\nundangan.\npasal60\nmasyarakatberkewajibanmemberikaninformasi,baiklisanmaupun\ntulisankepadapihakyangberwenangapabilamengetahuiatauadanya\nindikasiperusakanhutan.\npasal61\nmasyarakatberperansertadalampencegahandanpe\nmberantasan\nperusakanhutandengancara:\na.\nmembentukdanmembangunjejaringsosialgerakanantiperusakan\nhutan;\nb.\nmelibatkandanmenjadimitralembagapemberantasanperusakan\nhutandalamkegiatanpencegahandanpemberantasanperusakan\nhutan;\nc.\nmeningkatkankesa\ndarantentangpentingnyakelestarianhutandan\ndampaknegatifperusakanhutan;\nd.\nmemberikaninformasi,baiklisanmaupuntulisankepadapihakyang\nberwenangberkaitandenganpencegahandanpemberantasan\nperusakanhutan;\ne.\nikutsertamelakukanpengawasandalam\npenegakanhukum\npemberantasanperusakanhutan;dan/atau\nf.\nmelakukankegiatanlainyangbertujuanuntuk\npencegahandan\npemberantasanperusakanhutan.\npasal62\nlembagayangmenanganipemberantasanperusakanhutanmelakukan\nkemitraandengan\norganisasiataulemb\nagaswadayamasyarakatyang\nbergerakdibidangkehutananataudibidanglingkunganhidupserta\norganisasisosialkemasyarakatandalammelakukanpendampingan,\npelayanan,dandukungankepadamasyarakat.\n2013,\nno.\n130\n26\npasal63\nhakdankewajibansebagaimanadimaksuddalamp\nasal58sampaidengan\npasal60dilaksanakandenganberpegangteguhpadaasasatauketentuan\nperaturanperundang\n-\nundangan.\nbabvii\nkerjasamainternasional\npasal64\n(1)\npemerintahdapatmelakukankerjasamainternasionaldengannegara\nlaindalamrangkapencegah\nandanpemberantasan\nperusakanhutan\ndenganmempertimbangkandanmenjagakepentingannasional.\n(2)\nkerjasamainternasionaldalamrangkapencegahandan\npemberantasanperusakanhutandapatdilakukandalambentuk:\na.\nkerjasamabilateral;\nb.\nkerjasamaregional;atau\nc.\nkerjasamamultilateral.\npasal65\n(1)\nkerjasamasebagaimanadimaksuddalampasal64dapatdilakukan\nberdasarkansuatuperjanjian.\n(2)\ndalamhalbelumadaperjanjiansebagaimanadimaksudpadaayat(1),\nkerjasamadapatdilakukanatasdasarhubunganbaikberdasarka\nn\nprinsiptimbalbalik(resiprositas).\npasal66\n(1)\npemerintahmelakukankerjasamainternasionaldalamrangka\nmencegahperdagangandan/atau\npencuciankayutidaksah.\n(2)\npemerintahberkewajibanmelakukanupayapengembaliankerugian\natashasiltindakpidanaperusa\nkanhutan.\n(3)\nupayapengembaliankerugiansebagaimanadimaksudpadaayat(1)\nberupa:\na.\npemblokiranataupembekuansementarahartakekayaandengan\ntujuanuntukmencegahdialihkanataudipindahtangankanagar\norangtertentuatausemuaorangtidakberurusandengan\nharta\nyangtelahdiperolehataumungkintelahdiperolehdarikegiatan\nperusakanhutan;dan/atau\nb.\nperampasanhakataskekayaanataukeuntunganyangtelah\ndiperolehataumungkintelahdiperolehdarihasilkegiatan\nperusakanhutanberdasarkanputusanpengadila\nndiindonesia\nataudinegaraasing.\n2013,\nno.\n130\n27\npasal67\n(1)\nkerjasamainternasionaldalamrangkapencegahanperusakanhutan\ndapat\ndilakukandalamhal:\na.\nmanajemenpengelolaanhutanyangberkelanjutan;\nb.\nkerjasamakonservasidanrestorasikawasanhutan;\nc.\npemberdayaanmasyar\nakat;dan\nd.\npermerkuatansistemverifikasidansertifikasilegalitaskayuyang\ndiakuisecarainternasional.\n(2)\nkerjasamainternasionalsebagaimanadimaksudpadaayat(1)\nditujukanuntukmengurangikerusakanhutanakibatperusakan\nhutandankelestarianhutan.\np\nasal68\npemerintahmendorongkerjasamainternasionaldalamhalpendanaan\ndarimasyarakatinternasionaldaninvestasiswastainternasionaldalam\nrangkapencegahanperusakanhutan.\npasal69\n(1)\nuntukmelaksanakankerjasamainternasionaldalamrangka\npencegahan\ndanpemberantasanpembalakanliarsebagaimana\ndimaksuddalampasal64,menteridapatbertindakuntukdanatas\nnamapemerintahrepublikindonesiamelakukankerjasama\ninternasionaldengannegaralain,organisasiinternasional,dan/atau\nlembagakeuanganasi\nng,khususnyamenyangkutpenanganan\npemberantasanpembalakanliar.\n(2)\nkerjasamainternasionalsebagaimanadimaksudpadaayat(1)\ndilakukansesuaidenganketentuanperaturanperundang\n-\nundangan,\nkonvensi,dankebiasaaninternasionalyangberlakusecaraumum.\npasal70\ndalamrangkapenyelidikan,penyidikan,penuntutan,danpemeriksaandi\nsidangpengadilanterhadapperkaraperusakanhutan,pemerintahdapat\nmelakukankerjasamaregionaldaninternasionalmelaluiforumbilateral\nataumultilateralsesuaidengankete\nntuanperaturanperundang\n-\nundangan.\npasal71\nkerjasamainternasionaldalamrangkamelakukanpenyelidikan\nsebagaimanadimaksuddalampasal70meliputi:\na.\nidentitaskeberadaandankegiatandarisetiaporang,baiknasional\nmaupunasingyangdisangkaterlibatd\nalamperusakanhutan;\n2013,\nno.\n130\n28\nb.\npemindahanhasilkejahatanataukekayaanyangberasaldari\nperusakanhutan;\nc.\npemindahankekayaan,perlengkapan,ataualatpembantulainnya\nyangdigunakanataudimaksudkanuntukdigunakandalam\nmelakukanperusakanhutan;\nd.\nseluruhmatara\nntaiterjadinyatindakpidanapencuciankayutidak\nsahsampaidenganpencucianuang;\ne.\nidentitasdankegiatandarinegarayangmelakukanpencuciankayu\ntidaksahyangmerupakanhasilperusakanhutandiindonesia;\ndan/atau\nf.\nmelacak,membekukan,menyita,danme\nngembalikanasethasil\ntindakpidanaperusakanhutan.\npasal72\nkerjasamadalamrangkapenyelidikansebagaimanadimaksuddalam\npasal70dilakukanmelaluikerjasamainterpolnegaramasing\n-\nmasing.\npasal73\npemerintahdapatmembuatperjanjianataukesepakata\nndengannegara\nasinguntukmendapatpenggantianbiayadanbagihasilataspemanfaatan\nkayudariperusakanhutan.\nbabviii\npembiayaan\npasal74\nbiayayangdiperlukanuntukpelaksanaanundang\n-\nundanginidibebankan\nkepadaanggaranpendapatandanbelanjanegar\na.\npasal75\nperencanaandanpengajuanusulananggaranpemberantasanperusakan\nhutandilakukanolehlembagasebagaimanadimaksuddalampasal54.\nbabix\npelindungansaksi,pelapor,daninforman\npasal76\n(1)\nsetiaporangyangmenjadisaksi,pelapor,daninforman\ndalamupaya\npencegahandanpemberantasanpembalakanliar,wajibdiberi\npelindungankhususolehpemerintah.\n(2)\npelindungankhusussebagaimanadimaksudpadaayat(1)bertujuan\nuntukmenghindarikemungkinanancamanyangmembahayakandiri,\njiwa,dan/atauharta,t\nermasukkeluarganyadandilaksanakansesuai\ndenganketentuanperaturanperundang\n-\nundangan.\n2013,\nno.\n130\n29\npasal77\npelindungankeamananbagisaksi,pelapor,daninformanberupa:\na.\npelindunganataskeamananpribadi,keluarga,danhartabendanya,\nsertabebasdariancamanyan\ngberkenaandenganlaporandan\ninformasiyangakan,sedang,atautelahdiberikan;\nb.\npemberianinformasimengenaiputusanpengadilan;dan/atau\nc.\npemberitahuandalamhalterpidanadibebaskan.\npasal78\n(1)\npelapordaninformantidakdapatdituntutsecarahukum,baik\npidana\nmaupunperdataataslaporandankesaksianyangakan,sedang,atau\ntelahdiberikannya.\n(2)\npelindunganhukumtidakberlakuterhadappelapordaninforman\nyangmemberikanketerangantidakdenganitikadbaik.\npasal79\nseorangsaksiyangjugatersangkadalam\nkasusyangsamatidakdapat\ndibebaskandarituntutanpidanaapabilaiaternyataterbuktisecarasah\ndanmeyakinkanbersalah,tetapikesaksiannyadapatdijadikan\npertimbanganhakimdalammeringankanpidanayangakandijatuhkan.\npasal80\nmekanismepelindung\nanhukumpelapordaninforman:\na.\npelapordaninformanmendapatpelindunganhukumdengan\nmempertimbangkansyaratsebagaiberikut:\n1.\nsifatpentingnyaketeranganpelapordaninforman;\n2.\ntingkatancamanyangmembahayakanpelapordaninforman;\n3.\nhasilanalisistimmedi\nsataupsikologterhadappelapordan\ninforman;dan\n4.\nrekamjejakkejahatanyangpernahdilakukanolehpelapordan\ninforman.\nb.\ntatacaramemperolehpelindunganbagipelapordaninforman,baik\natasinisiatifsendirimaupunataspermintaanpejabatyang\nberwenang,\ndilakukandenganmengajukanpermohonansecara\ntertulissesuaidenganketentuanperaturanperundang\n-\nundangan.\npasal81\n(1)\npejabatyangberwenangwajibmemberikanpelindungansepenuhnya\nkepadapelapordaninforman,termasukkeluarganya,sejak\nditandatanganinya\npernyataankesediaan.\n(2)\npelindunganataskeamananpelapordaninformandihentikan\nberdasarkanalasan:\n2013,\nno.\n130\n30\na.\npelapordaninformanmemintaagarpelindunganterhadapnya\ndihentikandalampermohonandiajukanatasinisiatifsendiri;\nb.\nataspermintaanpejabatyangberwena\nngdalamhalpermintaan\npelindunganterhadappelapordaninformanberdasarkanatas\npermintaanpejabatyangbersangkutan;\nc.\npelapordaninformanmelanggarketentuanyangtertulisdalam\nperjanjian;\nd.\ninstansiyangberwenangberpendapatbahwapelapordan\ninforma\nntidaklagimemerlukanpelindunganberdasarkanbukti\n-\nbuktiyangmeyakinkan;atau\ne.\npenghentianpelindungankeamananseorangpelapordan\ninformanharusdilakukansecaratertulis.\nbabx\nketentuanpidana\npasal82\n(1)\norangperseoranganyangdengansengaja:\na.\nmelakuk\nanpenebanganpohondalamkawasanhutanyangtidak\nsesuaidenganizinpemanfaatanhutansebagaimanadimaksud\ndalampasal12hurufa;\nb.\nmelakukanpenebanganpohondalamkawasanhutantanpa\nmemilikiizinyangdikeluarkanolehpejabatyangberwenang\nsebagaimana\ndimaksuddalampasal12hurufb;dan/atau\nc.\nmelakukanpenebanganpohondalamkawasanhutansecara\ntidaksahsebagaimanadimaksuddalampasal12hurufc\ndipidanadenganpidanapenjarapalingsingkat1(satu)tahundan\npalinglama5(lima)tahunsertapidana\ndendapalingsedikit\nrp500.000.000,00(limaratusjutarupiah)danpalingbanyak\nrp2.500.000.000,00(duamiliarlimaratusjutarupiah).\n(2)\ndalamhaltindakpidanasebagaimanadimaksudpadaayat(1)\ndilakukanolehorangperseoranganyangbertempattinggaldi\ndalam\ndan/ataudisekitarkawasanhutan,pelakudipidanadenganpidana